import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import { FiCpu, FiMic, FiUser, FiSmile } from 'react-icons/fi';
import RobotSpeechDisplay from './RobotSpeechDisplay';
import {
  speakWithAudioFeedback,
  stopSpeaking,
  startListening,
} from '../utils/voice';

const API_URL = 'http://localhost:5000';

export default function RobotController() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [recognizedName, setRecognizedName] = useState('');
  const [collegeName, setCollegeName] = useState('');
  const [robotMessage, setRobotMessage] = useState('ðŸŽ“ Initializing...');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [volume, setVolume] = useState(0);

  const greetedPeopleRef = useRef(new Set());
  const interruptedRef = useRef(false);

  useEffect(() => {
    greetedPeopleRef.current.clear();
    fetchCollegeName();

    const interval = setInterval(fetchRecognition, 2000);
    startListening(handleTranscript);

    return () => {
      clearInterval(interval);
      stopSpeaking();
    };
  }, []);

  const logChatToServer = async (sender, receiver, message) => {
    try {
      await axios.post(`${API_URL}/api/log_chat`, {
        sender,
        receiver,
        message,
      });
    } catch (err) {
      console.error('Logging chat failed:', err);
    }
  };

  const handleTranscript = async (text, isFinal) => {
    if (!isFinal || !text.trim()) return;

    if (isSpeaking) {
      stopSpeaking();
      setIsSpeaking(false);
      interruptedRef.current = true;
    }

    const userQuery = text.trim();
    setRobotMessage(`ðŸ§  Processing: ${userQuery}`);
    await logChatToServer('User', 'Bot', userQuery);

    try {
      const res = await axios.post(`${API_URL}/api/robot_ask`, { query: userQuery });
      const response = res.data.response || "Sorry, I didn't catch that.";
      setRobotMessage(response);
      await logChatToServer('Bot', 'User', response);

      setIsSpeaking(true);
      speakWithAudioFeedback(response, setVolume, () => {
        setIsSpeaking(false);
        interruptedRef.current = false;
      }, handleTranscript);
    } catch (err) {
      console.error('Voice fetch failed:', err);
      setRobotMessage("âš ï¸ Error reaching assistant.");
    }
  };

  const fetchCollegeName = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/college_name`);
      setCollegeName(res.data.name || 'Your College');
    } catch {
      setCollegeName('Unknown College');
    }
  };

  const fetchRecognition = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/recognized_face`);
      const { name } = res.data;

      if (!name || greetedPeopleRef.current.has(name)) return;

      stopSpeaking();

      const isKnown = name.toLowerCase() !== 'unknown';
      const finalName = isKnown ? name : '';
      const greeting = isKnown
        ? `Hi ${finalName}, I'm your assistant at ${collegeName}. How can I help you today?`
        : `Hi, I'm the college bot. How can I help you today?`;

      setRobotMessage(greeting);
      setRecognizedName(finalName);
      setIsSpeaking(true);

      await logChatToServer('Bot', finalName || 'Visitor', greeting);

      speakWithAudioFeedback(greeting, setVolume, () => {
        setIsSpeaking(false);
      }, handleTranscript);

      greetedPeopleRef.current.add(name);
    } catch (err) {
      console.error('Face recognition failed:', err);
    }
  };

  useEffect(() => {
    const drawOverlay = () => {
      const canvas = canvasRef.current;
      const ctx = canvas?.getContext('2d');
      const img = videoRef.current;
      if (!canvas || !ctx || !img) return;

      const width = img.clientWidth;
      const height = img.clientHeight;

      canvas.width = width;
      canvas.height = height;
      ctx.clearRect(0, 0, width, height);

      if (recognizedName) {
        ctx.save();
        ctx.fillStyle = '#28a745';
        ctx.font = 'bold 2rem sans-serif';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'top';
        // ctx.fillText(`ðŸŽ‰ Hello, ${recognizedName}`, width / 2, 20);
        ctx.restore();
      }
    };

    const drawInterval = setInterval(drawOverlay, 500);
    return () => clearInterval(drawInterval);
  }, [recognizedName]);

  return (
    <div className="fade-in min-h-screen bg-gradient-to-br from-blue-50 via-white to-indigo-100 py-8 px-2">
      {/* College Name and Page Title */}
      <div className="max-w-4xl mx-auto text-center mb-10">
        <h1 className="text-4xl md:text-5xl font-extrabold mb-2 bg-gradient-to-r from-blue-700 via-purple-500 to-indigo-600 bg-clip-text text-transparent drop-shadow-2xl tracking-tight">
          Chettinad College of Engineering and Technology
        </h1>
        <div className="flex justify-center items-center gap-3 mt-2">
          <FiCpu className="text-2xl text-indigo-600" />
          <span className="text-2xl md:text-3xl font-bold bg-gradient-to-r from-purple-600 to-indigo-600 bg-clip-text text-transparent tracking-wide">Robot Controller</span>
        </div>
        <p className="text-lg text-blue-700/80 mt-2 font-medium">Voice Streaming & Face Recognition Assistant</p>
      </div>

      <div className="max-w-5xl mx-auto grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Camera Feed with Canvas Overlay */}
        <div className="glass-card overflow-hidden relative min-h-[340px] flex flex-col items-center justify-center shadow-xl">
          <div className="relative w-full aspect-video rounded-2xl overflow-hidden border-4 border-blue-200">
            <img
              ref={videoRef}
              src={`${API_URL}/video_feed`}
              alt="Robot Camera"
              className="w-full h-full object-cover"
            />
            <canvas
              ref={canvasRef}
              className="absolute top-0 left-0 w-full h-full pointer-events-none"
            />
          </div>
          <div className="mt-4 flex items-center gap-2">
            <FiUser className="text-blue-600 text-xl" />
            <span className="font-semibold text-blue-800 text-lg">{recognizedName ? `Recognized: ${recognizedName}` : 'Waiting for face...'}</span>
          </div>
        </div>

        {/* Robot Response Card */}
        <div className="glass-card p-8 flex flex-col justify-between items-center min-h-[340px] shadow-xl">
          <div className="flex-1 w-full overflow-y-auto max-h-[200px] custom-scroll">
            <RobotSpeechDisplay
              message={robotMessage}
              isSpeaking={isSpeaking}
              volume={volume}
            />
          </div>
          <div className="flex items-center gap-3 mt-6">
            <FiMic className={`text-2xl ${isSpeaking ? 'text-green-500 animate-pulse' : 'text-gray-400'}`} />
            <span className="text-sm text-gray-600 dark:text-gray-300 font-semibold">
              {isSpeaking ? 'Speaking...' : 'Listening... Speak anytime ðŸ‘‚'}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
}
