import React, { useEffect, useState } from 'react';
import API from '../utils/api';
import { FiMessageCircle } from 'react-icons/fi';
import { logChat as logChatUtil } from '../utils/chat_logger';

export default function RobotView() {
  const [robotMessage, setRobotMessage] = useState('Initializing...');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [lastName, setLastName] = useState('');
  const [chatLog, setChatLog] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [isListening, setIsListening] = useState(false);

  const speak = (text) => {
    const synth = window.speechSynthesis;
    const utter = new SpeechSynthesisUtterance(text);
    utter.onstart = () => setIsSpeaking(true);
    utter.onend = () => setIsSpeaking(false);
    synth.speak(utter);
  };

  const fetchRecognition = async () => {
    try {
      const res = await API.get('/recognized_face');
      const { name, confidence } = res.data;
      if (name && name !== lastName && name !== 'Unknown' && name !== 'No face') {
        const greet = `Hi ${name}, I'm your college assistant. How can I help you?`;
        setRobotMessage(greet);
        setLastName(name);
        speak(greet);
        logChat('robot', name, greet);
      } else if (name === 'Unknown' && lastName !== 'Unknown') {
        // Only log and clear, do not speak or display unknown message in RobotView
        setRobotMessage('');
        setLastName('Unknown');
        logChat('robot', 'unknown', 'Unknown person detected');
      }
    } catch (err) {
      console.error('Recognition failed:', err);
    }
  };

  useEffect(() => {
    const interval = setInterval(fetchRecognition, 3000);
    return () => clearInterval(interval);
  }, [lastName]);

  const logChat = async (sender, receiver, message) => {
    setChatLog((prev) => [...prev, { sender, receiver, message }]);
    await logChatUtil({ sender, receiver, message });
  };

  const handleAsk = async (inputText) => {
    const question = inputText || userInput;
    if (!question.trim()) return;
    setChatLog((prev) => [...prev, { sender: 'user', receiver: lastName || 'robot', message: question }]);
    try {
      const res = await API.get(`/info?question=${encodeURIComponent(question)}`);
      const answer = res.data.response;
      setRobotMessage(answer);
      speak(answer);
      logChat('robot', lastName || 'user', answer);
    } catch (err) {
      const errMsg = 'Error: Unable to fetch response.';
      setRobotMessage(errMsg);
      speak(errMsg);
    }
    setUserInput('');
  };

  return (
    <div className="space-y-6 fade-in sticky top-0 z-50">
      {/* Header */}
      <div className="glass-card p-6">
        <h1 className="text-3xl font-bold gradient-text mb-2">Robot View</h1>
        <p className="text-gray-600 dark:text-gray-300">Interactive camera + assistant canvas</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Video Feed */}
        <div className="glass-card p-4 relative min-h-[420px] flex flex-col justify-center items-center" style={{minWidth:'350px', maxWidth:'100%', boxSizing:'border-box'}}>
          <h2 className="text-xl font-semibold mb-4 text-gray-800 dark:text-white">Live Feed</h2>
          <div className="w-full h-96 bg-black rounded-xl overflow-hidden flex items-center justify-center relative" style={{minWidth:'320px', maxWidth:'100%'}}>
            <img
              src="http://localhost:5000/video_feed"
              alt="Live Feed"
              className="w-full h-full object-cover"
              style={{minWidth:'320px', maxWidth:'100%'}}
              onError={e => { e.target.style.display = 'none'; document.getElementById('video-fallback').style.display = 'block'; }}
            />
            <span id="video-fallback" style={{display:'none'}} className="absolute text-gray-400 text-lg left-1/2 top-1/2" >
              Video feed unavailable
            </span>
          </div>
        </div>

        {/* Chat */}
        <div className="glass-card p-4 flex flex-col items-center min-h-[400px] justify-between">
          <h2 className="text-xl font-semibold mb-4 text-gray-800 dark:text-white">
            Chat with Robot
          </h2>
          <div className="mt-6 w-full">
            <div className="h-48 overflow-y-auto bg-white/10 rounded-xl p-4 space-y-2 text-sm text-gray-800 dark:text-white font-mono">
              {chatLog.length === 0 ? (
                <div className="text-gray-400">No chat yet. Start a conversation!</div>
              ) : (
                chatLog.map((entry, i) => (
                  <div key={i} className={`p-2 rounded-lg ${entry.sender === 'robot'
                      ? 'bg-blue-100 dark:bg-blue-800/30'
                      : 'bg-gray-200 dark:bg-gray-700/50'
                    }`}>
                    <strong>{entry.sender === 'robot' ? 'ðŸ¤– Robot:' : 'ðŸ‘¤ User:'}</strong> {entry.message}
                    {entry.receiver && entry.receiver !== 'robot' && (
                      <span className="ml-2 text-xs text-gray-500">to {entry.receiver}</span>
                    )}
                  </div>
                ))
              )}
            </div>
            <div className="flex mt-4">
              <input
                className="flex-1 rounded-l-xl px-4 py-2 border border-gray-300 dark:border-gray-700 focus:outline-none"
                placeholder="Ask the robot..."
                value={userInput}
                onChange={(e) => setUserInput(e.target.value)}
                onKeyDown={(e) => e.key === 'Enter' && handleAsk()}
              />
              <button className="btn-primary rounded-r-xl px-4 flex items-center" onClick={() => handleAsk()}>
                <FiMessageCircle className="mr-1" /> Ask
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
