import React, { useEffect, useState } from 'react';
import API from '../utils/api';
import LoadingSpinner from '../components/LoadingSpinner';
import { FiTrash2 } from 'react-icons/fi';

export default function ChatLogs() {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    API.get('/api/chat_logs').then(res => {
      setLogs(res.data || []);
      setLoading(false);
    });
  }, []);

  const onDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this chat log?')) {
      API.delete(`/api/chat_logs/${id}`).then(res => {
        setLogs(logs.filter(log => log.id !== id));
      });
    }
  };

  const clearLogs = async () => {
    if (window.confirm('Are you sure you want to clear ALL chat logs?')) {
      setLoading(true);
      await API.post('/api/chat_logs/clear');
      setLogs([]);
      setLoading(false);
    }
  };

  return (
    <div className="glass-card p-6">
      <div className="flex items-center justify-between mb-4">
        <h1 className="text-2xl font-bold">Chat Logs</h1>
        <button onClick={clearLogs} className="btn-secondary bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded">
          Clear All
        </button>
      </div>
      {loading ? <LoadingSpinner /> : (
        <div className="overflow-x-auto">
          <table className="min-w-full text-sm">
            <thead>
              <tr className="bg-blue-100 dark:bg-blue-900">
                <th className="p-2">Time</th>
                <th className="p-2">Sender</th>
                <th className="p-2">Receiver</th>
                <th className="p-2">Message</th>
                <th className="p-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {logs.map(log => (
                <tr key={log.id} className="border-b border-gray-200 dark:border-gray-700">
                  <td className="p-2">{log.timestamp}</td>
                  <td className="p-2">{log.sender}</td>
                  <td className="p-2">{log.receiver}</td>
                  <td className="p-2">{log.message}</td>
                  <td className="p-2">
                    <button onClick={() => onDelete(log.id)} className="p-2 rounded-full bg-red-500/80 hover:bg-red-600 text-white">
                      <FiTrash2 size={16} />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
