import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

export default function RobotSpeechDisplay({ message, isSpeaking, volume = 0.5 }) {
  const [displayed, setDisplayed] = useState('');

  useEffect(() => {
    setDisplayed('');
    if (!message) return;
    let i = 0;
    const interval = setInterval(() => {
      setDisplayed((prev) => prev + message[i]);
      i++;
      if (i >= message.length) clearInterval(interval);
    }, 30);
    return () => clearInterval(interval);
  }, [message]);

  return (
    <div className="flex flex-col items-center space-y-4">
      {/* Avatar Head - lively, talking style */}
      <div className="relative">
        <motion.div
          animate={{
            scale: isSpeaking ? 1.08 : 1,
            boxShadow: isSpeaking ? '0 0 32px #6366f1, 0 0 64px #38bdf8' : 'none',
            rotate: isSpeaking ? [0, 2, -2, 2, 0] : 0
          }}
          transition={{
            duration: 0.6,
            repeat: isSpeaking ? Infinity : 0,
            repeatType: 'reverse'
          }}
          className="w-28 h-28 rounded-full bg-gradient-to-br from-blue-500 via-cyan-400 to-indigo-500 flex items-center justify-center shadow-2xl border-4 border-white/40"
        >
          {/* Eyes (no blinking) */}
          <div className="absolute left-8 top-11 w-4 h-4 bg-white rounded-full border-2 border-blue-200" />
          <div className="absolute right-8 top-11 w-4 h-4 bg-white rounded-full border-2 border-blue-200" />
          {/* Mouth - animate open/close with volume */}
          <motion.div
            className="absolute left-1/2 bottom-8 bg-white rounded-full"
            animate={{
              width: isSpeaking ? `${16 + Math.round(volume * 24)}px` : '16px',
              height: isSpeaking ? `${10 + Math.round(volume * 10)}px` : '8px',
              opacity: 1,
              scaleY: isSpeaking ? [1, 1.3, 1] : 1
            }}
            transition={{
              duration: 0.3,
              repeat: isSpeaking ? Infinity : 0,
              repeatType: 'reverse'
            }}
            style={{
              transform: 'translateX(-50%)',
              borderBottom: '4px solid #6366f1',
            }}
          />
        </motion.div>
      </div>

      {/* Typing text */}
      <AnimatePresence>
        <motion.div
          key={displayed}
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: 10 }}
          className="bg-white/80 dark:bg-gray-800/80 rounded-xl px-6 py-4 shadow-lg text-lg font-mono text-gray-800 dark:text-white min-h-[48px]"
        >
          {displayed}
        </motion.div>
      </AnimatePresence>
    </div>
  );
}
