import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import { FiCpu, FiSend } from 'react-icons/fi';
import RobotSpeechDisplay from './RobotSpeechDisplay';
import {
  speakWithAudioFeedback,
  stopSpeaking,
  startListening,
} from '../utils/voice';

const API_URL = 'http://localhost:5000';

export default function RobotController() {
  const [robotMessage, setRobotMessage] = useState('🎓 Hello! I am your college assistant robot. How can I help you?');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [volume, setVolume] = useState(0);
  const [userInput, setUserInput] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    startListening(handleTranscript);
    return () => {
      stopSpeaking();
    };
    // eslint-disable-next-line
  }, []);

  const handleTranscript = async (text, isFinal) => {
    if (!isFinal || !text.trim()) return;
    handleUserMessage(text.trim());
  };

  const handleUserMessage = async (message) => {
    if (!message) return;
    setLoading(true);
    setRobotMessage(`🧠 Processing: ${message}`);
    try {
      const res = await axios.post(`${API_URL}/api/robot_ask`, { query: message });
      const response = res.data.response || "Sorry, I didn't catch that.";
      setRobotMessage(response);
      setIsSpeaking(true);
      speakWithAudioFeedback(response, setVolume, () => {
        setIsSpeaking(false);
      }, handleTranscript);
    } catch (err) {
      setRobotMessage("⚠️ Error reaching assistant.");
    } finally {
      setLoading(false);
    }
  };

  const handleInputSubmit = (e) => {
    e.preventDefault();
    handleUserMessage(userInput);
    setUserInput('');
  };

  return (
    <div className="min-h-[80vh] flex flex-col items-center justify-center py-12 px-4 fade-in">
      <div className="glass-card p-10 rounded-3xl shadow-2xl flex flex-col items-center w-full max-w-xl">
        {/* Animated Robot Avatar */}
        <div className="relative mb-6">
          <div className={`mx-auto w-40 h-40 rounded-full flex items-center justify-center bg-gradient-to-br from-blue-200 via-purple-200 to-indigo-200 shadow-xl border-4 border-white ${isSpeaking ? 'animate-pulse-glow' : ''}`}>
            {/* Simple SVG robot face */}
            <svg width="120" height="120" viewBox="0 0 120 120" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="60" cy="60" r="56" fill="#fff" stroke="#6366f1" strokeWidth="8" />
              <ellipse cx="60" cy="80" rx="32" ry="18" fill="#e0e7ff" />
              <ellipse cx="45" cy="60" rx="8" ry="10" fill="#6366f1" />
              <ellipse cx="75" cy="60" rx="8" ry="10" fill="#6366f1" />
              <rect x="50" y="90" width="20" height="6" rx="3" fill="#6366f1" />
              <rect x="56" y="18" width="8" height="18" rx="4" fill="#6366f1" />
            </svg>
            {/* Glowing effect when speaking */}
            {isSpeaking && <div className="absolute inset-0 rounded-full bg-blue-400/30 blur-2xl animate-pulse"></div>}
          </div>
          <div className="absolute -top-8 left-1/2 -translate-x-1/2 bg-gradient-to-r from-blue-600 to-indigo-600 text-white px-6 py-2 rounded-full shadow-lg font-bold text-lg flex items-center gap-2">
            <FiCpu className="text-xl" /> Robot
          </div>
        </div>
        {/* Chat Bubble for Robot Message */}
        <div className={`w-full flex justify-center mb-6`}>
          <div className={`relative bg-gradient-to-br from-blue-100 via-white to-indigo-100 text-gray-800 dark:text-white px-6 py-4 rounded-2xl shadow-lg max-w-xs text-center text-lg font-semibold border-2 border-blue-200 ${isSpeaking ? 'ring-4 ring-blue-400/40' : ''}`}>
            {robotMessage}
            <div className="absolute left-1/2 -bottom-3 -translate-x-1/2 w-6 h-6 bg-gradient-to-br from-blue-100 to-indigo-100 rotate-45 border-b-2 border-r-2 border-blue-200"></div>
          </div>
        </div>
        {/* Text Input for User Message */}
        <form onSubmit={handleInputSubmit} className="w-full flex gap-3 mt-2">
          <input
            className="flex-1 rounded-xl px-4 py-3 border border-gray-300 dark:border-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500 text-lg shadow"
            placeholder="Type your message to the robot..."
            value={userInput}
            onChange={e => setUserInput(e.target.value)}
            disabled={loading}
          />
          <button type="submit" className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white px-6 py-3 rounded-xl font-bold shadow hover:scale-105 transition-transform flex items-center gap-2" disabled={loading || !userInput.trim()}>
            <FiSend className="text-xl" />
          </button>
        </form>
        <p className="text-xs text-gray-500 mt-4">You can talk or type to the robot 👂🤖</p>
      </div>
    </div>
  );
}

