import React, { useEffect, useState } from 'react';
import { FiDatabase, FiEdit, FiPlus, FiTrash2, FiX } from 'react-icons/fi';
import Notification from '../components/Notification';
import API from '../utils/api';
import LoadingSpinner from '../components/LoadingSpinner';

const API_URL = API.defaults.baseURL.replace('/api', '');

function Modal({ open, onClose, children }) {
  if (!open) return null;
  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-40">
      <div className="bg-white dark:bg-gray-900 rounded-lg shadow-lg p-6 w-full max-w-md relative animate-fadeIn">
        <button
          className="absolute top-3 right-3 text-gray-400 hover:text-gray-700 dark:hover:text-gray-200"
          onClick={onClose}
        >
          <FiX size={22} />
        </button>
        {children}
      </div>
    </div>
  );
}

export default function CollegeInfoEditor() {
  const [info, setInfo] = useState([]);
  const [loading, setLoading] = useState(true);
  const [notification, setNotification] = useState(null);
  const [editingInfo, setEditingInfo] = useState(null);
  const [addModalOpen, setAddModalOpen] = useState(false);
  const [editModalOpen, setEditModalOpen] = useState(false);
  const [newInfo, setNewInfo] = useState({ category: '', content: '' });

  useEffect(() => {
    fetchInfo();
  }, []);

  const fetchInfo = async () => {
    try {
      setLoading(true);
      const res = await API.get('/college_info');
      setInfo(res.data || []);
    } catch (err) {
      showNotification('error', 'Failed to fetch college information.');
    } finally {
      setLoading(false);
    }
  };

  const showNotification = (type, message) => {
    setNotification({ type, message });
    setTimeout(() => setNotification(null), 4000);
  };

  const handleAddInfo = async () => {
    if (!newInfo.category.trim() || !newInfo.content.trim()) {
      showNotification('error', 'Please fill in all fields.');
      return;
    }
    try {
      await API.post('/college_info', newInfo);
      setNewInfo({ category: '', content: '' });
      setAddModalOpen(false);
      showNotification('success', 'Info added successfully.');
      fetchInfo();
    } catch (err) {
      showNotification('error', 'Failed to add info.');
    }
  };

  const handleEditInfo = async () => {
    if (!editingInfo.category.trim() || !editingInfo.content.trim()) {
      showNotification('error', 'Please fill in all fields.');
      return;
    }
    try {
      await API.put(`/college_info/${editingInfo.id}`, editingInfo);
      setEditModalOpen(false);
      setEditingInfo(null);
      showNotification('success', 'Info updated successfully.');
      fetchInfo();
    } catch (err) {
      showNotification('error', 'Failed to update info.');
    }
  };

  const handleDeleteInfo = async (id) => {
    if (window.confirm('Are you sure you want to delete this info?')) {
      try {
        await API.delete(`/college_info/${id}`);
        showNotification('success', 'Info deleted successfully.');
        fetchInfo();
      } catch (err) {
        showNotification('error', 'Failed to delete info.');
      }
    }
  };

  return (
    <div className="max-w-5xl mx-auto py-10 px-4">
      <div className="flex items-center mb-8 space-x-3">
        <div className="p-3 rounded-lg bg-blue-600 text-white">
          <FiDatabase size={28} />
        </div>
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">College Info</h1>
          <p className="text-gray-500 dark:text-gray-300">Manage and update college-related data</p>
        </div>
        <div className="flex-1" />
        <button
          className="flex items-center gap-2 px-4 py-2 rounded-md bg-blue-600 text-white font-semibold hover:bg-blue-700 transition"
          onClick={() => setAddModalOpen(true)}
        >
          <FiPlus /> Add Info
        </button>
      </div>

      {loading ? (
        <div className="flex justify-center py-20">
          <LoadingSpinner />
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {info.length === 0 ? (
            <div className="col-span-full text-center text-gray-400 py-10">No college info found.</div>
          ) : (
            info.map(row => (
              <div
                key={row.id}
                className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-5 flex flex-col gap-3 border border-gray-100 dark:border-gray-700 relative group"
              >
                <div className="text-xs uppercase tracking-wider text-blue-600 font-semibold mb-1">{row.category}</div>
                <div className="text-gray-800 dark:text-gray-100 text-base whitespace-pre-line flex-1">{row.content}</div>
                <div className="flex justify-end gap-2 mt-2 opacity-0 group-hover:opacity-100 transition-opacity">
                  <button
                    onClick={() => {
                      setEditingInfo(row);
                      setEditModalOpen(true);
                    }}
                    className="p-2 rounded-full bg-yellow-400 hover:bg-yellow-500 text-white"
                  >
                    <FiEdit size={16} />
                  </button>
                  <button
                    onClick={() => handleDeleteInfo(row.id)}
                    className="p-2 rounded-full bg-red-500 hover:bg-red-600 text-white"
                  >
                    <FiTrash2 size={16} />
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      )}

      {/* Add Modal */}
      <Modal open={addModalOpen} onClose={() => setAddModalOpen(false)}>
        <h2 className="text-xl font-bold mb-4 text-gray-900 dark:text-white flex items-center gap-2">
          <FiPlus /> Add New Info
        </h2>
        <div className="space-y-3">
          <input
            type="text"
            placeholder="Category"
            value={newInfo.category}
            onChange={e => setNewInfo({ ...newInfo, category: e.target.value })}
            className="w-full px-3 py-2 border border-gray-300 dark:border-gray-700 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400 bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-white"
          />
          <textarea
            placeholder="Content"
            value={newInfo.content}
            onChange={e => setNewInfo({ ...newInfo, content: e.target.value })}
            className="w-full px-3 py-2 border border-gray-300 dark:border-gray-700 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400 bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-white min-h-[100px]"
          />
        </div>
        <div className="flex justify-end gap-2 mt-5">
          <button
            className="px-4 py-2 rounded-md bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-200 hover:bg-gray-300 dark:hover:bg-gray-600"
            onClick={() => setAddModalOpen(false)}
          >
            Cancel
          </button>
          <button
            className="px-4 py-2 rounded-md bg-blue-600 text-white font-semibold hover:bg-blue-700"
            onClick={handleAddInfo}
          >
            Add
          </button>
        </div>
      </Modal>

      {/* Edit Modal */}
      <Modal open={editModalOpen} onClose={() => { setEditModalOpen(false); setEditingInfo(null); }}>
        <h2 className="text-xl font-bold mb-4 text-gray-900 dark:text-white flex items-center gap-2">
          <FiEdit /> Edit Info
        </h2>
        {editingInfo && (
          <div className="space-y-3">
            <input
              type="text"
              placeholder="Category"
              value={editingInfo.category}
              onChange={e => setEditingInfo({ ...editingInfo, category: e.target.value })}
              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-700 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400 bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-white"
            />
            <textarea
              placeholder="Content"
              value={editingInfo.content}
              onChange={e => setEditingInfo({ ...editingInfo, content: e.target.value })}
              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-700 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400 bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-white min-h-[100px]"
            />
          </div>
        )}
        <div className="flex justify-end gap-2 mt-5">
          <button
            className="px-4 py-2 rounded-md bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-200 hover:bg-gray-300 dark:hover:bg-gray-600"
            onClick={() => { setEditModalOpen(false); setEditingInfo(null); }}
          >
            Cancel
          </button>
          <button
            className="px-4 py-2 rounded-md bg-blue-600 text-white font-semibold hover:bg-blue-700"
            onClick={handleEditInfo}
          >
            Save
          </button>
        </div>
      </Modal>

      {notification && (
        <Notification
          type={notification.type}
          message={notification.message}
          onClose={() => setNotification(null)}
        />
      )}
    </div>
  );
}
