import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import { FiCpu, FiMic } from 'react-icons/fi';
import RobotSpeechDisplay from './RobotSpeechDisplay';
import { speakWithAudioFeedback } from '../utils/voice';

const API_URL = 'http://localhost:5000';

export default function RobotController() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [recognizedName, setRecognizedName] = useState('');
  const [collegeName, setCollegeName] = useState('');
  const [robotMessage, setRobotMessage] = useState('ðŸŽ“ Initializing...');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [volume, setVolume] = useState(0);
  const [lastTimestamp, setLastTimestamp] = useState(0);
  const [lastSpokenName, setLastSpokenName] = useState('');
  const [micReady, setMicReady] = useState(false);

  const fetchCollegeName = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/college_name`);
      setCollegeName(res.data.name || 'Your College');
    } catch {
      setCollegeName('Your College');
    }
  };

  const fetchRecognition = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/recognized_face`);
      const { name, timestamp } = res.data;

      if (timestamp === lastTimestamp) return;
      setLastTimestamp(timestamp);

      // Greet only on new name change
      if (name && name !== 'Unknown' && name !== recognizedName) {
        const greet = `Hi ${name}, I am the robot assistant of ${collegeName}. How can I help you today?`;
        speakOnceThenListen(greet);
        setRecognizedName(name);
        setLastSpokenName(name);
        setRobotMessage(greet);
      } else if ((name === 'Unknown' || !name) && lastSpokenName !== 'Unknown') {
        const unknownGreet = `Hello! I am CollegeBot at ${collegeName}. How can I help you?`;
        speakOnceThenListen(unknownGreet);
        setRecognizedName('');
        setLastSpokenName('Unknown');
        setRobotMessage(unknownGreet);
      }
    } catch (err) {
      console.error('Recognition fetch failed:', err);
    }
  };

  const speakOnceThenListen = (text) => {
    setIsSpeaking(true);
    speakWithAudioFeedback(text, setVolume, () => {
      setIsSpeaking(false);
      setVolume(0);
      setMicReady(true); // ðŸ”„ Auto trigger mic after speech ends
    });
  };

  const handleMicListen = async () => {
    if (!micReady || isSpeaking) return;
    setMicReady(false); // only once per trigger
    setRobotMessage('ðŸŽ¤ Listening...');
    try {
      const res = await axios.post(`${API_URL}/api/robot_ask`, {
        query: 'listen_voice',
      });
      const answer = res.data.response || "Sorry, I didn't catch that.";
      speakOnceThenListen(answer);
      setRobotMessage(answer);
    } catch {
      setRobotMessage("âŒ Couldn't reach assistant.");
    }
  };

  const drawOverlay = () => {
    const canvas = canvasRef.current;
    const ctx = canvas?.getContext('2d');
    const img = videoRef.current;
    if (!canvas || !ctx || !img) return;

    canvas.width = img.clientWidth;
    canvas.height = img.clientHeight;
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.save();
    ctx.fillStyle = recognizedName ? '#28a745' : '#d63384';
    ctx.font = 'bold 1.5rem sans-serif';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'top';
    ctx.fillText(
      recognizedName ? `ðŸŽ‰ Hello, ${recognizedName}` : 'ðŸ¤– Face Not Recognized',
      canvas.width / 2,
      20
    );
    ctx.restore();
  };

  useEffect(() => {
    fetchCollegeName();
    const interval = setInterval(fetchRecognition, 3000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    if (micReady && !isSpeaking) {
      handleMicListen();
    }
  }, [micReady, isSpeaking]);

  useEffect(() => {
    drawOverlay();
    const obs = new ResizeObserver(drawOverlay);
    if (videoRef.current) obs.observe(videoRef.current);
    return () => obs.disconnect();
  }, [recognizedName]);

  return (
    <div className="max-w-5xl mx-auto p-4 space-y-6">
      <div className="glass-card p-6">
        <div className="flex items-center space-x-3">
          <FiCpu size={24} className="text-white p-3 rounded-xl bg-gradient-to-r from-purple-600 to-indigo-600" />
          <div>
            <h1 className="text-3xl font-bold gradient-text">Robot Controller</h1>
            <p className="text-gray-600">Vision + Voice integrated robot</p>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Camera */}
        <div className="glass-card overflow-hidden relative h-[450px]">
          <img
            ref={videoRef}
            src={`${API_URL}/video_feed`}
            alt="Robot Camera"
            className="absolute inset-0 w-full h-full object-cover"
          />
          <canvas
            ref={canvasRef}
            className="absolute top-0 left-0 w-full h-full pointer-events-none"
          />
        </div>

        {/* Robot Speech */}
        <div className="glass-card p-6 flex flex-col justify-between h-[450px]">
          <h2 className="text-xl font-bold flex items-center">
            <FiMic className="mr-2" /> Assistant
          </h2>
          <RobotSpeechDisplay
            message={robotMessage}
            isSpeaking={isSpeaking}
            volume={volume}
          />
          <div className="text-sm flex justify-between">
            <span>College: <strong>{collegeName}</strong></span>
            <span>Mic: {isSpeaking ? 'ðŸ”Š Speaking' : micReady ? 'ðŸŽ¤ Listening' : 'Idle'}</span>
          </div>
        </div>
      </div>
    </div>
  );
}
