// RobotView.jsx (Full Updated View with Dashboard Features)
import React, { useState, useRef, useEffect } from 'react';
import { FiSend, FiMessageSquare } from 'react-icons/fi';
import { robotAsk } from '../utils/api';

export default function RobotView() {
  const [chatInput, setChatInput] = useState('');
  const [chatLoading, setChatLoading] = useState(false);
  const [aiChat, setAiChat] = useState([]); // [{sender, message, isUser}]
  const chatEndRef = useRef(null);

  // Scroll to bottom on new message
  useEffect(() => {
    setTimeout(() => chatEndRef.current?.scrollIntoView({ behavior: 'smooth' }), 100);
  }, [aiChat]);

  // Handle sending a message
  const handleChatSend = async (e) => {
    e.preventDefault();
    if (!chatInput.trim()) return;
    setAiChat(prev => [...prev, { sender: 'You', message: chatInput, isUser: true }]);
    setChatLoading(true);
    try {
      const res = await robotAsk(chatInput);
      setAiChat(prev => [...prev, { sender: 'AI', message: res.data.response, isUser: false }]);
    } catch (e) {
      setAiChat(prev => [...prev, { sender: 'AI', message: 'Sorry, I could not process your request.', isUser: false }]);
    } finally {
      setChatInput('');
      setChatLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-indigo-100 via-white to-blue-200 relative overflow-hidden fade-in">
      {/* Futuristic background blobs */}
      <div className="pointer-events-none select-none absolute inset-0 z-0">
        <div className="absolute top-10 left-1/4 w-1/2 h-40 bg-gradient-to-r from-indigo-400/30 to-blue-400/20 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-10 right-1/4 w-1/3 h-32 bg-gradient-to-l from-blue-400/30 to-pink-400/20 rounded-full blur-2xl animate-pulse"></div>
      </div>
      <div className="relative z-10 w-full max-w-2xl mx-auto flex flex-col items-center justify-center py-12 px-2">
        <div className="glass-card w-full max-w-2xl mx-auto p-0 rounded-3xl shadow-2xl border-2 border-blue-100">
          <div className="flex flex-col items-center justify-center py-8 px-6">
            <div className="flex items-center gap-3 mb-2">
              <div className="bg-indigo-500 text-white rounded-full p-3 shadow-lg">
                <FiMessageSquare size={28} />
              </div>
              <h1 className="text-3xl md:text-4xl font-extrabold gradient-text tracking-tight">AI Chat Assistant</h1>
            </div>
            <p className="text-gray-500 text-center mb-4 max-w-xl">Ask me anything about the website, college, students, staff, or general topics. I'm here to help!</p>
            <div className="w-full h-80 md:h-96 bg-white/70 rounded-2xl border border-blue-100 shadow-inner overflow-y-auto flex flex-col p-4 mb-4">
              {aiChat.length === 0 ? (
                <div className="text-gray-400 text-center my-auto">Start a conversation with the AI assistant…</div>
              ) : (
                aiChat.map((msg, idx) => (
                  <div key={idx} className={msg.isUser ? 'flex justify-end mb-2' : 'flex justify-start mb-2'}>
                    <div className={
                      'max-w-[80%] px-4 py-2 rounded-2xl shadow ' +
                      (msg.isUser
                        ? 'bg-indigo-500 text-white rounded-br-none'
                        : 'bg-gray-100 text-gray-800 rounded-bl-none border border-blue-100')
                    }>
                      <span className="block text-xs font-semibold mb-1 opacity-70">{msg.sender}</span>
                      <span className="break-words whitespace-pre-line text-base">{msg.message}</span>
                    </div>
                  </div>
                ))
              )}
              <div ref={chatEndRef} />
            </div>
            <form onSubmit={handleChatSend} className="flex w-full gap-2 mt-2">
              <input
                type="text"
                className="flex-1 enhanced-input text-lg px-4 py-3 rounded-2xl border-2 border-blue-200 focus:border-indigo-400 focus:ring-2 focus:ring-indigo-100 transition-all shadow"
                placeholder="Type your message..."
                value={chatInput}
                onChange={e => setChatInput(e.target.value)}
                disabled={chatLoading}
                required
                autoFocus
              />
              <button
                type="submit"
                className="btn-primary flex items-center gap-1 px-6 py-3 rounded-2xl text-lg shadow-lg"
                disabled={chatLoading || !chatInput.trim()}
              >
                <FiSend size={22} />
                {chatLoading ? 'Sending...' : 'Send'}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
