import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import { FiCpu, FiMic } from 'react-icons/fi';
import RobotSpeechDisplay from './RobotSpeechDisplay';
import { speakWithAudioFeedback } from '../utils/voice';

const API_URL = 'http://localhost:5000';

export default function RobotController() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [recognizedName, setRecognizedName] = useState('');
  const [collegeName, setCollegeName] = useState('');
  const [robotMessage, setRobotMessage] = useState('ðŸŽ“ Initializing...');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [volume, setVolume] = useState(0);
  const [lastTimestamp, setLastTimestamp] = useState(0);

  useEffect(() => {
    fetchCollegeName();
    const interval = setInterval(fetchRecognition, 2000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      const video = videoRef.current;
      if (video && video.videoWidth && video.videoHeight) {
        drawOverlay();
        clearInterval(interval);
      }
    }, 500);
    return () => clearInterval(interval);
  }, [recognizedName]);

  const drawOverlay = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    if (!video || !canvas) return;

    const width = video.videoWidth;
    const height = video.videoHeight;
    if (!width || !height) return;

    canvas.width = width;
    canvas.height = height;

    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, width, height);

    ctx.save();
    ctx.fillStyle = recognizedName ? '#28a745' : '#d63384';
    ctx.font = 'bold 2rem sans-serif';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'top';
    ctx.fillText(
      recognizedName ? `ðŸŽ‰ Hello, ${recognizedName}` : 'ðŸ¤– Face Not Recognized',
      width / 2,
      20
    );
    ctx.restore();
  };

  const fetchCollegeName = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/college_name`);
      setCollegeName(res.data.name || 'Your College');
    } catch {
      setCollegeName('Unknown College');
    }
  };

  const fetchRecognition = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/recognized_face`);
      const { name, timestamp } = res.data;

      if (timestamp === lastTimestamp) return;
      setLastTimestamp(timestamp);

      if (name && name !== 'Unknown' && name !== recognizedName) {
        const greeting = `Hi ${name}, I am the robot assistant of ${collegeName}. How can I help you today?`;
        setRecognizedName(name);
        setRobotMessage(greeting);
        handleVoiceResponse(greeting);
        logChat(name, greeting);
        setTimeout(() => handleMicClick(name), 1000);
      } else if (name === 'Unknown' && recognizedName !== '') {
        const greet = `Hello! I am CollegeBot at ${collegeName}. How can I help you today?`;
        setRecognizedName('');
        setRobotMessage(greet);
        handleVoiceResponse(greet);
        logChat('Guest', greet);
        setTimeout(() => handleMicClick('Guest'), 1000);
      }
    } catch (err) {
      console.error('Recognition fetch failed:', err);
    }
  };

  const handleMicClick = async (user = recognizedName || 'Guest') => {
    try {
      const res = await axios.post(`${API_URL}/api/robot_ask`, {
        query: 'listen_voice',
      });
      const answer = res.data.response || "Sorry, I didn't catch that.";
      setRobotMessage(answer);
      handleVoiceResponse(answer);
      logChat(user, answer);
    } catch {
      setRobotMessage("Error: Couldn't reach AI assistant.");
    }
  };

  const handleVoiceResponse = (text) => {
    setIsSpeaking(true);
    speakWithAudioFeedback(text, setVolume, () => {
      setIsSpeaking(false);
      setVolume(0);
    });
  };

  const logChat = async (user, msg) => {
    try {
      await axios.post(`${API_URL}/api/chat_log`, {
        sender: 'robot',
        receiver: user,
        message: msg,
      });
    } catch (err) {
      console.error('Failed to log chat:', err);
    }
  };

  return (
    <div className="space-y-6 fade-in max-w-5xl mx-auto">
      <div className="glass-card p-6">
        <div className="flex items-center space-x-3">
          <div className="p-3 rounded-xl bg-gradient-to-r from-purple-600 to-indigo-600 text-white">
            <FiCpu size={24} />
          </div>
          <div>
            <h1 className="text-3xl font-bold gradient-text">Robot Controller</h1>
            <p className="text-gray-600 dark:text-gray-300">
              Vision + Voice integrated robot interface
            </p>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Fixed-height Camera Feed */}
        <div className="glass-card overflow-hidden relative min-h-[420px]">
          <div className="relative w-full" style={{ aspectRatio: '16 / 9' }}>
            <video
              ref={videoRef}
              src={`${API_URL}/video_feed`}
              autoPlay
              muted
              playsInline
              className="w-full h-full object-cover rounded-xl"
              onLoadedMetadata={drawOverlay}
            />
            <canvas
              ref={canvasRef}
              className="absolute top-0 left-0 w-full h-full pointer-events-none"
            />
          </div>
        </div>

        {/* Robot Canvas */}
        <div className="glass-card p-6 flex flex-col justify-between items-center">
          <h2 className="text-xl font-bold mb-4 flex items-center">
            <FiMic className="mr-2" /> Robot Canvas Display
          </h2>

          <RobotSpeechDisplay
            message={robotMessage}
            isSpeaking={isSpeaking}
            volume={volume}
          />

          <div className="mt-6 w-full flex items-center justify-between">
            <div className="text-sm text-gray-600 dark:text-gray-300">
              College: <span className="font-semibold">{collegeName}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
