import React, { useEffect, useState } from 'react';
import {
  fetchLiveChatLogs,
  deleteLiveChatLog,
  clearLiveChatLogs
} from '../utils/api';
import LoadingSpinner from '../components/LoadingSpinner';
import { FiTrash2 } from 'react-icons/fi';

export default function ChatLogs() {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchLogs = async () => {
    try {
      const res = await fetchLiveChatLogs();
      setLogs(res.data || []);
    } catch (err) {
      console.error("Failed to fetch live chat logs:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchLogs();
    const interval = setInterval(fetchLogs, 10000); // Auto-refresh every 10s
    return () => clearInterval(interval);
  }, []);

  const onDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this chat log?')) {
      try {
        await deleteLiveChatLog(id);
        setLogs((prevLogs) => prevLogs.filter((log) => log.id !== id));
      } catch (err) {
        console.error('Failed to delete chat log:', err);
      }
    }
  };

  const clearLogs = async () => {
    if (window.confirm('Are you sure you want to clear ALL chat logs?')) {
      try {
        setLoading(true);
        await clearLiveChatLogs();
        setLogs([]);
      } catch (err) {
        console.error("Failed to clear chat logs:", err);
      } finally {
        setLoading(false);
      }
    }
  };

  const formatTime = (timestamp) => {
    try {
      return new Date(timestamp).toLocaleString();
    } catch {
      return timestamp;
    }
  };

  return (
    <div className="max-w-5xl mx-auto py-10 px-4">
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between mb-8 gap-4">
        <h1 className="text-3xl font-bold text-blue-800 dark:text-white mb-1">Live Chat Logs</h1>
        <button
          onClick={clearLogs}
          className="flex items-center gap-2 bg-red-600 hover:bg-red-700 text-white font-semibold px-4 py-2 rounded-lg shadow transition"
        >
          <FiTrash2 /> Clear All
        </button>
      </div>
      {loading ? (
        <div className="flex justify-center py-20">
          <LoadingSpinner />
        </div>
      ) : logs.length === 0 ? (
        <div className="flex flex-col items-center justify-center py-20 text-gray-400 text-lg">
          <FiTrash2 size={40} className="mb-4" />
          No chat logs available.
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {logs.map((log) => (
            <div
              key={log.id}
              className="bg-white dark:bg-gray-800 rounded-xl shadow-md p-6 flex flex-col gap-2 border border-gray-100 dark:border-gray-700 hover:shadow-lg transition relative"
            >
              <div className="flex items-center justify-between mb-1">
                <span className="text-xs text-gray-500 dark:text-gray-400 font-mono">{formatTime(log.timestamp)}</span>
                <button
                  onClick={() => onDelete(log.id)}
                  className="p-2 rounded-lg bg-red-500/90 hover:bg-red-600 text-white transition"
                  title="Delete log"
                >
                  <FiTrash2 size={16} />
                </button>
              </div>
              <div className="flex items-center gap-2 mb-1">
                <span className="text-sm font-semibold text-blue-700 dark:text-blue-300">{log.sender}</span>
                <span className="text-xs text-gray-400">→</span>
                <span className="text-sm font-semibold text-green-700 dark:text-green-300">{log.receiver}</span>
              </div>
              <div className="text-gray-800 dark:text-gray-100 text-base whitespace-pre-line break-words">
                {log.message}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
