import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  FiUsers, 
  FiUserCheck, 
  FiEye, 
  FiUpload, 
  FiGrid, 
  FiActivity, 
  FiDatabase,
  FiTrendingUp,
  FiShield,
  FiZap,
  FiMonitor,
  FiCamera,
  FiClock,
  FiCheckCircle,
  FiCpu
} from 'react-icons/fi';
import StatsCard from '../components/StatsCard';
import LoadingSpinner from '../components/LoadingSpinner';
import axios from 'axios';
import { motion } from 'framer-motion';

function animateValue(start, end, duration, setter) {
  if (start === end) {
    setter(end);
    return;
  }
  const range = end - start;
  let current = start;
  const increment = range / (duration / 16);
  let startTime = null;

  function step(timestamp) {
    if (!startTime) startTime = timestamp;
    const progress = timestamp - startTime;
    current = start + increment * (progress / 16);
    if ((increment > 0 && current >= end) || (increment < 0 && current <= end)) {
      setter(end);
      return;
    }
    setter(Math.round(current));
    requestAnimationFrame(step);
  }
  requestAnimationFrame(step);
}

// Add a utility for gradient text
const gradientTextClass = "bg-gradient-to-r from-indigo-700 via-purple-600 to-blue-500 bg-clip-text text-transparent drop-shadow-lg";

export default function Home() {
  const [stats, setStats] = useState({
    students: 0,
    staff: 0,
    unknowns: 0,
    accuracy: 0
  });
  const [loading, setLoading] = useState(true);
  const [displayStats, setDisplayStats] = useState(stats);
  const [robotQuery, setRobotQuery] = useState("");
  const [robotResponse, setRobotResponse] = useState("");

  useEffect(() => {
    setLoading(true);
    axios.get('http://localhost:5000/api/statistics')
      .then(res => {
        const apiStats = {
          students: res.data.total_students,
          staff: res.data.total_staff,
          unknowns: res.data.total_unknowns,
          accuracy: res.data.accuracy_rate
        };
        setStats(apiStats);
        setLoading(false);
        // Animate numbers
        animateValue(displayStats.students, apiStats.students, 800, val => setDisplayStats(s => ({ ...s, students: val })));
        animateValue(displayStats.staff, apiStats.staff, 800, val => setDisplayStats(s => ({ ...s, staff: val })));
        animateValue(displayStats.unknowns, apiStats.unknowns, 800, val => setDisplayStats(s => ({ ...s, unknowns: val })));
        animateValue(displayStats.accuracy, apiStats.accuracy, 800, val => setDisplayStats(s => ({ ...s, accuracy: val })));
      })
      .catch(() => setLoading(false));
    // eslint-disable-next-line
  }, []);

  const features = [
    {
      icon: FiCamera,
      title: "Real-time Recognition",
      description: "Advanced facial recognition with instant identification and tracking capabilities.",
      color: "from-blue-500 to-cyan-500"
    },
    {
      icon: FiShield,
      title: "Secure & Private",
      description: "Enterprise-grade security with encrypted data storage and privacy protection.",
      color: "from-green-500 to-emerald-500"
    },
    {
      icon: FiZap,
      title: "Lightning Fast",
      description: "Optimized algorithms for quick recognition and minimal processing time.",
      color: "from-purple-500 to-pink-500"
    },
    {
      icon: FiMonitor,
      title: "Multi-Camera Support",
      description: "Support for multiple camera feeds with centralized monitoring dashboard.",
      color: "from-orange-500 to-red-500"
    }
  ];

  const quickActions = [
    { icon: FiUsers, title: "View Students", path: "/students", color: "bg-blue-500" },
    { icon: FiUserCheck, title: "View Staff", path: "/staff", color: "bg-green-500" },
    { icon: FiEye, title: "Unknown Faces", path: "/unknowns", color: "bg-red-500" },
    { icon: FiDatabase, title: "College Info", path: "/collegeinfo", color: "bg-teal-500" },
    { icon: FiDatabase, title: "Chat Info", path: "/chatlogs", color: "bg-pink-500" }
  ];

  const statsData = [
    { 
      icon: FiUsers, 
      title: "Students Recognized",
      value: loading ? 0 : displayStats.students, 
      color: "from-blue-500 to-cyan-500",
      delay: 0.1
    },
    { 
      icon: FiUserCheck, 
      title: "Staff Recognized",
      value: loading ? 0 : displayStats.staff, 
      color: "from-green-500 to-emerald-500",
      delay: 0.2
    },
    { 
      icon: FiEye,
      title: "Unknown Recognized",
      value: loading ? 0 : displayStats.unknowns,
      color: "from-red-500 to-pink-500",
      delay: 0.3
    }
  ];

  const startVoiceRecognition = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Your browser does not support Speech Recognition.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.interimResults = false;
    recognition.start();

    recognition.onresult = function (event) {
      const query = event.results[0][0].transcript;
      setRobotQuery(query);

      fetch("http://localhost:5000/robot_ask", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query: query })
      })
        .then(res => res.json())
        .then(data => {
          const reply = data.response;
          setRobotResponse(reply);

          const synth = window.speechSynthesis;
          const utter = new window.SpeechSynthesisUtterance(reply);
          synth.speak(utter);
        })
        .catch(err => {
          setRobotResponse("Sorry, something went wrong.");
          console.error("Voice Assistant Error:", err);
        });
    };

    recognition.onerror = function (event) {
      console.error("Speech recognition error", event);
    };
  };

  return (
    <motion.div className="min-h-screen fade-in bg-gradient-to-br from-indigo-200 via-blue-100 to-purple-200" initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.8 }}>
      {/* College Name at the top */}
      <section className="relative py-12 px-6 bg-gradient-to-br from-blue-100 via-white to-indigo-100 text-center">
        <h1 className={`text-4xl md:text-6xl font-extrabold mb-4 ${gradientTextClass}`}>Chettinad College Of Engineering and Technology</h1>
      </section>

      {/* Robot Button at the top */}
      <div className="flex justify-center pt-16 pb-8">
        <Link to="/robotcontroller" className="inline-flex items-center gap-2 px-10 py-6 bg-gradient-to-r from-purple-700 to-indigo-700 text-white text-2xl font-extrabold rounded-full shadow-2xl hover:scale-105 transition-transform tracking-wide uppercase">
          <FiCpu className="text-3xl" /> <span className="tracking-widest">Robot</span>
        </Link>
      </div>

      {/* Quick Actions Section - Centered in one line */}
      <section className="max-w-5xl mx-auto px-6 py-8 flex flex-col items-center">
        <h2 className={`text-3xl font-extrabold mb-8 ${gradientTextClass} text-center drop-shadow`}>Quick Actions</h2>
        <div className="w-full flex flex-col md:flex-row justify-center items-center gap-8">
          {quickActions.map((action, i) => (
            <motion.div key={action.title} whileHover={{ scale: 1.08 }} whileTap={{ scale: 0.97 }} className="flex-1 min-w-[180px]">
              <Link to={action.path} className={`flex flex-col items-center justify-center p-8 rounded-2xl shadow-xl text-white ${action.color} hover:shadow-2xl transition-all duration-300 w-full`}>
                <action.icon className="text-4xl mb-2 drop-shadow-lg" />
                <span className="font-semibold text-lg text-center tracking-wide uppercase drop-shadow">{action.title}</span>
              </Link>
            </motion.div>
          ))}
        </div>
      </section>

      {/* Stats Section - Centered in one line */}
      <section className="max-w-5xl mx-auto px-6 py-8 flex flex-col items-center">
        <h2 className={`text-3xl font-extrabold mb-8 ${gradientTextClass} text-center drop-shadow`}>Statistics</h2>
        <div className="w-full flex flex-col md:flex-row justify-center items-center gap-8">
          {statsData.map((stat, i) => (
            <motion.div key={stat.title} initial={{ y: 40, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ delay: stat.delay, duration: 0.7 }} className="flex-1 min-w-[180px]">
              <div className="card flex flex-col items-center p-8">
                <span className={`text-4xl mb-4 bg-gradient-to-br ${stat.color} text-white p-3 rounded-full shadow-lg drop-shadow-lg`}>
                  <stat.icon />
                </span>
                <h3 className={`text-xl font-bold mb-2 ${gradientTextClass} text-center drop-shadow`}>{stat.title}</h3>
                <p className="text-3xl font-extrabold text-gray-800 dark:text-white text-center drop-shadow-lg">{stat.value}</p>
              </div>
            </motion.div>
          ))}
        </div>
      </section>

      {/* Ask the Robot (AI Question Input) Section */}
      <section className="max-w-3xl mx-auto px-6 py-10">
        <div className="glass-card p-10 flex flex-col items-center bg-white/80 shadow-2xl rounded-2xl">
          <h2 className={`text-2xl font-extrabold mb-4 ${gradientTextClass} text-center drop-shadow`}>Ask the Robot</h2>
          <div className="flex w-full gap-4">
            <input
              className="flex-1 rounded-xl px-4 py-3 border border-gray-300 dark:border-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500 text-lg shadow"
              placeholder="Type your question..."
              value={robotQuery}
              onChange={e => setRobotQuery(e.target.value)}
            />
            <motion.button whileHover={{ scale: 1.08 }} whileTap={{ scale: 0.97 }} className="btn-primary px-8 font-bold text-lg shadow" onClick={startVoiceRecognition}>
              🎤
            </motion.button>
          </div>
          {robotResponse && (
            <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} className="mt-6 p-4 rounded-xl bg-gradient-to-r from-blue-100 to-indigo-100 dark:from-blue-900 dark:to-indigo-900 text-lg text-center shadow-lg font-semibold text-gray-800 dark:text-white">
              {robotResponse}
            </motion.div>
          )}
        </div>
      </section>

      {/* Footer */}
      <footer className="py-8 px-6 bg-gradient-to-r from-gray-800 to-gray-900 text-white">
        <div className="max-w-7xl mx-auto text-center">
          <p className="text-gray-300">
            © 2024 Face Recognition System. Powered by advanced AI technology.
          </p>
        </div>
      </footer>
    </motion.div>
  );
}
