import React, { useEffect, useState } from 'react';
import { FiDatabase, FiEdit, FiPlus, FiTrash2, FiRefreshCw } from 'react-icons/fi';
import Notification from '../components/Notification';
import API from '../utils/api';
import LoadingSpinner from '../components/LoadingSpinner';

const API_URL = API.defaults.baseURL.replace('/api', '');

export default function CollegeInfoEditor() {
  const [info, setInfo] = useState([]);
  const [loading, setLoading] = useState(true);
  const [notification, setNotification] = useState(null);
  const [editingInfo, setEditingInfo] = useState(null);
  const [newInfo, setNewInfo] = useState({ category: '', content: '' });

  useEffect(() => {
    fetchInfo();
  }, []);


  const fetchInfo = async () => {
    try {
      setLoading(true);
      const res = await API.get('/college_info');
      setInfo(res.data || []);
    } catch (err) {
      showNotification('error', 'Failed to fetch college information.');
    } finally {
      setLoading(false);
    }
  };

  const showNotification = (type, message) => {
    setNotification({ type, message });
    setTimeout(() => setNotification(null), 4000);
  };

  const handleAddInfo = async () => {
    try {
      await API.post('/college_info', newInfo);
      setNewInfo({ category: '', content: '' });
      showNotification('success', 'Info added successfully.');
      fetchInfo();
    } catch (err) {
      showNotification('error', 'Failed to add info.');
    }
  };

  const handleEditInfo = async () => {
    try {
      await API.put(`/college_info/${editingInfo.id}`, editingInfo);
      setEditingInfo(null);
      showNotification('success', 'Info updated successfully.');
      fetchInfo();
    } catch (err) {
      showNotification('error', 'Failed to update info.');
    }
  };

  const handleDeleteInfo = async (id) => {
    if (window.confirm('Are you sure you want to delete this info?')) {
      try {
        await API.delete(`/college_info/${id}`);
        showNotification('success', 'Info deleted successfully.');
        fetchInfo();
      } catch (err) {
        showNotification('error', 'Failed to delete info.');
      }
    }
  };

  return (
    <div className="fade-in max-w-4xl mx-auto py-8 px-4 space-y-8">
      <div className="glass-card p-6 mb-6">
        <div className="flex items-center space-x-3">
          <div className="p-3 rounded-xl bg-gradient-to-r from-indigo-500 to-blue-500 text-white">
            <FiDatabase size={24} />
          </div>
          <div>
            <h1 className="text-3xl font-bold text-blue-800 drop-shadow">College Info Editor</h1>
            <p className="text-gray-600 dark:text-gray-300">Manage and update college-related data shown to users</p>
          </div>
        </div>
      </div>

      <div className="glass-card p-6">
        <h1 className="text-2xl font-bold mb-4">College Info</h1>
        {loading ? <LoadingSpinner /> : (
          <div className="overflow-x-auto">
            <table className="min-w-full text-sm">
              <thead>
                <tr className="bg-blue-100 dark:bg-blue-900">
                  <th className="p-2">Category</th>
                  <th className="p-2">Content</th>
                  <th className="p-2 text-center">Actions</th>
                </tr>
              </thead>
              <tbody>
                {info.map(row => (
                  <tr key={row.id} className="border-b border-gray-200 dark:border-gray-700">
                    <td className="p-2">{row.category}</td>
                    <td className="p-2">{row.content}</td>
                    <td className="p-2 text-center">
                      <button
                        onClick={() => setEditingInfo(row)}
                        className="p-2 rounded-full bg-yellow-500/80 hover:bg-yellow-600 text-white mr-2"
                      >
                        <FiEdit size={16} />
                      </button>
                      <button
                        onClick={() => handleDeleteInfo(row.id)}
                        className="p-2 rounded-full bg-red-500/80 hover:bg-red-600 text-white"
                      >
                        <FiTrash2 size={16} />
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
        <div className="mt-4">
          <h2 className="text-lg font-semibold mb-2">Add New Info</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-2">
            <input
              type="text"
              placeholder="Category"
              value={newInfo.category}
              onChange={(e) => setNewInfo({ ...newInfo, category: e.target.value })}
              className="enhanced-input"
            />
            <input
              type="text"
              placeholder="Content"
              value={newInfo.content}
              onChange={(e) => setNewInfo({ ...newInfo, content: e.target.value })}
              className="enhanced-input"
            />
          </div>
          <button className="btn-primary mt-2" onClick={handleAddInfo}>
            <FiPlus className="mr-1" /> Add Info
          </button>
        </div>
      </div>

      {editingInfo && (
        <div className="glass-card p-6 space-y-4">
          <div className="flex items-center space-x-3">
            <FiEdit className="text-gray-400" />
            <h2 className="text-xl font-semibold">Edit Info</h2>
          </div>
          <input
            type="text"
            className="enhanced-input"
            value={editingInfo.category}
            onChange={e => setEditingInfo({ ...editingInfo, category: e.target.value })}
            placeholder="Category"
          />
          <textarea
            className="enhanced-input min-h-[120px]"
            value={editingInfo.content}
            onChange={e => setEditingInfo({ ...editingInfo, content: e.target.value })}
            placeholder="Content"
          ></textarea>
          <button onClick={handleEditInfo} className="btn-primary">
            Save Changes
          </button>
          <button onClick={() => setEditingInfo(null)} className="btn-secondary ml-2">
            Cancel
          </button>
        </div>
      )}

      {notification && (
        <Notification
          type={notification.type}
          message={notification.message}
          onClose={() => setNotification(null)}
        />
      )}
    </div>
  );
}
