import React, { useEffect, useRef, useState } from 'react';
import { FiCpu, FiMic, FiMicOff, FiPower, FiUser, FiVolume2 } from 'react-icons/fi';
import RobotSpeechDisplay from './RobotSpeechDisplay';
import {
  speakWithAudioFeedback,
  stopSpeaking,
  startListening,
} from '../utils/voice';
import API from '../utils/api';
const API_URL = API.defaults.baseURL.replace('/api', '');

export default function RobotController() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [recognizedName, setRecognizedName] = useState('');
  const [collegeName, setCollegeName] = useState('');
  const [robotMessage, setRobotMessage] = useState('ðŸ¤– Ready to assist you!');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [volume, setVolume] = useState(0);
  const [isMicOn, setIsMicOn] = useState(true);
  const [isCameraOn, setIsCameraOn] = useState(true);
  const [voices, setVoices] = useState([]);
  const [selectedVoice, setSelectedVoice] = useState(null);

  const greetedPeopleRef = useRef(new Set());
  const interruptedRef = useRef(false);

  // Load available voices
  useEffect(() => {
    const loadVoices = () => {
      const synth = window.speechSynthesis;
      let voicesList = synth.getVoices();
      if (voicesList.length === 0) {
        // Some browsers need this to populate voices
        synth.onvoiceschanged = () => {
          voicesList = synth.getVoices();
          setVoices(voicesList);
          setSelectedVoice(voicesList[0] || null);
        };
      } else {
        setVoices(voicesList);
        setSelectedVoice(voicesList[0] || null);
      }
    };
    loadVoices();
  }, []);

  useEffect(() => {
    greetedPeopleRef.current.clear();
    fetchCollegeName();
    const interval = setInterval(fetchRecognition, 2500);
    startListening(handleTranscript);
    return () => {
      clearInterval(interval);
      stopSpeaking();
    };
  }, []);

  const logChatToServer = async (sender, receiver, message) => {
    try {
      await API.post('/log_chat', { sender, receiver, message });
    } catch {}
  };

  // Custom speak function with selected voice
  const speakWithVoice = (text, cb) => {
    if (!window.speechSynthesis) return;
    const utter = new window.SpeechSynthesisUtterance(text);
    if (selectedVoice) utter.voice = selectedVoice;
    utter.volume = 1;
    utter.rate = 1;
    utter.pitch = 1;
    utter.onend = cb;
    window.speechSynthesis.speak(utter);
  };

  const handleTranscript = async (text, isFinal) => {
    if (!isFinal || !text.trim()) return;
    if (isSpeaking) {
      stopSpeaking();
      setIsSpeaking(false);
      interruptedRef.current = true;
    }
    const userQuery = text.trim();
    setRobotMessage(`ðŸ§  Processing: ${userQuery}`);
    await logChatToServer('User', 'Bot', userQuery);
    try {
      const res = await API.post('/robot_ask', { query: userQuery });
      const response = res.data.response || "Sorry, I didn't catch that.";
      setRobotMessage(response);
      await logChatToServer('Bot', 'User', response);
      setIsSpeaking(true);
      speakWithVoice(response, () => {
        setIsSpeaking(false);
        interruptedRef.current = false;
      });
    } catch {
      setRobotMessage("âš ï¸ Error reaching assistant.");
    }
  };

  const fetchCollegeName = async () => {
    try {
      const res = await API.get('/college_name');
      setCollegeName(res.data.name || 'Your College');
    } catch {
      setCollegeName('Unknown College');
    }
  };

  const fetchRecognition = async () => {
    try {
      const res = await API.get('/recognized_face');
      const { name } = res.data;
      if (!name || greetedPeopleRef.current.has(name)) return;
      stopSpeaking();
      const isKnown = name.toLowerCase() !== 'unknown';
      const finalName = isKnown ? name : '';
      const greeting = isKnown
        ? `Hello ${finalName}, welcome to ${collegeName}! How can I help you today?`
        : `Hello! I'm the college assistant. How can I help you today?`;
      setRobotMessage(greeting);
      setRecognizedName(finalName);
      setIsSpeaking(true);
      await logChatToServer('Bot', finalName || 'Visitor', greeting);
      speakWithVoice(greeting, () => {
        setIsSpeaking(false);
      });
      greetedPeopleRef.current.add(name);
    } catch {}
  };

  // Camera controls
  const toggleMic = () => setIsMicOn((prev) => !prev);
  const toggleCamera = () => setIsCameraOn((prev) => !prev);

  // Animated glassmorphism card with floating controls and effects
  return (
    <div className="min-h-screen w-full flex items-center justify-center bg-gradient-to-br from-[#0f2027] via-[#2c5364] to-[#24243e] relative overflow-hidden">
      {/* Animated floating blobs */}
      <div className="absolute top-0 left-0 w-full h-full z-0 pointer-events-none">
        <div className="absolute left-1/3 top-10 w-96 h-96 bg-gradient-to-br from-blue-400/30 to-indigo-400/10 rounded-full blur-3xl animate-pulse" />
        <div className="absolute right-10 bottom-10 w-80 h-80 bg-gradient-to-br from-pink-400/30 to-blue-400/10 rounded-full blur-3xl animate-pulse" />
      </div>
      <div className="relative z-10 w-full max-w-5xl mx-auto flex flex-col md:flex-row gap-10 items-center justify-center py-10">
        {/* Camera Card */}
        <div className="glass-card bg-white/20 backdrop-blur-2xl border border-blue-200/30 shadow-2xl rounded-3xl flex flex-col items-center p-0 w-full max-w-md transition-all duration-500 hover:scale-[1.02]">
          <div className="flex items-center justify-between w-full px-8 py-6 border-b border-blue-100/40 bg-white/30 rounded-t-3xl">
            <span className="inline-flex items-center gap-2 text-xl font-bold text-blue-900"><FiCpu /> Robot Camera</span>
            <span className="flex items-center gap-2 text-green-500 font-semibold animate-pulse">
              <span className="w-2 h-2 rounded-full bg-green-500 animate-ping"></span> ACTIVE
            </span>
          </div>
          <div className="relative bg-black flex items-center justify-center min-h-[340px] w-full rounded-b-3xl">
            {isCameraOn ? (
              <img
                ref={videoRef}
                src={`${API_URL}/video_feed`}
                alt="Robot Camera"
                className="object-contain w-full h-[340px] rounded-b-3xl transition-all duration-500 shadow-xl"
              />
            ) : (
              <div className="w-full h-[340px] flex items-center justify-center bg-gray-900 rounded-b-3xl">
                <div className="text-center text-gray-400">
                  <FiPower className="mx-auto text-5xl mb-2" />
                  <p className="text-base">Camera is Off</p>
                </div>
              </div>
            )}
            <canvas
              ref={canvasRef}
              className="absolute top-0 left-0 w-full h-full pointer-events-none"
            />
          </div>
          {/* Recognized Name */}
          {recognizedName && (
            <div className="absolute top-4 right-4 flex items-center gap-2 bg-blue-100/80 px-4 py-2 rounded-xl shadow text-blue-800 animate-fade-in">
              <FiUser /> <span className="font-semibold">{recognizedName}</span>
            </div>
          )}
        </div>
        {/* Robot Response Card */}
        <div className="glass-card bg-white/20 backdrop-blur-2xl border border-indigo-200/30 shadow-2xl rounded-3xl flex flex-col items-center p-0 w-full max-w-md transition-all duration-500 hover:scale-[1.02]">
          <div className="w-full flex flex-col items-center justify-center px-8 py-6 border-b border-indigo-100/40 bg-white/30 rounded-t-3xl">
            <span className="text-2xl font-bold text-indigo-700 mb-1 tracking-wide animate-gradient-x bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500 bg-clip-text text-transparent">{collegeName}</span>
            <div className="mt-2 w-full flex items-center gap-2">
              <FiVolume2 className="text-blue-400" />
              <select
                className="rounded-lg px-2 py-1 bg-white/70 text-blue-900 border border-blue-200 focus:outline-none focus:ring-2 focus:ring-blue-300 text-sm"
                value={selectedVoice ? selectedVoice.name : ''}
                onChange={e => {
                  const v = voices.find(v => v.name === e.target.value);
                  setSelectedVoice(v || null);
                }}
              >
                {voices.map(v => (
                  <option key={v.name} value={v.name}>{v.name} ({v.lang})</option>
                ))}
              </select>
            </div>
          </div>
          <div className="w-full flex flex-col items-center justify-center px-8 py-10">
            <RobotSpeechDisplay message={robotMessage} isSpeaking={isSpeaking} volume={volume} />
          </div>
          {/* Floating Controls */}
          <div className="w-full flex items-center justify-center gap-8 pb-8">
            <button
              onClick={toggleMic}
              className={`p-5 rounded-full shadow-xl text-3xl ${isMicOn ? 'bg-green-500 text-white' : 'bg-gray-300 text-gray-500'} hover:scale-110 transition-all duration-200 border-2 border-white/70`}
              title={isMicOn ? 'Mute Mic' : 'Unmute Mic'}
            >
              {isMicOn ? <FiMic /> : <FiMicOff />}
            </button>
            <button
              onClick={toggleCamera}
              className={`p-5 rounded-full shadow-xl text-3xl ${isCameraOn ? 'bg-blue-500 text-white' : 'bg-gray-300 text-gray-500'} hover:scale-110 transition-all duration-200 border-2 border-white/70`}
              title={isCameraOn ? 'Turn Off Camera' : 'Turn On Camera'}
            >
              <FiPower />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
