import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  FiUsers, 
  FiUserCheck, 
  FiEye, 
  FiUpload, 
  FiGrid, 
  FiActivity, 
  FiDatabase,
  FiTrendingUp,
  FiShield,
  FiZap,
  FiMonitor,
  FiCamera,
  FiClock,
  FiCheckCircle
} from 'react-icons/fi';
import StatsCard from '../components/StatsCard';
import LoadingSpinner from '../components/LoadingSpinner';
import axios from 'axios';
import { motion } from 'framer-motion';

function animateValue(start, end, duration, setter) {
  if (start === end) {
    setter(end);
    return;
  }
  const range = end - start;
  let current = start;
  const increment = range / (duration / 16);
  let startTime = null;

  function step(timestamp) {
    if (!startTime) startTime = timestamp;
    const progress = timestamp - startTime;
    current = start + increment * (progress / 16);
    if ((increment > 0 && current >= end) || (increment < 0 && current <= end)) {
      setter(end);
      return;
    }
    setter(Math.round(current));
    requestAnimationFrame(step);
  }
  requestAnimationFrame(step);
}

export default function Home() {
  const [stats, setStats] = useState({
    students: 0,
    staff: 0,
    unknowns: 0,
    accuracy: 0
  });
  const [loading, setLoading] = useState(true);
  const [displayStats, setDisplayStats] = useState(stats);
  const [robotQuery, setRobotQuery] = useState("");
  const [robotResponse, setRobotResponse] = useState("");

  useEffect(() => {
    setLoading(true);
    axios.get('http://localhost:5000/api/statistics')
      .then(res => {
        const apiStats = {
          students: res.data.total_students,
          staff: res.data.total_staff,
          unknowns: res.data.total_unknowns,
          accuracy: res.data.accuracy_rate
        };
        setStats(apiStats);
        setLoading(false);
        // Animate numbers
        animateValue(displayStats.students, apiStats.students, 800, val => setDisplayStats(s => ({ ...s, students: val })));
        animateValue(displayStats.staff, apiStats.staff, 800, val => setDisplayStats(s => ({ ...s, staff: val })));
        animateValue(displayStats.unknowns, apiStats.unknowns, 800, val => setDisplayStats(s => ({ ...s, unknowns: val })));
        animateValue(displayStats.accuracy, apiStats.accuracy, 800, val => setDisplayStats(s => ({ ...s, accuracy: val })));
      })
      .catch(() => setLoading(false));
    // eslint-disable-next-line
  }, []);

  const features = [
    {
      icon: FiCamera,
      title: "Real-time Recognition",
      description: "Advanced facial recognition with instant identification and tracking capabilities.",
      color: "from-blue-500 to-cyan-500"
    },
    {
      icon: FiShield,
      title: "Secure & Private",
      description: "Enterprise-grade security with encrypted data storage and privacy protection.",
      color: "from-green-500 to-emerald-500"
    },
    {
      icon: FiZap,
      title: "Lightning Fast",
      description: "Optimized algorithms for quick recognition and minimal processing time.",
      color: "from-purple-500 to-pink-500"
    },
    {
      icon: FiMonitor,
      title: "Multi-Camera Support",
      description: "Support for multiple camera feeds with centralized monitoring dashboard.",
      color: "from-orange-500 to-red-500"
    }
  ];

  const quickActions = [
    { icon: FiUsers, title: "View Students", path: "/students", color: "bg-blue-500" },
    { icon: FiUserCheck, title: "View Staff", path: "/staff", color: "bg-green-500" },
    { icon: FiUpload, title: "Upload Image", path: "/upload", color: "bg-purple-500" },
    { icon: FiGrid, title: "Live Feed", path: "/live", color: "bg-orange-500" },
    { icon: FiEye, title: "Unknown Faces", path: "/unknowns", color: "bg-red-500" },
    { icon: FiActivity, title: "View Logs", path: "/logs", color: "bg-indigo-500" },
    { icon: FiDatabase, title: "Chat Logs", path: "/chatlogs", color: "bg-pink-500" },
    { icon: FiDatabase, title: "College Info", path: "/collegeinfo", color: "bg-teal-500" }
  ];

  const statsData = [
    {
      icon: FiUsers,
      title: "Students Recognized",
      value: loading ? 0 : displayStats.students,
      color: "from-blue-500 to-cyan-500",
      delay: 0.1
    },
    {
      icon: FiUserCheck,
      title: "Staff Recognized",
      value: loading ? 0 : displayStats.staff,
      color: "from-green-500 to-emerald-500",
      delay: 0.2
    },
    {
      icon: FiEye,
      title: "Unknown Recognized",
      value: loading ? 0 : displayStats.unknowns,
      color: "from-red-500 to-pink-500",
      delay: 0.3
    }
  ];

  const startVoiceRecognition = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Your browser does not support Speech Recognition.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.interimResults = false;
    recognition.start();

    recognition.onresult = function (event) {
      const query = event.results[0][0].transcript;
      setRobotQuery(query);

      fetch("http://localhost:5000/robot_ask", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query: query })
      })
        .then(res => res.json())
        .then(data => {
          const reply = data.response;
          setRobotResponse(reply);

          const synth = window.speechSynthesis;
          const utter = new window.SpeechSynthesisUtterance(reply);
          synth.speak(utter);
        })
        .catch(err => {
          setRobotResponse("Sorry, something went wrong.");
          console.error("Voice Assistant Error:", err);
        });
    };

    recognition.onerror = function (event) {
      console.error("Speech recognition error", event);
    };
  };

  return (
    <motion.div className="min-h-screen fade-in" initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.8 }}>
      {/* Hero Section */}
      <section className="relative overflow-hidden py-20 px-6 bg-gradient-to-br from-blue-100 via-white to-indigo-100">
        <div className="absolute inset-0 bg-gradient-to-br from-blue-600/10 via-purple-600/10 to-indigo-600/10 animate-pulse"></div>
        <div className="relative max-w-4xl mx-auto text-center">
          <motion.div className="slide-in-up" initial={{ y: 40, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ duration: 0.8 }}>
            <h1 className="text-4xl md:text-6xl font-extrabold mb-4 text-blue-800 drop-shadow-lg">
              Chettinad College Of Engineering and Technology
            </h1>
            <p className="text-lg md:text-2xl text-gray-700 dark:text-gray-200 mb-8 font-medium">

            </p>

          </motion.div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="max-w-7xl mx-auto px-6 py-10 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
        {statsData.map((stat, i) => (
          <motion.div key={stat.title} initial={{ y: 40, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ delay: stat.delay, duration: 0.7 }}>
            <div className="card flex flex-col items-center p-8">
              <span className={`text-4xl mb-4 bg-gradient-to-br ${stat.color} text-white p-3 rounded-full shadow-lg`}>
                <stat.icon />
              </span>
              <h3 className="text-xl font-semibold mb-2 gradient-text">{stat.title}</h3>
              <p className="text-3xl font-bold text-gray-800 dark:text-white">{stat.value}</p>
            </div>
          </motion.div>
        ))}
      </section>

      {/* Features Section */}
      <section className="max-w-7xl mx-auto px-6 py-10">
        <h2 className="text-3xl font-bold mb-8 gradient-text text-center">Key Features</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {features.map((feature, i) => (
            <motion.div key={feature.title} initial={{ y: 40, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ delay: i * 0.15, duration: 0.7 }}>
              <div className={`gradient-card p-8 flex flex-col items-center shadow-xl hover:scale-105 transition-transform duration-300`}>
                <span className={`text-4xl mb-4 bg-gradient-to-br ${feature.color} text-white p-3 rounded-full shadow-lg`}>
                  <feature.icon />
                </span>
                <h3 className="text-xl font-semibold mb-2 gradient-text">{feature.title}</h3>
                <p className="text-gray-600 dark:text-gray-300 text-center">{feature.description}</p>
              </div>
            </motion.div>
          ))}
        </div>
      </section>

      {/* Quick Actions Section */}
      <section className="max-w-7xl mx-auto px-6 py-10">
        <h2 className="text-3xl font-bold mb-8 gradient-text text-center">Quick Actions</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-8 gap-6">
          {quickActions.map((action, i) => (
            <motion.div key={action.title} whileHover={{ scale: 1.08 }} whileTap={{ scale: 0.97 }}>
              <Link to={action.path} className={`flex flex-col items-center justify-center p-6 rounded-2xl shadow-lg text-white ${action.color} hover:shadow-2xl transition-all duration-300`}>
                <action.icon className="text-3xl mb-2" />
                <span className="font-semibold text-lg">{action.title}</span>
              </Link>
            </motion.div>
          ))}
        </div>
      </section>

      {/* System Status Section */}
      <section className="py-16 px-6">
        <div className="max-w-4xl mx-auto">
          <div className="glass-card p-8 text-center">
            <h2 className="text-3xl font-bold mb-8 gradient-text">
              System Status
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="flex items-center justify-center space-x-3">
                <div className="status-online"></div>
                <span className="text-green-600 dark:text-green-400 font-semibold">Database Connected</span>
              </div>
              <div className="flex items-center justify-center space-x-3">
                <div className="status-online"></div>
                <span className="text-green-600 dark:text-green-400 font-semibold">Recognition Active</span>
              </div>
              <div className="flex items-center justify-center space-x-3">
                <div className="status-online"></div>
                <span className="text-green-600 dark:text-green-400 font-semibold">All Systems Online</span>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Voice Assistant Section */}
      <section className="max-w-3xl mx-auto px-6 py-10">
        <div className="glass-card p-8 flex flex-col items-center">
          <h2 className="text-2xl font-bold mb-4 gradient-text">Ask the Robot</h2>
          <div className="flex w-full gap-4">
            <input
              className="flex-1 rounded-xl px-4 py-3 border border-gray-300 dark:border-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500 text-lg"
              placeholder="Type your question..."
              value={robotQuery}
              onChange={e => setRobotQuery(e.target.value)}
            />
            <motion.button whileHover={{ scale: 1.08 }} whileTap={{ scale: 0.97 }} className="btn-primary px-8" onClick={startVoiceRecognition}>
              🎤
            </motion.button>
          </div>
          {robotResponse && (
            <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} className="mt-6 p-4 rounded-xl bg-gradient-to-r from-blue-100 to-indigo-100 dark:from-blue-900 dark:to-indigo-900 text-lg text-center shadow-lg">
              {robotResponse}
            </motion.div>
          )}
        </div>
      </section>

      {/* Footer */}
      <footer className="py-8 px-6 bg-gradient-to-r from-gray-800 to-gray-900 text-white">
        <div className="max-w-7xl mx-auto text-center">
          <p className="text-gray-300">
            © 2024 Face Recognition System. Powered by advanced AI technology.
          </p>
        </div>
      </footer>
    </motion.div>
  );
}
