import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import { FiCpu, FiMic } from 'react-icons/fi';
import RobotSpeechDisplay from './RobotSpeechDisplay';
import { speakWithAudioFeedback } from '../utils/voice';

const API_URL = 'http://localhost:5000';

export default function RobotController() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [recognizedName, setRecognizedName] = useState('');
  const [collegeName, setCollegeName] = useState('');
  const [robotMessage, setRobotMessage] = useState('🎓 Initializing...');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [volume, setVolume] = useState(0);

  // Track face presence
  const [currentFace, setCurrentFace] = useState(null);
  const [lastSeenTime, setLastSeenTime] = useState(Date.now());
  const FACE_TIMEOUT = 5000;

  let micListening = useRef(false);

  useEffect(() => {
    fetchCollegeName();
    const interval = setInterval(() => {
      if (!isSpeaking) detectFace();
    }, 1500);
    return () => clearInterval(interval);
  }, [isSpeaking]);

  useEffect(() => {
    stabilizeLayout();
    const obs = new ResizeObserver(stabilizeLayout);
    if (videoRef.current) obs.observe(videoRef.current);
    return () => obs.disconnect();
  }, [recognizedName]);

  const fetchCollegeName = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/college_name`);
      setCollegeName(res.data.name || 'Your College');
    } catch {
      setCollegeName('Your College');
    }
  };

  const detectFace = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/recognized_face`);
      const { name } = res.data;
      const now = Date.now();

      if (!name || name === 'Unknown') {
        if (currentFace && now - lastSeenTime > FACE_TIMEOUT) {
          setCurrentFace(null);
        }
        return;
      }

      setLastSeenTime(now);

      if (name !== currentFace) {
        const greeting = `Hi ${name}, I am the robot assistant of ${collegeName}. How can I help?`;
        triggerResponse(greeting);
        setCurrentFace(name);
      }
    } catch (err) {
      console.error(err);
    }
  };

  const triggerResponse = (text) => {
    if (isSpeaking) {
      window.speechSynthesis.cancel();
    }
    setRobotMessage(text);
    setIsSpeaking(true);

    speakWithAudioFeedback(text, setVolume, () => {
      setIsSpeaking(false);
      if (!micListening.current) {
        micListening.current = true;
        listenForUser();
      }
    });
  };

  const listenForUser = async () => {
    if (isSpeaking) return;
    setRobotMessage('🎤 Listening...');
    try {
      const res = await axios.post(`${API_URL}/api/robot_ask`, {
        query: 'listen_voice',
      });
      const answer = res.data.response || "Sorry, I didn't catch that.";
      micListening.current = false;
      triggerResponse(answer);
    } catch {
      setRobotMessage("❌ AI assistant unreachable.");
    }
  };

  const stabilizeLayout = () => {
    const img = videoRef.current;
    const canvas = canvasRef.current;
    if (!img || !canvas) return;

    canvas.width = img.clientWidth;
    canvas.height = img.clientHeight;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.fillStyle = currentFace ? '#28a745' : '#d63384';
    ctx.font = 'bold 1.5rem sans-serif';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'top';
    ctx.fillText(
      currentFace ? `🎉 Hello, ${currentFace}` : '🤖 Face Not Recognized',
      canvas.width / 2,
      20
    );
    setRecognizedName(currentFace);
  };

  return (
    <div className="max-w-5xl mx-auto p-4 space-y-6">
      <div className="glass-card p-6 flex items-center space-x-3">
        <FiCpu size={24} className="text-white p-3 rounded-xl bg-gradient-to-r from-purple-600 to-indigo-600" />
        <div>
          <h1 className="text-3xl font-bold gradient-text">Robot Controller</h1>
          <p className="text-gray-600">Stable interactive robot interface</p>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="glass-card overflow-hidden relative h-[450px]">
          <img
            ref={videoRef}
            src={`${API_URL}/video_feed`}
            alt="Robot Camera"
            className="absolute inset-0 w-full h-full object-cover"
          />
          <canvas ref={canvasRef} className="absolute inset-0 w-full h-full"></canvas>
        </div>

        <div className="glass-card flex flex-col justify-between p-6 h-[450px]">
          <h2 className="text-xl font-bold flex items-center">
            <FiMic className="mr-2" /> Assistant
          </h2>
          <RobotSpeechDisplay
            message={robotMessage}
            isSpeaking={isSpeaking}
            volume={volume}
          />
          <div className="text-sm flex justify-between pt-4">
            <span>College: <strong>{collegeName}</strong></span>
            <span>Mic: {isSpeaking ? 'Speaking' : 'Idle'}</span>
          </div>
        </div>
      </div>
    </div>
  );
}
