import React, { useEffect, useRef, useState } from 'react';
import { FiCpu, FiMic, FiMicOff, FiPower, FiCamera, FiUser, FiVolume2 } from 'react-icons/fi';
import RobotSpeechDisplay from './RobotSpeechDisplay';
import {
  speakWithAudioFeedback,
  stopSpeaking,
  startListening,
} from '../utils/voice';
import API from '../utils/api';
const API_URL = API.defaults.baseURL.replace('/api', '');

export default function RobotController() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [recognizedName, setRecognizedName] = useState('');
  const [collegeName, setCollegeName] = useState('');
  const [robotMessage, setRobotMessage] = useState('ðŸ¤– Ready to assist you!');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [volume, setVolume] = useState(0);
  const [isMicOn, setIsMicOn] = useState(true);
  const [isCameraOn, setIsCameraOn] = useState(true);

  const greetedPeopleRef = useRef(new Set());
  const interruptedRef = useRef(false);

  useEffect(() => {
    greetedPeopleRef.current.clear();
    fetchCollegeName();
    const interval = setInterval(fetchRecognition, 2500);
    startListening(handleTranscript);
    return () => {
      clearInterval(interval);
      stopSpeaking();
    };
  }, []);

  const logChatToServer = async (sender, receiver, message) => {
    try {
      await API.post('/log_chat', { sender, receiver, message });
    } catch {}
  };

  const handleTranscript = async (text, isFinal) => {
    if (!isFinal || !text.trim()) return;
    if (isSpeaking) {
      stopSpeaking();
      setIsSpeaking(false);
      interruptedRef.current = true;
    }
    const userQuery = text.trim();
    setRobotMessage(`ðŸ§  Processing: ${userQuery}`);
    await logChatToServer('User', 'Bot', userQuery);
    try {
      const res = await API.post('/robot_ask', { query: userQuery });
      const response = res.data.response || "Sorry, I didn't catch that.";
      setRobotMessage(response);
      await logChatToServer('Bot', 'User', response);
      setIsSpeaking(true);
      speakWithAudioFeedback(response, setVolume, () => {
        setIsSpeaking(false);
        interruptedRef.current = false;
      }, handleTranscript);
    } catch {
      setRobotMessage("âš ï¸ Error reaching assistant.");
    }
  };

  const fetchCollegeName = async () => {
    try {
      const res = await API.get('/college_name');
      setCollegeName(res.data.name || 'Your College');
    } catch {
      setCollegeName('Unknown College');
    }
  };

  const fetchRecognition = async () => {
    try {
      const res = await API.get('/recognized_face');
      const { name } = res.data;
      if (!name || greetedPeopleRef.current.has(name)) return;
      stopSpeaking();
      const isKnown = name.toLowerCase() !== 'unknown';
      const finalName = isKnown ? name : '';
      const greeting = isKnown
        ? `Hello ${finalName}, welcome to ${collegeName}! How can I help you today?`
        : `Hello! I'm the college assistant. How can I help you today?`;
      setRobotMessage(greeting);
      setRecognizedName(finalName);
      setIsSpeaking(true);
      await logChatToServer('Bot', finalName || 'Visitor', greeting);
      speakWithAudioFeedback(greeting, setVolume, () => {
        setIsSpeaking(false);
      }, handleTranscript);
      greetedPeopleRef.current.add(name);
    } catch {}
  };

  // Camera controls
  const toggleMic = () => setIsMicOn((prev) => !prev);
  const toggleCamera = () => setIsCameraOn((prev) => !prev);

  // New layout: sidebar for controls, main for camera & robot
  return (
    <div className="min-h-screen w-full flex bg-gradient-to-br from-blue-50 via-white to-indigo-100">
      {/* Sidebar Controls */}
      <aside className="w-20 md:w-28 flex flex-col items-center py-8 bg-white/80 border-r border-blue-100 shadow-xl gap-8">
        <div className="flex flex-col items-center gap-4">
          <span className="inline-flex items-center justify-center w-14 h-14 rounded-full bg-gradient-to-br from-indigo-500 to-blue-500 shadow-lg">
            <FiCpu size={32} className="text-white" />
          </span>
          <span className="text-xs font-bold text-blue-900 text-center">Robot</span>
        </div>
        <button
          onClick={toggleCamera}
          className={`p-4 rounded-full bg-blue-100 hover:bg-blue-200 text-blue-700 shadow transition text-2xl ${isCameraOn ? 'ring-2 ring-blue-400' : ''}`}
          title={isCameraOn ? 'Turn Off Camera' : 'Turn On Camera'}
        >
          <FiCamera />
        </button>
        <button
          onClick={toggleMic}
          className={`p-4 rounded-full bg-green-100 hover:bg-green-200 text-green-700 shadow transition text-2xl ${isMicOn ? 'ring-2 ring-green-400' : ''}`}
          title={isMicOn ? 'Mute Mic' : 'Unmute Mic'}
        >
          {isMicOn ? <FiMic /> : <FiMicOff />}
        </button>
      </aside>
      {/* Main Content */}
      <main className="flex-1 flex flex-col md:flex-row items-center justify-center gap-10 py-10 px-4 md:px-12">
        {/* Camera Feed */}
        <section className="flex-1 flex flex-col items-center justify-center max-w-xl w-full">
          <div className="w-full rounded-3xl overflow-hidden shadow-2xl bg-white/90 border-2 border-blue-100 flex flex-col items-center">
            <div className="flex items-center justify-between w-full px-6 py-4 border-b border-blue-100 bg-white/95">
              <span className="font-bold text-lg text-blue-900 tracking-wide flex items-center gap-2">
                <FiCamera className="text-blue-400" /> Camera
              </span>
              <span className="flex items-center gap-2 text-green-500 font-semibold animate-pulse">
                <span className="w-2 h-2 rounded-full bg-green-500 animate-ping"></span> ACTIVE
              </span>
            </div>
            <div className="relative bg-black flex items-center justify-center min-h-[340px] w-full">
              {isCameraOn ? (
                <img
                  ref={videoRef}
                  src={`${API_URL}/video_feed`}
                  alt="Robot Camera"
                  className="object-contain w-full h-[340px] rounded-b-3xl"
                />
              ) : (
                <div className="w-full h-[340px] flex items-center justify-center bg-gray-900 rounded-b-3xl">
                  <div className="text-center text-gray-400">
                    <FiPower className="mx-auto text-5xl mb-2" />
                    <p className="text-base">Camera is Off</p>
                  </div>
                </div>
              )}
              <canvas
                ref={canvasRef}
                className="absolute top-0 left-0 w-full h-full pointer-events-none"
              />
            </div>
          </div>
          {/* Recognized Name Display */}
          {recognizedName && (
            <div className="mt-4 flex items-center gap-2 bg-blue-100 px-4 py-2 rounded-xl shadow text-blue-800">
              <FiUser /> <span className="font-semibold">{recognizedName}</span>
            </div>
          )}
        </section>
        {/* Robot Response Section */}
        <section className="flex-1 flex flex-col items-center justify-center max-w-xl w-full">
          <div className="w-full bg-white/90 rounded-3xl shadow-2xl p-8 border-2 border-indigo-100 flex flex-col items-center">
            <div className="mb-4 text-center">
              <span className="text-2xl font-bold text-indigo-700">{collegeName}</span>
            </div>
            <RobotSpeechDisplay message={robotMessage} isSpeaking={isSpeaking} volume={volume} />
            <div className="mt-6 flex items-center gap-4">
              <span className="text-gray-500 text-xs">Voice:</span>
              <span className="inline-flex items-center gap-1 text-blue-600 font-semibold">
                <FiVolume2 /> {isSpeaking ? 'Speaking' : 'Idle'}
              </span>
            </div>
          </div>
        </section>
      </main>
    </div>
  );
}
