import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import { FiCpu } from 'react-icons/fi';
import RobotSpeechDisplay from './RobotSpeechDisplay';
import {
  speakWithAudioFeedback,
  stopSpeaking,
  startListening,
  stopListening,
} from '../utils/voice';

const API_URL = 'http://localhost:5000';

export default function RobotController() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [recognizedName, setRecognizedName] = useState('');
  const [collegeName, setCollegeName] = useState('');
  const [robotMessage, setRobotMessage] = useState('🎓 Initializing...');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [volume, setVolume] = useState(0);
  const [lastRecognized, setLastRecognized] = useState('');
  const [greetedFaces, setGreetedFaces] = useState(new Set());

  useEffect(() => {
    fetchCollegeName();
    const interval = setInterval(fetchRecognition, 2000);
    startListening(handleTranscript);
    return () => {
      clearInterval(interval);
      stopListening();
    };
  }, []);

  const handleTranscript = async (text, isFinal) => {
    if (!isFinal) return;

    stopSpeaking(); // cancel robot speaking if user starts speaking
    stopListening();

    setRobotMessage(`🧠 Processing: ${text}`);
    try {
      const res = await axios.post(`${API_URL}/api/robot_ask`, {
        query: text,
      });

      const response = res.data.response || "Sorry, I didn’t catch that.";
      speakWithAudioFeedback(response, setVolume, () => {
        setIsSpeaking(false);
        setRobotMessage(response);
        startListening(handleTranscript);
      });
      setRobotMessage(response);
      setIsSpeaking(true);
    } catch (err) {
      console.error('Voice fetch failed:', err);
      setRobotMessage("⚠️ Error reaching assistant.");
    }
  };

  const fetchCollegeName = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/college_name`);
      setCollegeName(res.data.name || 'Your College');
    } catch {
      setCollegeName('Unknown College');
    }
  };

  const fetchRecognition = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/recognized_face`);
      const { name } = res.data;

      if (name && name !== 'Unknown') {
        if (!greetedFaces.has(name)) {
          const greeting = `Hi ${name}, I'm your assistant at ${collegeName}. How can I help you today?`;
          stopSpeaking();
          speakWithAudioFeedback(greeting, setVolume, () => {
            setIsSpeaking(false);
          });
          setRobotMessage(greeting);
          setRecognizedName(name);
          setLastRecognized(name);
          setIsSpeaking(true);
          setGreetedFaces(prev => new Set(prev).add(name));
        } else {
          setRecognizedName(name); // still update name
        }
      } else {
        setRecognizedName('');
      }
    } catch (err) {
      console.error('Face recognition failed:', err);
    }
  };

  // Draw overlay (only for recognized faces, no unknown text)
  useEffect(() => {
    const drawOverlay = () => {
      const canvas = canvasRef.current;
      const ctx = canvas?.getContext('2d');
      const img = videoRef.current;
      if (!canvas || !ctx || !img) return;

      canvas.width = img.clientWidth;
      canvas.height = img.clientHeight;
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      if (recognizedName) {
        ctx.save();
        ctx.fillStyle = '#28a745';
        ctx.font = 'bold 2rem sans-serif';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'top';
        ctx.fillText(`🎉 Hello, ${recognizedName}`, canvas.width / 2, 20);
        ctx.restore();
      }
    };

    const interval = setInterval(drawOverlay, 500);
    return () => clearInterval(interval);
  }, [recognizedName]);

  return (
    <div className="space-y-6 fade-in max-w-5xl mx-auto">
      <div className="glass-card p-6">
        <div className="flex items-center space-x-3">
          <div className="p-3 rounded-xl bg-gradient-to-r from-purple-600 to-indigo-600 text-white">
            <FiCpu size={24} />
          </div>
          <div>
            <h1 className="text-3xl font-bold gradient-text">Robot Controller</h1>
            <p className="text-gray-600 dark:text-gray-300">
              Voice Streaming + Face Recognition
            </p>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Camera Feed */}
        <div className="glass-card overflow-hidden relative min-h-[320px]">
          <img
            ref={videoRef}
            src={`${API_URL}/video_feed`}
            alt="Robot Camera"
            className="w-full h-auto object-cover rounded-xl"
          />
          <canvas
            ref={canvasRef}
            className="absolute top-0 left-0 w-full h-full pointer-events-none"
          />
        </div>

        {/* Avatar Display */}
        <div className="glass-card p-6 flex flex-col justify-center items-center min-h-[320px]">
          <RobotSpeechDisplay
            message={robotMessage}
            isSpeaking={isSpeaking}
            volume={volume}
          />
          <p className="text-xs text-gray-500 mt-4">
            Listening... You can speak anytime 👂
          </p>
        </div>
      </div>
    </div>
  );
}
