import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { 
  FiActivity, 
  FiSearch, 
  FiFilter, 
  FiRefreshCw, 
  FiEye, 
  FiDownload,
  FiCalendar,
  FiUser,
  FiUsers,
  FiEye as FiUnknown,
  FiClock,
  FiPercent,
  FiImage
} from 'react-icons/fi';
import LoadingSpinner from '../components/LoadingSpinner';

export default function RecognitionLog() {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('');
  const [filterDate, setFilterDate] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(20);

  useEffect(() => {
    fetchLogs();
  }, []);

    const fetchLogs = async () => {
      try {
      setLoading(true);
        const res = await axios.get('http://localhost:5000/api/logs');
        setLogs(res.data);
      } catch (error) {
        console.error('Error fetching logs:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredLogs = logs.filter(log => {
    const matchesSearch = log.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = !filterType || log.type === filterType;
    const matchesDate = !filterDate || log.timestamp.startsWith(filterDate);
    return matchesSearch && matchesType && matchesDate;
  });

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentLogs = filteredLogs.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredLogs.length / itemsPerPage);

  const getTypeIcon = (type) => {
    switch (type) {
      case 'student': return <FiUser className="text-blue-500" />;
      case 'staff': return <FiUsers className="text-green-500" />;
      case 'unknown': return <FiUnknown className="text-red-500" />;
      default: return <FiUser className="text-gray-500" />;
    }
  };

  const getTypeColor = (type) => {
    switch (type) {
      case 'student': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
      case 'staff': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      case 'unknown': return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200';
    }
  };

  const getConfidenceColor = (confidence) => {
    const conf = parseFloat(confidence);
    if (conf >= 0.8) return 'text-green-600 dark:text-green-400';
    if (conf >= 0.6) return 'text-yellow-600 dark:text-yellow-400';
    return 'text-red-600 dark:text-red-400';
  };

  const exportLogs = () => {
    const csvContent = [
      ['ID', 'Name', 'Type', 'Confidence', 'Timestamp', 'Image Path'].join(','),
      ...filteredLogs.map(log => [
        log.id,
        log.name,
        log.type,
        log.confidence,
        log.timestamp,
        log.image_path || ''
      ].join(','))
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `recognition_logs_${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const clearAllLogs = async () => {
    if (!window.confirm('Are you sure you want to clear all recognition logs? This action cannot be undone.')) return;
    try {
      setLoading(true);
      await axios.post('http://localhost:5000/api/recognition_logs/clear');
      await fetchLogs();
    } catch (error) {
      alert('Failed to clear recognition logs.');
      console.error('Error clearing recognition logs:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="space-y-6 fade-in">
      <div className="glass-card p-6">
        <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
          <div className="flex items-center space-x-3">
            <div className="p-3 rounded-xl bg-gradient-to-r from-indigo-500 to-purple-500 text-white">
              <FiActivity size={24} />
            </div>
            <div>
              <h1 className="text-3xl font-bold gradient-text">Recognition Logs</h1>
              <p className="text-gray-600 dark:text-gray-300">Track and analyze face recognition events</p>
            </div>
          </div>
          <div className="flex items-center space-x-3">
            <button onClick={exportLogs} className="btn-secondary">
              <FiDownload className="mr-2" />
              Export CSV
            </button>
            <button onClick={fetchLogs} className="btn-secondary" disabled={loading}>
              <FiRefreshCw className={`mr-2 ${loading ? 'animate-spin' : ''}`} />
              Refresh
            </button>
            <button onClick={clearAllLogs} className="btn-danger" disabled={loading}>
              Clear All
            </button>
          </div>
        </div>
      </div>

      <div className="glass-card p-6">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="relative">
            <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
            <input
              type="text"
              placeholder="Search by name..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="enhanced-input pl-10"
            />
          </div>
          <div className="relative">
            <FiFilter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
            <select
              value={filterType}
              onChange={(e) => setFilterType(e.target.value)}
              className="enhanced-input pl-10"
            >
              <option value="">All Types</option>
              <option value="student">Students</option>
              <option value="staff">Staff</option>
              <option value="unknown">Unknown</option>
            </select>
          </div>
          <div className="relative">
            <FiCalendar className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
            <input
              type="date"
              value={filterDate}
              onChange={(e) => setFilterDate(e.target.value)}
              className="enhanced-input pl-10"
            />
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <div className="gradient-card p-4 text-center">
          <div className="text-2xl font-bold text-gray-800 dark:text-white">{logs.length}</div>
          <div className="text-gray-600 dark:text-gray-300">Total Logs</div>
        </div>
        <div className="gradient-card p-4 text-center">
          <div className="text-2xl font-bold text-gray-800 dark:text-white">
            {logs.filter(log => log.type === 'student').length}
          </div>
          <div className="text-gray-600 dark:text-gray-300">Students</div>
        </div>
        <div className="gradient-card p-4 text-center">
          <div className="text-2xl font-bold text-gray-800 dark:text-white">
            {logs.filter(log => log.type === 'staff').length}
          </div>
          <div className="text-gray-600 dark:text-gray-300">Staff</div>
        </div>
        <div className="gradient-card p-4 text-center">
          <div className="text-2xl font-bold text-gray-800 dark:text-white">
            {logs.filter(log => log.type === 'unknown').length}
          </div>
          <div className="text-gray-600 dark:text-gray-300">Unknown</div>
        </div>
      </div>

      <div className="glass-card overflow-hidden">
        {loading ? (
          <div className="p-12">
            <LoadingSpinner size="lg" text="Loading recognition logs..." />
          </div>
        ) : (
          <>
            <div className="overflow-x-auto">
              <table className="enhanced-table">
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Name</th>
                    <th>Type</th>
                    <th>Confidence</th>
                    <th>Timestamp</th>
                    <th>Image</th>
                    <th>Actions</th>
            </tr>
          </thead>
          <tbody>
                  {currentLogs.map((log, index) => (
                    <tr key={log.id || index} className="slide-in-up" style={{ animationDelay: `${index * 0.05}s` }}>
                      <td className="font-mono font-semibold">#{log.id || index + 1}</td>
                      <td className="font-semibold">{log.name}</td>
                      <td>
                        <div className="flex items-center space-x-2">
                          {getTypeIcon(log.type)}
                          <span className={`px-2 py-1 rounded-full text-xs font-semibold ${getTypeColor(log.type)}`}>
                            {log.type}
                          </span>
                        </div>
                      </td>
                      <td>
                        <div className="flex items-center space-x-2">
                          <FiPercent className="text-gray-400" />
                          <span className={`font-semibold ${getConfidenceColor(log.confidence)}`}>
                            {(parseFloat(log.confidence) * 100).toFixed(1)}%
                          </span>
                        </div>
                      </td>
                      <td>
                        <div className="flex items-center space-x-2">
                          <FiClock className="text-gray-400" />
                          <span>{new Date(log.timestamp).toLocaleString()}</span>
                        </div>
                      </td>
                      <td>
                  {log.image_path ? (
                          <div className="flex justify-center">
                    <img
                      src={`http://localhost:5000/${log.image_path}`}
                      alt="snapshot"
                              className="w-12 h-12 object-cover rounded-lg shadow-md border-2 border-white dark:border-gray-700 hover:scale-110 transition-transform duration-200"
                    />
                          </div>
                  ) : (
                          <div className="flex justify-center">
                            <FiImage className="text-gray-400" size={20} />
                          </div>
                  )}
                </td>
                      <td>
                        <div className="flex space-x-2">
                          <button className="p-2 rounded-lg bg-blue-100 text-blue-600 hover:bg-blue-200 transition-colors duration-200">
                            <FiEye size={16} />
                          </button>
                        </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
            </div>

            {totalPages > 1 && (
              <div className="p-4 border-t border-gray-200 dark:border-gray-700">
                <div className="flex items-center justify-between">
                  <div className="text-sm text-gray-600 dark:text-gray-300">
                    Showing {indexOfFirstItem + 1} to {Math.min(indexOfLastItem, filteredLogs.length)} of {filteredLogs.length} results
                  </div>
                  <div className="flex space-x-2">
                    <button
                      onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                      disabled={currentPage === 1}
                      className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      Previous
                    </button>
                    <span className="px-3 py-2 text-sm text-gray-600 dark:text-gray-300">
                      Page {currentPage} of {totalPages}
                    </span>
                    <button
                      onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                      disabled={currentPage === totalPages}
                      className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      Next
                    </button>
                  </div>
                </div>
              </div>
            )}
          </>
        )}
        
        {!loading && filteredLogs.length === 0 && (
          <div className="p-12 text-center">
            <FiActivity className="mx-auto text-6xl text-gray-400 mb-4" />
            <p className="text-gray-600 dark:text-gray-300 text-lg">
              {logs.length === 0 ? 'No recognition logs found' : 'No results match your filters'}
            </p>
            <p className="text-gray-500 dark:text-gray-400">
              {logs.length === 0 
                ? 'Start the live feed to generate recognition logs.'
                : 'Try adjusting your search or filter criteria.'
              }
            </p>
          </div>
        )}
      </div>
    </div>
  );
}
