import React, { useEffect, useRef, useState } from 'react';
import { FiMic, FiMicOff, FiPower, FiUser, FiVolume2 } from 'react-icons/fi';
import RobotSpeechDisplay from './RobotSpeechDisplay';
import {
  speakWithAudioFeedback,
  stopSpeaking,
  startListening,
} from '../utils/voice';
import API from '../utils/api';
const API_URL = API.defaults.baseURL.replace('/api', '');

export default function RobotController() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [recognizedName, setRecognizedName] = useState('');
  const [collegeName, setCollegeName] = useState('');
  const [robotMessage, setRobotMessage] = useState('ðŸŽ“ Initializing...');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [volume, setVolume] = useState(0);
  const [isMicOn, setIsMicOn] = useState(true);
  const [isCameraOn, setIsCameraOn] = useState(true);

  const greetedPeopleRef = useRef(new Set());
  const interruptedRef = useRef(false);

  useEffect(() => {
    greetedPeopleRef.current.clear();
    fetchCollegeName();

    const interval = setInterval(fetchRecognition, 2000);
    startListening(handleTranscript);

    return () => {
      clearInterval(interval);
      stopSpeaking();
    };
  }, []);

  const logChatToServer = async (sender, receiver, message) => {
    try {
      await API.post('/log_chat', {
        sender,
        receiver,
        message,
      });
    } catch (err) {
      console.error('Logging chat failed:', err);
    }
  };

  const handleTranscript = async (text, isFinal) => {
    if (!isFinal || !text.trim()) return;

    if (isSpeaking) {
      stopSpeaking();
      setIsSpeaking(false);
      interruptedRef.current = true;
    }

    const userQuery = text.trim();
    setRobotMessage(`ðŸ§  Processing: ${userQuery}`);
    await logChatToServer('User', 'Bot', userQuery);

    try {
      const res = await API.post('/robot_ask', { query: userQuery });
      const response = res.data.response || "Sorry, I didn't catch that.";
      setRobotMessage(response);
      await logChatToServer('Bot', 'User', response);

      setIsSpeaking(true);
      speakWithAudioFeedback(response, setVolume, () => {
        setIsSpeaking(false);
        interruptedRef.current = false;
      }, handleTranscript);
    } catch (err) {
      console.error('Voice fetch failed:', err);
      setRobotMessage("âš ï¸ Error reaching assistant.");
    }
  };

  const fetchCollegeName = async () => {
    try {
      const res = await API.get('/college_name');
      setCollegeName(res.data.name || 'Your College');
    } catch {
      setCollegeName('Unknown College');
    }
  };

  const fetchRecognition = async () => {
    try {
      const res = await API.get('/recognized_face');
      const { name } = res.data;

      if (!name || greetedPeopleRef.current.has(name)) return;

      stopSpeaking();

      const isKnown = name.toLowerCase() !== 'unknown';
      const finalName = isKnown ? name : '';
      const greeting = isKnown
        ? `Hi ${finalName}, I'm your assistant at ${collegeName}. How can I help you today?`
        : `Hi, I'm the college bot. How can I help you today?`;

      setRobotMessage(greeting);
      setRecognizedName(finalName);
      setIsSpeaking(true);

      await logChatToServer('Bot', finalName || 'Visitor', greeting);

      speakWithAudioFeedback(greeting, setVolume, () => {
        setIsSpeaking(false);
      }, handleTranscript);

      greetedPeopleRef.current.add(name);
    } catch (err) {
      console.error('Face recognition failed:', err);
    }
  };

  useEffect(() => {
    const drawOverlay = () => {
      const canvas = canvasRef.current;
      const ctx = canvas?.getContext('2d');
      const img = videoRef.current;
      if (!canvas || !ctx || !img) return;

      const width = img.clientWidth;
      const height = img.clientHeight;

      canvas.width = width;
      canvas.height = height;
      ctx.clearRect(0, 0, width, height);
    };

    const drawInterval = setInterval(drawOverlay, 500);
    return () => clearInterval(drawInterval);
  }, [recognizedName]);

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-white via-blue-50 to-indigo-100 p-4">
      <div className="w-full max-w-3xl rounded-3xl shadow-2xl bg-white/95 border border-blue-100 flex flex-col md:flex-row overflow-hidden">
        {/* Left: Robot Avatar & Chat */}
        <div className="flex-1 flex flex-col items-center justify-center p-8 bg-gradient-to-b from-blue-50 to-white">
          <div className="flex flex-col items-center mb-6">
            <div className="w-28 h-28 rounded-full bg-gradient-to-br from-indigo-400 to-blue-400 flex items-center justify-center shadow-lg mb-2">
              <FiUser size={60} className="text-white" />
            </div>
            <div className="font-bold text-lg text-blue-900 mb-1">{collegeName || 'College Bot'}</div>
            <div className="text-xs text-blue-400">{recognizedName ? `Hi, ${recognizedName}!` : 'Welcome!'}</div>
          </div>
          <div className="w-full flex flex-col items-center">
            <div className="w-full max-w-xs bg-blue-100/80 rounded-2xl shadow px-5 py-4 mb-4 relative">
              <RobotSpeechDisplay message={robotMessage} isSpeaking={isSpeaking} volume={volume} />
              <span className="absolute -bottom-3 left-8 w-4 h-4 bg-blue-100 rotate-45 rounded-sm"></span>
            </div>
            <div className="flex gap-4 mt-2">
              <button
                onClick={() => setIsMicOn((prev) => !prev)}
                className={`p-4 rounded-full shadow text-2xl transition-all duration-200 border ${isMicOn ? 'bg-green-500 text-white border-green-400' : 'bg-gray-200 text-gray-400 border-gray-300'}`}
                title={isMicOn ? 'Mute Mic' : 'Unmute Mic'}
              >
                {isMicOn ? <FiMic /> : <FiMicOff />}
              </button>
              <button
                onClick={() => setIsCameraOn((prev) => !prev)}
                className={`p-4 rounded-full shadow text-2xl transition-all duration-200 border ${isCameraOn ? 'bg-blue-500 text-white border-blue-400' : 'bg-gray-200 text-gray-400 border-gray-300'}`}
                title={isCameraOn ? 'Turn Off Camera' : 'Turn On Camera'}
              >
                <FiPower />
              </button>
            </div>
          </div>
        </div>
        {/* Right: Camera Feed */}
        <div className="flex-1 flex flex-col items-center justify-center bg-white p-6 border-l border-blue-100 min-h-[400px]">
          <div className="w-full flex flex-col items-center">
            <div className="w-full h-64 rounded-2xl overflow-hidden bg-gray-900 flex items-center justify-center mb-4 shadow">
              {isCameraOn ? (
                <img
                  ref={videoRef}
                  src={`${API_URL}/video_feed`}
                  alt="Robot Camera"
                  className="object-cover w-full h-full"
                />
              ) : (
                <div className="flex flex-col items-center justify-center w-full h-full text-gray-400">
                  <FiPower className="text-5xl mb-2" />
                  <span className="text-base">Camera is Off</span>
                </div>
              )}
              <canvas
                ref={canvasRef}
                className="absolute top-0 left-0 w-full h-full pointer-events-none"
              />
            </div>
            <div className="flex items-center gap-2 mt-2">
              <FiVolume2 className={`text-xl ${isSpeaking ? 'text-blue-500 animate-pulse' : 'text-gray-300'}`} />
              <span className="text-xs text-gray-500">{isSpeaking ? 'Speaking...' : 'Idle'}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
