import React, { useState, useRef } from 'react';
import axios from 'axios';
import { 
  FiUpload, FiUser, FiUsers, FiFile, FiX, FiCheckCircle, FiCamera, FiDatabase 
} from 'react-icons/fi';
import LoadingSpinner from '../components/LoadingSpinner';
import Notification from '../components/Notification';

export default function UploadImage() {
  const [name, setName] = useState('');
  const [ageOrRole, setAgeOrRole] = useState('');
  const [department, setDepartment] = useState('');
  const [type, setType] = useState('student');
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [dragActive, setDragActive] = useState(false);
  const [notification, setNotification] = useState(null);
  const fileInputRef = useRef(null);

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFile(e.dataTransfer.files[0]);
    }
  };

  const handleFile = (selectedFile) => {
    if (selectedFile.type.startsWith('image/')) {
      setFile(selectedFile);
      const reader = new FileReader();
      reader.onload = (e) => setPreview(e.target.result);
      reader.readAsDataURL(selectedFile);
    } else {
      showNotification('error', 'Invalid file type', 'Please select an image file.');
    }
  };

  const showNotification = (type, title, message) => {
    setNotification({ type, title, message });
    setTimeout(() => setNotification(null), 5000);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) {
      showNotification('error', 'No file selected', 'Please select an image to upload.');
      return;
    }
    if (!name.trim() || !department.trim() || !ageOrRole.trim()) {
      showNotification('error', 'Missing information', 'Please fill in all required fields.');
      return;
    }
    setUploading(true);
    const formData = new FormData();
    formData.append('image', file);
    formData.append('name', name);
    formData.append('department', department);
    formData.append('type', type);
    formData.append('age_or_role', ageOrRole);
    try {
      await axios.post('http://localhost:5000/api/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      showNotification('success', 'Upload successful!', 'The image has been uploaded successfully.');
      resetForm();
    } catch (error) {
      console.error('Upload error:', error);
      showNotification('error', 'Upload failed', 'Failed to upload image. Please try again.');
    } finally {
      setUploading(false);
    }
  };

  const resetForm = () => {
    setName('');
    setAgeOrRole('');
    setDepartment('');
    setFile(null);
    setPreview(null);
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  const removeFile = () => {
    setFile(null);
    setPreview(null);
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  return (
    <div className="max-w-5xl mx-auto py-12 px-4 fade-in">
      {/* Header */}
      <div className="flex flex-col items-center mb-10">
        <div className="flex items-center gap-4 mb-2">
          <span className="inline-flex items-center justify-center w-16 h-16 rounded-full bg-gradient-to-br from-purple-500 to-indigo-600 shadow-lg">
            <FiUpload size={36} className="text-white" />
          </span>
          <h1 className="text-4xl md:text-5xl font-extrabold bg-gradient-to-r from-purple-700 via-blue-600 to-indigo-700 bg-clip-text text-transparent drop-shadow-2xl tracking-tight">Upload New Face</h1>
        </div>
        <p className="text-lg md:text-xl font-medium text-blue-700/80 tracking-wide text-center">Add new students or staff members to the database</p>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-10">
        {/* Upload Area */}
        <div className="glass-card p-8 flex flex-col items-center justify-center shadow-2xl">
          <h2 className="text-xl font-bold mb-6 bg-gradient-to-r from-purple-700 via-blue-600 to-indigo-700 bg-clip-text text-transparent text-center">Image Upload</h2>
          <div
            className={`relative border-4 border-dashed rounded-2xl p-10 w-full text-center transition-all duration-300 ${dragActive ? 'border-purple-500 bg-purple-50 dark:bg-purple-900/20' : 'border-gray-300 dark:border-gray-600 hover:border-purple-400 dark:hover:border-purple-400'}`}
            onDragEnter={handleDrag}
            onDragLeave={handleDrag}
            onDragOver={handleDrag}
            onDrop={handleDrop}
          >
            {preview ? (
              <div className="space-y-4">
                <img src={preview} alt="Preview" className="w-40 h-40 object-cover rounded-xl mx-auto shadow-xl border-4 border-white/60" />
                <div className="flex justify-center space-x-2">
                  <button type="button" onClick={() => fileInputRef.current?.click()} className="btn-secondary text-sm">
                    <FiCamera className="mr-1" />Change Image
                  </button>
                  <button type="button" onClick={removeFile} className="btn-secondary text-sm bg-red-100 text-red-600 hover:bg-red-200">
                    <FiX className="mr-1" />Remove
                  </button>
                </div>
              </div>
            ) : (
              <div className="space-y-4">
                <FiUpload className="mx-auto text-5xl text-purple-400" />
                <div>
                  <p className="text-lg font-semibold text-gray-700 dark:text-gray-200">Drop your image here</p>
                  <p className="text-sm text-gray-500 dark:text-gray-400">or click to browse</p>
                </div>
                <button type="button" onClick={() => fileInputRef.current?.click()} className="btn-primary px-6 py-2 rounded-full text-base font-bold">
                  <FiFile className="mr-2" />Choose File
                </button>
              </div>
            )}
            <input ref={fileInputRef} type="file" accept="image/*" onChange={(e) => e.target.files[0] && handleFile(e.target.files[0])} className="hidden" />
          </div>
          {file && (
            <div className="mt-4 p-3 bg-green-50 dark:bg-green-900/20 rounded-lg w-full">
              <div className="flex items-center space-x-2 text-green-700 dark:text-green-300">
                <FiCheckCircle />
                <span className="text-sm font-medium truncate">{file.name}</span>
              </div>
            </div>
          )}
        </div>
        {/* Form */}
        <div className="glass-card p-8 flex flex-col justify-center shadow-2xl">
          <h2 className="text-xl font-bold mb-6 bg-gradient-to-r from-purple-700 via-blue-600 to-indigo-700 bg-clip-text text-transparent text-center">Information</h2>
          <form onSubmit={handleUpload} className="space-y-6">
            <div>
              <label className="block text-sm font-semibold text-gray-700 dark:text-gray-300 mb-2">Name *</label>
              <input type="text" placeholder="Enter full name" required value={name} onChange={(e) => setName(e.target.value)} className="enhanced-input" />
            </div>
            <div>
              <label className="block text-sm font-semibold text-gray-700 dark:text-gray-300 mb-2">Department *</label>
              <input type="text" placeholder="Enter department" required value={department} onChange={(e) => setDepartment(e.target.value)} className="enhanced-input" />
            </div>
            <div>
              <label className="block text-sm font-semibold text-gray-700 dark:text-gray-300 mb-2">Type *</label>
              <div className="grid grid-cols-2 gap-3">
                <button type="button" onClick={() => setType('student')} className={`p-3 rounded-lg border-2 transition-all duration-200 flex items-center justify-center space-x-2 ${type === 'student' ? 'border-blue-500 bg-blue-50 dark:bg-blue-900/20 text-blue-700 dark:text-blue-300' : 'border-gray-300 dark:border-gray-600 hover:border-blue-400'}`}><FiUser /><span>Student</span></button>
                <button type="button" onClick={() => setType('staff')} className={`p-3 rounded-lg border-2 transition-all duration-200 flex items-center justify-center space-x-2 ${type === 'staff' ? 'border-green-500 bg-green-50 dark:bg-green-900/20 text-green-700 dark:text-green-300' : 'border-gray-300 dark:border-gray-600 hover:border-green-400'}`}><FiUsers /><span>Staff</span></button>
              </div>
            </div>
            <div>
              <label className="block text-sm font-semibold text-gray-700 dark:text-gray-300 mb-2">{type === 'student' ? 'Age' : 'Role'} *</label>
              <input type="text" placeholder={type === 'student' ? 'Enter age' : 'Enter role'} required value={ageOrRole} onChange={(e) => setAgeOrRole(e.target.value)} className="enhanced-input" />
            </div>
            <button type="submit" disabled={uploading || !file} className="w-full btn-primary disabled:opacity-50 disabled:cursor-not-allowed text-lg font-bold py-3 rounded-full">
              {uploading ? (<><LoadingSpinner size="sm" text="" /><span className="ml-2">Uploading...</span></>) : (<><FiDatabase className="mr-2" />Upload to Database</>)}
            </button>
          </form>
        </div>
      </div>
      {/* Notification */}
      {notification && (
        <Notification type={notification.type} title={notification.title} message={notification.message} onClose={() => setNotification(null)} />
      )}
    </div>
  );
}
