import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import { FiCpu, FiMic, FiMicOff, FiVolume2, FiVolumeX, FiRefreshCw, FiSettings, FiPower, FiMaximize, FiMinimize } from 'react-icons/fi';
import RobotSpeechDisplay from './RobotSpeechDisplay';
import {
  speakWithAudioFeedback,
  stopSpeaking,
  startListening,
} from '../utils/voice';

const API_URL = 'http://localhost:5000';

export default function RobotController() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [recognizedName, setRecognizedName] = useState('');
  const [collegeName, setCollegeName] = useState('');
  const [robotMessage, setRobotMessage] = useState('🎓 Initializing...');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [volume, setVolume] = useState(0);
  const [isMicOn, setIsMicOn] = useState(true);
  const [isCameraOn, setIsCameraOn] = useState(true);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [showSettings, setShowSettings] = useState(false);

  const greetedPeopleRef = useRef(new Set());
  const interruptedRef = useRef(false);

  useEffect(() => {
    greetedPeopleRef.current.clear();
    fetchCollegeName();

    const interval = setInterval(fetchRecognition, 2000);
    startListening(handleTranscript);

    return () => {
      clearInterval(interval);
      stopSpeaking();
    };
  }, []);

  const logChatToServer = async (sender, receiver, message) => {
    try {
      await axios.post(`${API_URL}/api/log_chat`, {
        sender,
        receiver,
        message,
      });
    } catch (err) {
      console.error('Logging chat failed:', err);
    }
  };

  const handleTranscript = async (text, isFinal) => {
    if (!isFinal || !text.trim()) return;

    if (isSpeaking) {
      stopSpeaking();
      setIsSpeaking(false);
      interruptedRef.current = true;
    }

    const userQuery = text.trim();
    setRobotMessage(`🧠 Processing: ${userQuery}`);
    await logChatToServer('User', 'Bot', userQuery);

    try {
      const res = await axios.post(`${API_URL}/api/robot_ask`, { query: userQuery });
      const response = res.data.response || "Sorry, I didn't catch that.";
      setRobotMessage(response);
      await logChatToServer('Bot', 'User', response);

      setIsSpeaking(true);
      speakWithAudioFeedback(response, setVolume, () => {
        setIsSpeaking(false);
        interruptedRef.current = false;
      }, handleTranscript);
    } catch (err) {
      console.error('Voice fetch failed:', err);
      setRobotMessage("⚠️ Error reaching assistant.");
    }
  };

  const fetchCollegeName = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/college_name`);
      setCollegeName(res.data.name || 'Your College');
    } catch {
      setCollegeName('Unknown College');
    }
  };

  const fetchRecognition = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/recognized_face`);
      const { name } = res.data;

      if (!name || greetedPeopleRef.current.has(name)) return;

      stopSpeaking();

      const isKnown = name.toLowerCase() !== 'unknown';
      const finalName = isKnown ? name : '';
      const greeting = isKnown
        ? `Hi ${finalName}, I'm your assistant at ${collegeName}. How can I help you today?`
        : `Hi, I'm the college bot. How can I help you today?`;

      setRobotMessage(greeting);
      setRecognizedName(finalName);
      setIsSpeaking(true);

      await logChatToServer('Bot', finalName || 'Visitor', greeting);

      speakWithAudioFeedback(greeting, setVolume, () => {
        setIsSpeaking(false);
      }, handleTranscript);

      greetedPeopleRef.current.add(name);
    } catch (err) {
      console.error('Face recognition failed:', err);
    }
  };

  useEffect(() => {
    const drawOverlay = () => {
      const canvas = canvasRef.current;
      const ctx = canvas?.getContext('2d');
      const img = videoRef.current;
      if (!canvas || !ctx || !img) return;

      const width = img.clientWidth;
      const height = img.clientHeight;

      canvas.width = width;
      canvas.height = height;
      ctx.clearRect(0, 0, width, height);
    };

    const drawInterval = setInterval(drawOverlay, 500);
    return () => clearInterval(drawInterval);
  }, [recognizedName]);

  // Camera controls
  const toggleMic = () => setIsMicOn((prev) => !prev);
  const toggleCamera = () => setIsCameraOn((prev) => !prev);
  const toggleFullscreen = () => {
    const container = document.getElementById('robot-video-container');
    if (!isFullscreen) {
      if (container.requestFullscreen) container.requestFullscreen();
      setIsFullscreen(true);
    } else {
      if (document.fullscreenElement) {
        document.exitFullscreen();
        setIsFullscreen(false);
      }
    }
  };
  const openSettings = () => setShowSettings(true);
  const closeSettings = () => setShowSettings(false);

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 via-white to-blue-100 py-10 px-2 fade-in">
      <div className="max-w-6xl mx-auto grid grid-cols-1 lg:grid-cols-2 gap-10 items-start">
        {/* Camera Feed Section */}
        <div
          id="robot-video-container"
          className={`relative rounded-3xl overflow-hidden shadow-2xl glass-card p-0 bg-white/70 flex flex-col items-center ${isFullscreen ? 'fixed inset-0 z-50 w-screen h-screen rounded-none bg-black' : ''}`}
        >
          <div className="flex items-center justify-between px-6 py-4 border-b border-gray-200 dark:border-gray-700 bg-white/80 w-full">
            <div className="flex items-center gap-3">
              <FiCpu className="text-indigo-600" />
              <span className="font-bold text-lg text-gray-800">Robot Camera</span>
              <span className="ml-3 flex items-center gap-1 text-green-500 font-semibold animate-pulse">
                <span className="w-2 h-2 rounded-full bg-green-500 animate-ping"></span> ACTIVE
              </span>
            </div>
            <div className="flex items-center gap-2">
              <button onClick={toggleCamera} className="p-2 rounded-lg bg-indigo-100 hover:bg-indigo-200 text-indigo-700 transition" title={isCameraOn ? 'Turn Off Camera' : 'Turn On Camera'}>
                <FiPower />
              </button>
              <button onClick={toggleMic} className="p-2 rounded-lg bg-indigo-100 hover:bg-indigo-200 text-indigo-700 transition" title={isMicOn ? 'Mute Mic' : 'Unmute Mic'}>
                {isMicOn ? <FiMic /> : <FiMicOff />}
              </button>
              <button onClick={toggleFullscreen} className="p-2 rounded-lg bg-indigo-100 hover:bg-indigo-200 text-indigo-700 transition" title={isFullscreen ? 'Exit Fullscreen' : 'Fullscreen'}>
                {isFullscreen ? <FiMinimize /> : <FiMaximize />}
              </button>
              <button onClick={openSettings} className="p-2 rounded-lg bg-indigo-100 hover:bg-indigo-200 text-indigo-700 transition" title="Camera Settings">
                <FiSettings />
              </button>
            </div>
          </div>
          <div className={`relative bg-black flex items-center justify-center ${isFullscreen ? 'w-screen h-screen' : 'min-h-[400px] w-full'}`}>
            {isCameraOn ? (
              <img
                ref={videoRef}
                src={`${API_URL}/video_feed`}
                alt="Robot Camera"
                className={`object-contain ${isFullscreen ? 'w-screen h-screen' : 'w-full h-[400px]'} ${isFullscreen ? 'rounded-none' : 'rounded-b-3xl'}`}
                style={isFullscreen ? { borderRadius: 0 } : {}}
              />
            ) : (
              <div className="w-full h-[400px] flex items-center justify-center bg-gray-900 rounded-b-3xl">
                <div className="text-center text-gray-400">
                  <FiPower className="mx-auto text-6xl mb-4" />
                  <p className="text-lg">Camera is Off</p>
                  <p className="text-sm">Click power to turn on</p>
                </div>
              </div>
            )}
            <canvas
              ref={canvasRef}
              className="absolute top-0 left-0 w-full h-full pointer-events-none"
            />
          </div>
          {/* Camera Settings Modal */}
          {showSettings && (
            <div className="fixed inset-0 bg-black/40 z-50 flex items-center justify-center">
              <div className="bg-white rounded-2xl shadow-2xl p-8 w-full max-w-md relative">
                <button onClick={closeSettings} className="absolute top-3 right-3 text-gray-400 hover:text-red-500 text-xl font-bold">&times;</button>
                <h2 className="text-xl font-bold mb-4 flex items-center gap-2"><FiSettings /> Camera Settings</h2>
                <div className="text-gray-600">(Settings options coming soon...)</div>
              </div>
            </div>
          )}
        </div>
        {/* Robot Avatar/Response Section */}
        <div className="flex flex-col gap-8 items-center justify-center w-full">
          <div className="w-full flex flex-col items-center justify-center bg-gradient-to-br from-indigo-200 via-white to-blue-100 rounded-3xl shadow-xl p-8">
            <RobotSpeechDisplay message={robotMessage} isSpeaking={isSpeaking} volume={volume} />
          </div>
          {/* Floating Action Bar for Voice/Chat Controls */}
          <div className="flex gap-4 items-center justify-center w-full">
            <button
              onClick={toggleMic}
              className={`p-4 rounded-full shadow-lg text-2xl ${isMicOn ? 'bg-green-500 text-white' : 'bg-gray-300 text-gray-500'} hover:scale-105 transition`}
              title={isMicOn ? 'Mute Mic' : 'Unmute Mic'}
            >
              {isMicOn ? <FiMic /> : <FiMicOff />}
            </button>
            <button
              onClick={toggleCamera}
              className={`p-4 rounded-full shadow-lg text-2xl ${isCameraOn ? 'bg-blue-500 text-white' : 'bg-gray-300 text-gray-500'} hover:scale-105 transition`}
              title={isCameraOn ? 'Turn Off Camera' : 'Turn On Camera'}
            >
              <FiPower />
            </button>
            <button
              onClick={openSettings}
              className="p-4 rounded-full shadow-lg text-2xl bg-indigo-500 text-white hover:scale-105 transition"
              title="Settings"
            >
              <FiSettings />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
