import React, { useState, useRef } from 'react';
import axios from 'axios';
import { 
  FiUpload, FiUser, FiUsers, FiFile, FiX, FiCheckCircle, FiCamera, FiDatabase, FiArrowRightCircle 
} from 'react-icons/fi';
import LoadingSpinner from '../components/LoadingSpinner';
import Notification from '../components/Notification';

export default function UploadImage() {
  const [name, setName] = useState('');
  const [ageOrRole, setAgeOrRole] = useState('');
  const [department, setDepartment] = useState('');
  const [type, setType] = useState('student');
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [dragActive, setDragActive] = useState(false);
  const [notification, setNotification] = useState(null);
  const fileInputRef = useRef(null);
  const [step, setStep] = useState(1);
  const [success, setSuccess] = useState(false);

  

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFile(e.dataTransfer.files[0]);
    }
  };

  const handleFile = (selectedFile) => {
    if (selectedFile.type.startsWith('image/')) {
      setFile(selectedFile);
      const reader = new FileReader();
      reader.onload = (e) => setPreview(e.target.result);
      reader.readAsDataURL(selectedFile);
    } else {
      showNotification('error', 'Invalid file type', 'Please select an image file.');
    }
  };

  const showNotification = (type, title, message) => {
    setNotification({ type, title, message });
    setTimeout(() => setNotification(null), 5000);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) {
      showNotification('error', 'No file selected', 'Please select an image to upload.');
      return;
    }
    if (!name.trim() || !department.trim() || !ageOrRole.trim()) {
      showNotification('error', 'Missing information', 'Please fill in all required fields.');
      return;
    }
    setUploading(true);
    const formData = new FormData();
    formData.append('image', file);
    formData.append('name', name);
    formData.append('department', department);
    formData.append('type', type);
    formData.append('age_or_role', ageOrRole);
    try {
      await axios.post('http://localhost:5000/api/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setSuccess(true);
      showNotification('success', 'Upload successful!', 'The image has been uploaded successfully.');
      setTimeout(() => {
        setSuccess(false);
        resetForm();
        setStep(1);
      }, 2000);
    } catch (error) {
      console.error('Upload error:', error);
      showNotification('error', 'Upload failed', 'Failed to upload image. Please try again.');
    } finally {
      setUploading(false);
    }
  };

  const resetForm = () => {
    setName('');
    setAgeOrRole('');
    setDepartment('');
    setFile(null);
    setPreview(null);
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  const removeFile = () => {
    setFile(null);
    setPreview(null);
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  const Stepper = () => (
    <div className="flex md:flex-col items-center md:items-start gap-4 mb-8">
      <div className={`flex items-center gap-2 ${step === 1 ? 'text-blue-600' : 'text-gray-400'}`}> <span className="w-8 h-8 flex items-center justify-center rounded-full border-2 border-blue-400 bg-white font-bold">1</span> <span className="hidden md:inline">Select Image</span> </div>
      <div className="w-8 h-8 flex items-center justify-center text-gray-300"> <FiArrowRightCircle size={24} /> </div>
      <div className={`flex items-center gap-2 ${step === 2 ? 'text-blue-600' : 'text-gray-400'}`}> <span className="w-8 h-8 flex items-center justify-center rounded-full border-2 border-blue-400 bg-white font-bold">2</span> <span className="hidden md:inline">Fill Info</span> </div>
      <div className="w-8 h-8 flex items-center justify-center text-gray-300"> <FiArrowRightCircle size={24} /> </div>
      <div className={`flex items-center gap-2 ${step === 3 ? 'text-blue-600' : 'text-gray-400'}`}> <span className="w-8 h-8 flex items-center justify-center rounded-full border-2 border-blue-400 bg-white font-bold">3</span> <span className="hidden md:inline">Upload</span> </div>
    </div>
  );

  return (
    <div className="relative min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-100 via-white to-indigo-100 py-16 px-2 fade-in">
      {/* Floating Glass Card */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute top-20 left-1/4 w-1/2 h-32 bg-gradient-to-r from-purple-400/30 to-blue-400/20 rounded-full blur-2xl animate-pulse"></div>
        <div className="absolute bottom-10 right-1/4 w-1/3 h-24 bg-gradient-to-l from-indigo-400/30 to-pink-400/20 rounded-full blur-2xl animate-pulse"></div>
      </div>
      <div className="relative z-10 w-full max-w-5xl">
        <div className="glass-card p-10 md:p-16 rounded-3xl shadow-2xl flex flex-col md:flex-row gap-12 items-center">
          {/* Stepper */}
          <div className="hidden md:block">
            <Stepper />
          </div>
          {/* Main Content */}
          <div className="flex-1 w-full">
            {/* Success Animation */}
            {success ? (
              <div className="flex flex-col items-center justify-center h-full min-h-[400px]">
                <FiCheckCircle className="text-green-500 animate-bounce mb-4" size={64} />
                <h2 className="text-3xl font-bold text-green-700 mb-2">Upload Successful!</h2>
                <p className="text-lg text-gray-700">The image has been uploaded to the database.</p>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-10">
                {/* Upload Area */}
                <div className="flex flex-col items-center justify-center">
                  <div className="mb-6">
                    <span className="inline-flex items-center justify-center w-20 h-20 rounded-full bg-gradient-to-br from-purple-500 to-indigo-600 shadow-xl animate-pulse">
                      <FiUpload size={48} className="text-white" />
                    </span>
                  </div>
                  <div
                    className={`relative border-4 border-dashed rounded-2xl p-10 w-full text-center transition-all duration-300 ${dragActive ? 'border-purple-500 bg-purple-50 dark:bg-purple-900/20' : 'border-gray-300 dark:border-gray-600 hover:border-purple-400 dark:hover:border-purple-400'}`}
                    onDragEnter={handleDrag}
                    onDragLeave={handleDrag}
                    onDragOver={handleDrag}
                    onDrop={handleDrop}
                  >
                    {preview ? (
                      <div className="space-y-4">
                        <img src={preview} alt="Preview" className="w-44 h-44 object-cover rounded-2xl mx-auto shadow-2xl border-4 border-white/60" />
                        <div className="flex justify-center space-x-2">
                          <button type="button" onClick={() => { fileInputRef.current?.click(); setStep(1); }} className="btn-secondary text-sm">
                            <FiCamera className="mr-1" />Change Image
                          </button>
                          <button type="button" onClick={removeFile} className="btn-secondary text-sm bg-red-100 text-red-600 hover:bg-red-200">
                            <FiX className="mr-1" />Remove
                          </button>
                        </div>
                      </div>
                    ) : (
                      <div className="space-y-4">
                        <FiUpload className="mx-auto text-6xl text-purple-400 animate-bounce" />
                        <div>
                          <p className="text-lg font-semibold text-gray-700 dark:text-gray-200">Drop your image here</p>
                          <p className="text-sm text-gray-500 dark:text-gray-400">or click to browse</p>
                        </div>
                        <button type="button" onClick={() => { fileInputRef.current?.click(); setStep(1); }} className="btn-primary px-8 py-3 rounded-full text-lg font-bold">
                          <FiFile className="mr-2" />Choose File
                        </button>
                      </div>
                    )}
                    <input ref={fileInputRef} type="file" accept="image/*" onChange={(e) => { if (e.target.files[0]) { handleFile(e.target.files[0]); setStep(2); }}} className="hidden" />
                  </div>
                  {file && (
                    <div className="mt-4 p-3 bg-green-50 dark:bg-green-900/20 rounded-lg w-full">
                      <div className="flex items-center space-x-2 text-green-700 dark:text-green-300">
                        <FiCheckCircle />
                        <span className="text-sm font-medium truncate">{file.name}</span>
                      </div>
                    </div>
                  )}
                </div>
                {/* Form */}
                <div className="flex flex-col justify-center">
                  <h2 className="text-2xl font-bold mb-6 bg-gradient-to-r from-purple-700 via-blue-600 to-indigo-700 bg-clip-text text-transparent text-center">Information</h2>
                  <form onSubmit={(e) => { setStep(3); handleUpload(e); }} className="space-y-6">
                    <div>
                      <label className="block text-base font-semibold text-gray-700 dark:text-gray-300 mb-2">Name *</label>
                      <input type="text" placeholder="Enter full name" required value={name} onChange={(e) => setName(e.target.value)} className="enhanced-input text-lg" />
                    </div>
                    <div>
                      <label className="block text-base font-semibold text-gray-700 dark:text-gray-300 mb-2">Department *</label>
                      <input type="text" placeholder="Enter department" required value={department} onChange={(e) => setDepartment(e.target.value)} className="enhanced-input text-lg" />
                    </div>
                    <div>
                      <label className="block text-base font-semibold text-gray-700 dark:text-gray-300 mb-2">Type *</label>
                      <div className="grid grid-cols-2 gap-3">
                        <button type="button" onClick={() => setType('student')} className={`p-4 rounded-lg border-2 transition-all duration-200 flex items-center justify-center space-x-2 text-lg ${type === 'student' ? 'border-blue-500 bg-blue-50 dark:bg-blue-900/20 text-blue-700 dark:text-blue-300' : 'border-gray-300 dark:border-gray-600 hover:border-blue-400'}`}><FiUser /><span>Student</span></button>
                        <button type="button" onClick={() => setType('staff')} className={`p-4 rounded-lg border-2 transition-all duration-200 flex items-center justify-center space-x-2 text-lg ${type === 'staff' ? 'border-green-500 bg-green-50 dark:bg-green-900/20 text-green-700 dark:text-green-300' : 'border-gray-300 dark:border-gray-600 hover:border-green-400'}`}><FiUsers /><span>Staff</span></button>
                      </div>
                    </div>
                    <div>
                      <label className="block text-base font-semibold text-gray-700 dark:text-gray-300 mb-2">{type === 'student' ? 'Age' : 'Role'} *</label>
                      <input type="text" placeholder={type === 'student' ? 'Enter age' : 'Enter role'} required value={ageOrRole} onChange={(e) => setAgeOrRole(e.target.value)} className="enhanced-input text-lg" />
                    </div>
                    <button type="submit" disabled={uploading || !file} className="w-full btn-primary disabled:opacity-50 disabled:cursor-not-allowed text-xl font-bold py-4 rounded-full mt-4">
                      {uploading ? (<><LoadingSpinner size="sm" text="" /><span className="ml-2">Uploading...</span></>) : (<><FiDatabase className="mr-2" />Upload to Database</>)}
                    </button>
                  </form>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
      {/* Notification */}
      {notification && (
        <Notification type={notification.type} title={notification.title} message={notification.message} onClose={() => setNotification(null)} />
      )}
    </div>
  );
}
