import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import { FiMic, FiCpu } from 'react-icons/fi';
import { speakWithAudioFeedback, stopSpeaking } from '../utils/voice';
import { startListening, stopListening } from '../utils/speechRecognition';
import RobotSpeechDisplay from './RobotSpeechDisplay';

const API_URL = 'http://localhost:5000';

export default function RobotController() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [recognizedName, setRecognizedName] = useState('');
  const [collegeName, setCollegeName] = useState('Your College');
  const [robotMessage, setRobotMessage] = useState('üéì Initializing...');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [volume, setVolume] = useState(0);
  const [currentFace, setCurrentFace] = useState(null);
  const [lastSeen, setLastSeen] = useState(0);

  const micActive = useRef(false);

  useEffect(() => {
    fetchCollegeName();
    startSpeechListener();
    const interval = setInterval(checkFace, 1500);
    return () => {
      clearInterval(interval);
      stopListening();
    };
  }, []);

  const fetchCollegeName = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/college_name`);
      setCollegeName(res.data.name || 'Your College');
    } catch {
      setCollegeName('Unknown College');
    }
  };

  const checkFace = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/recognized_face`);
      const { name } = res.data;

      if (name && name !== 'Unknown' && name !== currentFace) {
        greet(name);
        setCurrentFace(name);
        setLastSeen(Date.now());
      } else if (name === 'Unknown' && currentFace !== 'Unknown') {
        setCurrentFace('Unknown');
        setRobotMessage('ü§ñ Face not recognized...');
      }
    } catch (e) {
      console.error('Face recognition error:', e);
    }
  };

  const greet = (name) => {
    const greeting = `Hi ${name}, I‚Äôm CollegeBot from ${collegeName}. How can I assist you today?`;
    speakWithInterrupt(greeting);
    setRecognizedName(name);
    setRobotMessage(greeting);
  };

  const speakWithInterrupt = (text) => {
    stopSpeaking(); // Cancel any ongoing speech
    setIsSpeaking(true);
    speakWithAudioFeedback(text, setVolume, () => {
      setIsSpeaking(false);
    });
  };

  const handleTranscript = async (text) => {
    stopSpeaking(); // Interrupt any response
    setRobotMessage(`üé§ You said: ${text}`);
    try {
      const res = await axios.post(`${API_URL}/api/robot_ask`, {
        query: text,
      });
      const reply = res.data.response;
      speakWithInterrupt(reply);
      setRobotMessage(reply);
    } catch {
      speakWithInterrupt("Sorry, I couldn't fetch a response.");
    }
  };

  const startSpeechListener = () => {
    if (micActive.current) return;
    micActive.current = true;
    startListening(handleTranscript);
  };

  useEffect(() => {
    const drawOverlay = () => {
      const canvas = canvasRef.current;
      const ctx = canvas?.getContext('2d');
      const img = videoRef.current;
      if (!canvas || !ctx || !img) return;
      canvas.width = img.width;
      canvas.height = img.height;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.fillStyle = currentFace && currentFace !== 'Unknown' ? '#28a745' : '#d63384';
      ctx.font = 'bold 1.5rem sans-serif';
      ctx.textAlign = 'center';
      ctx.textBaseline = 'top';
      ctx.fillText(
        currentFace && currentFace !== 'Unknown' ? `üéâ Hello, ${currentFace}` : 'ü§ñ Face Not Recognized',
        canvas.width / 2,
        20
      );
    };
    drawOverlay();
    const obs = new ResizeObserver(drawOverlay);
    if (videoRef.current) obs.observe(videoRef.current);
    return () => obs.disconnect();
  }, [currentFace]);

  return (
    <div className="max-w-6xl mx-auto p-4 space-y-6">
      <div className="glass-card p-6 flex items-center space-x-3">
        <FiCpu size={24} className="text-white p-3 rounded-xl bg-gradient-to-r from-purple-600 to-indigo-600" />
        <div>
          <h1 className="text-3xl font-bold gradient-text">Robot Controller</h1>
          <p className="text-gray-600">Live voice + vision Gemini-style assistant</p>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="glass-card overflow-hidden relative h-[450px]">
          <img
            ref={videoRef}
            src={`${API_URL}/video_feed`}
            alt="Robot Camera"
            className="absolute inset-0 w-full h-full object-cover"
          />
          <canvas ref={canvasRef} className="absolute inset-0 w-full h-full pointer-events-none" />
        </div>

        <div className="glass-card flex flex-col justify-between p-6 h-[450px]">
          <h2 className="text-xl font-bold mb-4 flex items-center">
            <FiMic className="mr-2" /> Robot Assistant
          </h2>
          <RobotSpeechDisplay
            message={robotMessage}
            isSpeaking={isSpeaking}
            volume={volume}
          />
          <div className="text-sm text-gray-600 dark:text-gray-300 mt-4 flex justify-between">
            <span>College: <strong>{collegeName}</strong></span>
            <span>Mic: <strong className="text-green-600">üéôÔ∏è On</strong></span>
          </div>
        </div>
      </div>
    </div>
  );
}
