import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  FiUsers, 
  FiUserCheck, 
  FiEye, 
  FiUpload, 
  FiGrid, 
  FiActivity, 
  FiDatabase,
  FiTrendingUp,
  FiShield,
  FiZap,
  FiMonitor,
  FiCamera,
  FiClock,
  FiCheckCircle
} from 'react-icons/fi';
import StatsCard from '../components/StatsCard';
import LoadingSpinner from '../components/LoadingSpinner';
import axios from 'axios';

function animateValue(start, end, duration, setter) {
  if (start === end) {
    setter(end);
    return;
  }
  const range = end - start;
  let current = start;
  const increment = range / (duration / 16);
  let startTime = null;

  function step(timestamp) {
    if (!startTime) startTime = timestamp;
    const progress = timestamp - startTime;
    current = start + increment * (progress / 16);
    if ((increment > 0 && current >= end) || (increment < 0 && current <= end)) {
      setter(end);
      return;
    }
    setter(Math.round(current));
    requestAnimationFrame(step);
  }
  requestAnimationFrame(step);
}

export default function Home() {
  const [stats, setStats] = useState({
    students: 0,
    staff: 0,
    recognitions: 0,
    accuracy: 0
  });
  const [loading, setLoading] = useState(true);
  const [displayStats, setDisplayStats] = useState(stats);
  const [robotQuery, setRobotQuery] = useState("");
  const [robotResponse, setRobotResponse] = useState("");

  useEffect(() => {
    setLoading(true);
    axios.get('http://localhost:5000/api/statistics')
      .then(res => {
        const apiStats = {
          students: res.data.total_students,
          staff: res.data.total_staff,
          recognitions: res.data.total_recognitions,
          accuracy: res.data.accuracy_rate
        };
        setStats(apiStats);
        setLoading(false);
        // Animate numbers
        animateValue(displayStats.students, apiStats.students, 800, val => setDisplayStats(s => ({ ...s, students: val })));
        animateValue(displayStats.staff, apiStats.staff, 800, val => setDisplayStats(s => ({ ...s, staff: val })));
        animateValue(displayStats.recognitions, apiStats.recognitions, 800, val => setDisplayStats(s => ({ ...s, recognitions: val })));
        animateValue(displayStats.accuracy, apiStats.accuracy, 800, val => setDisplayStats(s => ({ ...s, accuracy: val })));
      })
      .catch(() => setLoading(false));
    // eslint-disable-next-line
  }, []);

  const features = [
    {
      icon: FiCamera,
      title: "Real-time Recognition",
      description: "Advanced facial recognition with instant identification and tracking capabilities.",
      color: "from-blue-500 to-cyan-500"
    },
    {
      icon: FiShield,
      title: "Secure & Private",
      description: "Enterprise-grade security with encrypted data storage and privacy protection.",
      color: "from-green-500 to-emerald-500"
    },
    {
      icon: FiZap,
      title: "Lightning Fast",
      description: "Optimized algorithms for quick recognition and minimal processing time.",
      color: "from-purple-500 to-pink-500"
    },
    {
      icon: FiMonitor,
      title: "Multi-Camera Support",
      description: "Support for multiple camera feeds with centralized monitoring dashboard.",
      color: "from-orange-500 to-red-500"
    }
  ];

  const quickActions = [
    { icon: FiUsers, title: "View Students", path: "/students", color: "bg-blue-500" },
    { icon: FiUserCheck, title: "View Staff", path: "/staff", color: "bg-green-500" },
    { icon: FiUpload, title: "Upload Image", path: "/upload", color: "bg-purple-500" },
    { icon: FiGrid, title: "Live Feed", path: "/live", color: "bg-orange-500" },
    { icon: FiEye, title: "Unknown Faces", path: "/unknowns", color: "bg-red-500" },
    { icon: FiActivity, title: "View Logs", path: "/logs", color: "bg-indigo-500" },
    { icon: FiDatabase, title: "Chat Logs", path: "/chatlogs", color: "bg-pink-500" },
    { icon: FiDatabase, title: "College Info", path: "/collegeinfo", color: "bg-teal-500" }
  ];

  const statsData = [
    { 
      icon: FiUsers, 
      title: "Students", 
      value: loading ? 0 : displayStats.students, 
      color: "from-blue-500 to-cyan-500",
      delay: 0.1
    },
    { 
      icon: FiUserCheck, 
      title: "Staff Members", 
      value: loading ? 0 : displayStats.staff, 
      color: "from-green-500 to-emerald-500",
      delay: 0.2
    },
    { 
      icon: FiTrendingUp, 
      title: "Recognitions", 
      value: loading ? 0 : displayStats.recognitions, 
      color: "from-purple-500 to-pink-500",
      delay: 0.3
    },
    { 
      icon: FiCheckCircle, 
      title: "Accuracy Rate", 
      value: loading ? "0%" : `${displayStats.accuracy}%`, 
      color: "from-orange-500 to-red-500",
      delay: 0.4
    }
  ];

  const startVoiceRecognition = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Your browser does not support Speech Recognition.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.interimResults = false;
    recognition.start();

    recognition.onresult = function (event) {
      const query = event.results[0][0].transcript;
      setRobotQuery(query);

      fetch("http://localhost:5000/robot_ask", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query: query })
      })
        .then(res => res.json())
        .then(data => {
          const reply = data.response;
          setRobotResponse(reply);

          const synth = window.speechSynthesis;
          const utter = new window.SpeechSynthesisUtterance(reply);
          synth.speak(utter);
        })
        .catch(err => {
          setRobotResponse("Sorry, something went wrong.");
          console.error("Voice Assistant Error:", err);
        });
    };

    recognition.onerror = function (event) {
      console.error("Speech recognition error", event);
    };
  };

  return (
    <div className="min-h-screen fade-in">
      {/* Hero Section */}
      <section className="relative overflow-hidden py-20 px-6">
        <div className="absolute inset-0 bg-gradient-to-br from-blue-600/10 via-purple-600/10 to-indigo-600/10"></div>
        <div className="relative max-w-7xl mx-auto">
          <div className="text-center slide-in-up">
            <h1 className="text-5xl md:text-7xl font-bold mb-6 gradient-text text-shadow-lg">
              Face Recognition
            </h1>
            <p className="text-xl md:text-2xl text-gray-600 dark:text-gray-300 mb-8 max-w-3xl mx-auto">
              Advanced facial recognition system for secure identification and monitoring
            </p>
            <div className="flex flex-wrap justify-center gap-4">
              <Link to="/live" className="btn-primary text-lg px-8 py-4">
                <FiCamera className="mr-2" />
                Start Live Feed
              </Link>
              <Link to="/upload" className="btn-secondary text-lg px-8 py-4">
                <FiUpload className="mr-2" />
                Upload Images
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="py-16 px-6">
        <div className="max-w-7xl mx-auto">
          <h2 className="text-3xl font-bold text-center mb-12 gradient-text">
            System Statistics
          </h2>
          {loading ? (
            <div className="flex justify-center py-12">
              <LoadingSpinner size="lg" text="Loading statistics..." />
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              {statsData.map((stat, index) => (
                <StatsCard
                  key={stat.title}
                  icon={stat.icon}
                  title={stat.title}
                  value={stat.value}
                  color={stat.color}
                  delay={stat.delay}
                />
              ))}
            </div>
          )}
        </div>
      </section>

      {/* Features Section */}
      <section className="py-16 px-6 bg-gradient-to-br from-gray-50/50 to-blue-50/50 dark:from-gray-800/50 dark:to-gray-900/50">
        <div className="max-w-7xl mx-auto">
          <h2 className="text-3xl font-bold text-center mb-12 gradient-text">
            Key Features
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {features.map((feature, index) => (
              <div 
                key={feature.title}
                className="card p-6 text-center slide-in-up"
                style={{ animationDelay: `${index * 0.2}s` }}
              >
                <div className={`inline-flex items-center justify-center w-16 h-16 rounded-full bg-gradient-to-r ${feature.color} mb-4 text-white text-2xl`}>
                  <feature.icon />
                </div>
                <h3 className="text-xl font-semibold text-gray-800 dark:text-white mb-3">
                  {feature.title}
                </h3>
                <p className="text-gray-600 dark:text-gray-300">
                  {feature.description}
                </p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Quick Actions Section */}
      <section className="py-16 px-6">
        <div className="max-w-7xl mx-auto">
          <h2 className="text-3xl font-bold text-center mb-12 gradient-text">
            Quick Actions
          </h2>
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-6 gap-4">
            {quickActions.map((action, index) => (
              <Link
                key={action.title}
                to={action.path}
                className={`${action.color} text-white p-6 rounded-2xl text-center transition-all duration-300 transform hover:scale-105 hover:shadow-xl slide-in-up`}
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <action.icon className="text-3xl mx-auto mb-3" />
                <div className="font-semibold text-sm">
                  {action.title}
                </div>
              </Link>
            ))}
          </div>
        </div>
      </section>

      {/* System Status Section */}
      <section className="py-16 px-6">
        <div className="max-w-4xl mx-auto">
          <div className="glass-card p-8 text-center">
            <h2 className="text-3xl font-bold mb-8 gradient-text">
              System Status
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="flex items-center justify-center space-x-3">
                <div className="status-online"></div>
                <span className="text-green-600 dark:text-green-400 font-semibold">Database Connected</span>
              </div>
              <div className="flex items-center justify-center space-x-3">
                <div className="status-online"></div>
                <span className="text-green-600 dark:text-green-400 font-semibold">Recognition Active</span>
              </div>
              <div className="flex items-center justify-center space-x-3">
                <div className="status-online"></div>
                <span className="text-green-600 dark:text-green-400 font-semibold">All Systems Online</span>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Voice Assistant Section */}
      <section className="py-16 px-6">
        <div className="max-w-3xl mx-auto text-center">
          <h2 className="text-3xl font-bold mb-4 gradient-text">🎤 Ask the Robot</h2>
          <p className="mb-6 text-gray-600 dark:text-gray-300">
            Press the mic and ask about the college, departments, faculty, or more.
          </p>
          <button
            style={{
              background: "linear-gradient(to right, #3b82f6, #06b6d4)",
              color: "white",
              border: "none",
              borderRadius: "9999px",
              padding: "12px 24px",
              fontSize: "16px",
              cursor: "pointer"
            }}
            onClick={startVoiceRecognition}
          >
            🎙️ Speak Now
          </button>

          {(robotQuery || robotResponse) && (
            <div className="mt-6 text-left bg-gray-100 dark:bg-gray-800 rounded-xl p-6 shadow-lg">
              {robotQuery && (
                <p className="text-gray-800 dark:text-white"><strong>You:</strong> {robotQuery}</p>
              )}
              {robotResponse && (
                <p className="text-blue-600 dark:text-blue-300 mt-2"><strong>Robot:</strong> {robotResponse}</p>
              )}
            </div>
          )}
        </div>
      </section>

      {/* Footer */}
      <footer className="py-8 px-6 bg-gradient-to-r from-gray-800 to-gray-900 text-white">
        <div className="max-w-7xl mx-auto text-center">
          <p className="text-gray-300">
            © 2024 Face Recognition System. Powered by advanced AI technology.
          </p>
        </div>
      </footer>
    </div>
  );
}
