import {
  speakWithAudioFeedback,
  stopSpeaking,
  startListening,
} from '../utils/voice';
import API from '../utils/api';
const API_URL = API.defaults.baseURL.replace('/api', '');

export default function RobotController() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [recognizedName, setRecognizedName] = useState('');
  const [collegeName, setCollegeName] = useState('');
  const [robotMessage, setRobotMessage] = useState('🤖 Initializing...');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [volume, setVolume] = useState(0);
  const [isMicOn, setIsMicOn] = useState(true);
  const [isCameraOn, setIsCameraOn] = useState(true);

  const greetedPeopleRef = useRef(new Set());
  const interruptedRef = useRef(false);

  useEffect(() => {
    greetedPeopleRef.current.clear();
    fetchCollegeName();
    const interval = setInterval(fetchRecognition, 2000);
    startListening(handleTranscript);
    return () => {
      clearInterval(interval);
      stopSpeaking();
    };
  }, []);

  const logChatToServer = async (sender, receiver, message) => {
    try {
      await API.post('/log_chat', {
        sender,
        receiver,
        message,
      });
    } catch (err) {
      console.error('Logging chat failed:', err);
    }
  };

  const handleTranscript = async (text, isFinal) => {
    if (!isFinal || !text.trim()) return;
    if (isSpeaking) {
      stopSpeaking();
      setIsSpeaking(false);
      interruptedRef.current = true;
    }
    const userQuery = text.trim();
    setRobotMessage(`🧠 Processing: ${userQuery}`);
    await logChatToServer('User', 'Bot', userQuery);
    try {
      const res = await API.post('/robot_ask', { query: userQuery });
      const response = res.data.response || "Sorry, I didn't catch that.";
      setRobotMessage(response);
      await logChatToServer('Bot', 'User', response);
      setIsSpeaking(true);
      speakWithAudioFeedback(response, setVolume, () => {
        setIsSpeaking(false);
        interruptedRef.current = false;
      }, handleTranscript);
    } catch (err) {
      console.error('Voice fetch failed:', err);
      setRobotMessage("⚠️ Error reaching assistant.");
    }
  };

  const fetchCollegeName = async () => {
    try {
      const res = await API.get('/college_name');
      setCollegeName(res.data.name || 'Your College');
    } catch {
      setCollegeName('Unknown College');
    }
  };

  const fetchRecognition = async () => {
    try {
      const res = await API.get('/recognized_face');
      const { name } = res.data;
      if (!name || greetedPeopleRef.current.has(name)) return;
      stopSpeaking();
      const isKnown = name.toLowerCase() !== 'unknown';
      const finalName = isKnown ? name : '';
      const greeting = isKnown
        ? `Hi ${finalName}, I'm your assistant at ${collegeName}. How can I help you today?`
        : `Hi, I'm the college bot. How can I help you today?`;
      setRobotMessage(greeting);
      setRecognizedName(finalName);
      setIsSpeaking(true);
      await logChatToServer('Bot', finalName || 'Visitor', greeting);
      speakWithAudioFeedback(greeting, setVolume, () => {
        setIsSpeaking(false);
      }, handleTranscript);
      greetedPeopleRef.current.add(name);
    } catch (err) {
      console.error('Face recognition failed:', err);
    }
  };

  useEffect(() => {
    const drawOverlay = () => {
      const canvas = canvasRef.current;
      const ctx = canvas?.getContext('2d');
      const img = videoRef.current;
      if (!canvas || !ctx || !img) return;
      const width = img.clientWidth;
      const height = img.clientHeight;
      canvas.width = width;
      canvas.height = height;
      ctx.clearRect(0, 0, width, height);
    };
    const drawInterval = setInterval(drawOverlay, 500);
    return () => clearInterval(drawInterval);
  }, [recognizedName]);

  // Camera controls
  const toggleMic = () => setIsMicOn((prev) => !prev);
  const toggleCamera = () => setIsCameraOn((prev) => !prev);

  return (
    <div className="relative min-h-screen w-full overflow-x-hidden bg-gradient-to-br from-[#0f2027] via-[#2c5364] to-[#24243e] animate-bg-pan">
      {/* Animated background particles */}
      <div className="absolute inset-0 z-0 pointer-events-none">
        <svg className="w-full h-full" style={{ opacity: 0.18 }}>
          <defs>
            <radialGradient id="g1" cx="50%" cy="50%" r="80%">
              <stop offset="0%" stopColor="#00ffe7" />
              <stop offset="100%" stopColor="#2c5364" />
            </radialGradient>
            <radialGradient id="g2" cx="80%" cy="20%" r="60%">
              <stop offset="0%" stopColor="#ff00cc" />
              <stop offset="100%" stopColor="#24243e" />
            </radialGradient>
          </defs>
          <circle cx="30%" cy="20%" r="320" fill="url(#g1)" />
          <circle cx="80%" cy="80%" r="220" fill="url(#g2)" />
        </svg>
      </div>
      <div className="relative z-10 flex flex-col lg:flex-row items-center justify-center min-h-screen gap-10 px-2 py-8">
        {/* Left: Robot Avatar & Camera Feed */}
        <div className="flex flex-col gap-8 items-center w-full lg:w-1/2">
          {/* Animated Robot Avatar */}
          <div className="flex flex-col items-center">
            <div className="bg-white/10 rounded-full p-4 shadow-2xl border-4 border-cyan-400/40 animate-pulse-glow">
              {/* SVG Animated Robot */}
              <svg width="110" height="110" viewBox="0 0 110 110" fill="none" xmlns="http://www.w3.org/2000/svg" className="animate-bounce-slow">
                <ellipse cx="55" cy="55" rx="50" ry="50" fill="#181f2e" />
                <ellipse cx="55" cy="55" rx="44" ry="44" fill="#232b47" />
                <ellipse cx="55" cy="55" rx="36" ry="36" fill="#00ffe7" fillOpacity="0.12" />
                <ellipse cx="55" cy="55" rx="30" ry="30" fill="#fff" fillOpacity="0.08" />
                <ellipse cx="55" cy="55" rx="24" ry="24" fill="#00ffe7" fillOpacity="0.10" />
                <ellipse cx="55" cy="55" rx="18" ry="18" fill="#fff" fillOpacity="0.10" />
                <ellipse cx="55" cy="55" rx="12" ry="12" fill="#00ffe7" fillOpacity="0.18" />
                <ellipse cx="55" cy="55" rx="7" ry="7" fill="#fff" fillOpacity="0.18" />
                {/* Eyes */}
                <ellipse cx="45" cy="50" rx="4" ry="7" fill="#00ffe7" className="animate-blink" />
                <ellipse cx="65" cy="50" rx="4" ry="7" fill="#00ffe7" className="animate-blink" />
                {/* Smile */}
                <path d="M45 65 Q55 75 65 65" stroke="#00ffe7" strokeWidth="3" fill="none" />
              </svg>
            </div>
            <div className="mt-2 text-cyan-200 font-bold text-lg tracking-wide drop-shadow-lg animate-fade-in">AI Robot Assistant</div>
          </div>
          {/* Camera Feed Card */}
          <div className="relative w-full max-w-md rounded-3xl glassmorphic-card border-2 border-cyan-400/40 shadow-2xl overflow-hidden">
            <div className="absolute top-2 right-2 z-20 flex gap-2">
              <button onClick={toggleCamera} className={`p-2 rounded-full bg-cyan-700/80 hover:bg-cyan-500/80 text-white shadow transition text-xl ${isCameraOn ? 'ring-2 ring-cyan-300' : ''}`} title={isCameraOn ? 'Turn Off Camera' : 'Turn On Camera'}>
                <FiPower />
              </button>
              <button onClick={toggleMic} className={`p-2 rounded-full bg-pink-700/80 hover:bg-pink-500/80 text-white shadow transition text-xl ${isMicOn ? 'ring-2 ring-pink-300' : ''}`} title={isMicOn ? 'Mute Mic' : 'Unmute Mic'}>
                {isMicOn ? <FiMic /> : <FiMicOff />}
              </button>
            </div>
            <div className="bg-black flex items-center justify-center min-h-[320px] w-full">
              {isCameraOn ? (
                <img
                  ref={videoRef}
                  src={`${API_URL}/video_feed`}
                  alt="Robot Camera"
                  className="object-contain w-full h-[320px] rounded-3xl border-b-4 border-cyan-400/30"
                />
              ) : (
                <div className="w-full h-[320px] flex items-center justify-center bg-gray-900 rounded-3xl">
                  <div className="text-center text-cyan-200/60">
                    <FiPower className="mx-auto text-5xl mb-2 animate-pulse" />
                    <p className="text-lg">Camera is Off</p>
                    <p className="text-sm">Click power to turn on</p>
                  </div>
                </div>
              )}
              <canvas
                ref={canvasRef}
                className="absolute top-0 left-0 w-full h-full pointer-events-none"
              />
            </div>
          </div>
        </div>
        {/* Right: Chat/Response Area */}
        <div className="flex flex-col gap-8 items-center w-full lg:w-1/2">
          <div className="w-full max-w-xl glassmorphic-card border-2 border-pink-400/40 shadow-2xl rounded-3xl p-8 flex flex-col items-center animate-fade-in">
            <div className="w-full flex flex-col items-center">
              <div className="text-2xl md:text-3xl font-bold text-pink-200 mb-2 tracking-wide animate-gradient-text bg-gradient-to-r from-cyan-400 via-pink-400 to-blue-400 bg-clip-text text-transparent">
                {recognizedName ? `Hello, ${recognizedName}!` : 'Welcome!'}
              </div>
              <div className="text-lg md:text-xl text-cyan-100 font-mono text-center min-h-[60px] transition-all duration-300 animate-fade-in">
                <RobotSpeechDisplay message={robotMessage} isSpeaking={isSpeaking} volume={volume} />
              </div>
            </div>
            {/* Animated Typing Indicator */}
            {isSpeaking && (
              <div className="mt-4 flex gap-1 items-center">
                <span className="w-2 h-2 bg-pink-400 rounded-full animate-bounce" style={{ animationDelay: '0ms' }}></span>
                <span className="w-2 h-2 bg-cyan-400 rounded-full animate-bounce" style={{ animationDelay: '100ms' }}></span>
                <span className="w-2 h-2 bg-blue-400 rounded-full animate-bounce" style={{ animationDelay: '200ms' }}></span>
                <span className="ml-2 text-cyan-300 text-xs font-mono">Speaking...</span>
              </div>
            )}
          </div>
          {/* Floating Action Bar */}
          <div className="flex gap-8 items-center justify-center w-full mt-2">
            <button
              onClick={toggleMic}
              className={`p-5 rounded-full shadow-xl text-3xl ${isMicOn ? 'bg-pink-500 text-white' : 'bg-gray-300 text-gray-500'} hover:scale-110 transition-all duration-200 border-2 border-white/70 animate-glow`}
              title={isMicOn ? 'Mute Mic' : 'Unmute Mic'}
            >
              {isMicOn ? <FiMic /> : <FiMicOff />}
            </button>
            <button
              onClick={toggleCamera}
              className={`p-5 rounded-full shadow-xl text-3xl ${isCameraOn ? 'bg-cyan-500 text-white' : 'bg-gray-300 text-gray-500'} hover:scale-110 transition-all duration-200 border-2 border-white/70 animate-glow`}
              title={isCameraOn ? 'Turn Off Camera' : 'Turn On Camera'}
            >
              <FiPower />
            </button>
          </div>
        </div>
      </div>
      {/* Custom Animations */}
      <style>{`
        .glassmorphic-card {
          background: rgba(30, 41, 59, 0.55);
          box-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.18);
          backdrop-filter: blur(12px);
          -webkit-backdrop-filter: blur(12px);
        }
        .animate-bg-pan {
          animation: bg-pan 12s linear infinite alternate;
        }
        @keyframes bg-pan {
          0% { background-position: 0% 50%; }
          100% { background-position: 100% 50%; }
        }
        .animate-pulse-glow {
          animation: pulse-glow 2.5s infinite alternate;
        }
        @keyframes pulse-glow {
          0% { box-shadow: 0 0 0 0 #00ffe7aa; }
          100% { box-shadow: 0 0 32px 8px #00ffe7cc; }
        }
        .animate-bounce-slow {
          animation: bounce-slow 2.2s infinite alternate;
        }
        @keyframes bounce-slow {
          0% { transform: translateY(0); }
          100% { transform: translateY(-12px); }
        }
        .animate-blink {
          animation: blink 3s infinite;
        }
        @keyframes blink {
          0%, 90%, 100% { opacity: 1; }
          92%, 98% { opacity: 0.2; }
        }
        .animate-fade-in {
          animation: fade-in 1.2s cubic-bezier(.39,.575,.565,1) both;
        }
        @keyframes fade-in {
          0% { opacity: 0; transform: translateY(24px); }
          100% { opacity: 1; transform: translateY(0); }
        }
        .animate-gradient-text {
          background-size: 200% 200%;
          animation: gradient-text 3s ease-in-out infinite alternate;
        }
        @keyframes gradient-text {
          0% { background-position: 0% 50%; }
          100% { background-position: 100% 50%; }
        }
        .animate-glow {
          box-shadow: 0 0 16px 2px #00ffe7cc, 0 0 4px 1px #ff00cc99;
        }
      `}</style>
    </div>
  );
}
