import React from 'react';
import { useLocation } from 'react-router-dom';
import { motion } from 'framer-motion';

const getTitleFromPath = (path) => {
  if (path.startsWith('/enroll')) return 'Enroll New Person';
  switch (path) {
    case '/': return 'Student Database';
    case '/staff': return 'Staff Database';
    case '/unknowns': return 'Unknown Faces';
    case '/upload': return 'Upload New Image';
    case '/live': return 'Live Camera Feed';
    case '/cams': return 'All Camera Feeds';
    case '/logs': return 'Recognition Logs';
    default: return 'Dashboard';
  }
};

const Navbar = () => {
  const location = useLocation();
  const title = getTitleFromPath(location.pathname);

  return (
    <motion.header className="bg-white/80 dark:bg-gray-800/80 glass-card shadow-glass p-4 z-20 transition-colors duration-500 sticky top-0 backdrop-blur-xl">
      <div className="flex items-center justify-between">
        {/* Page Title */}
        <h1 className="text-2xl font-extrabold gradient-text drop-shadow-lg tracking-tight flex items-center">
          <span className="mr-2 shimmer">{title}</span>
          <span className="ml-2 w-2 h-2 rounded-full bg-gradient-to-r from-pink-500 to-orange-400 animate-pulse shadow-glow"></span>
        </h1>

        {/* Search and User Actions */}
        <div className="flex items-center space-x-4">
          <motion.div className="relative hidden md:block" whileFocus={{ scale: 1.03 }}>
            <input
              type="text"
              placeholder="Search..."
              className="pl-10 pr-4 py-2 rounded-full bg-gray-100/80 dark:bg-gray-700/80 focus:outline-none focus:ring-2 focus:ring-primary transition-all shadow-md"
            />
            <span className="absolute left-3 top-1/2 -translate-y-1/2">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
              </svg>
            </span>
          </motion.div>
          <motion.button className="p-2 rounded-full hover:bg-gradient-to-br hover:from-primary hover:to-secondary focus:outline-none focus:ring-2 focus:ring-primary shadow-md relative" whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.95 }}>
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-gray-500 dark:text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9" />
            </svg>
            <span className="absolute -top-1 -right-1 w-3 h-3 bg-gradient-to-r from-pink-500 to-orange-400 rounded-full border-2 border-white dark:border-gray-800 animate-pulse"></span>
          </motion.button>
          <motion.div className="w-10 h-10 rounded-full bg-gradient-to-br from-primary to-secondary shadow-glow flex items-center justify-center text-white font-bold text-lg cursor-pointer border-2 border-white dark:border-gray-800 hover:scale-110 transition-transform duration-300" whileHover={{ scale: 1.15, rotate: 6 }}>
            S
          </motion.div>
        </div>
      </div>
    </motion.header>
  );
};

export default Navbar;
