import React, { useEffect, useState } from 'react';
import API from '../utils/api';
import { FiMessageCircle } from 'react-icons/fi';
import { logChat as logChatUtil } from '../utils/chat_logger';
import RobotSpeechDisplay from './RobotSpeechDisplay';
import { speakWithAudioFeedback } from '../utils/voice';

export default function RobotView() {
  const [robotMessage, setRobotMessage] = useState('🤖 Awaiting input...');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [lastName, setLastName] = useState(null);
  const [chatLog, setChatLog] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [volume, setVolume] = useState(0);

  const speak = (text) => {
    setIsSpeaking(true);
    speakWithAudioFeedback(text, setVolume, () => {
      setIsSpeaking(false);
      setVolume(0);
    });
  };

  const fetchRecognition = async () => {
    try {
      const res = await API.get('/recognized_face');
      const { name } = res.data;

      const isNewKnownPerson = (
        name &&
        name !== 'Unknown' &&
        name !== 'No face' &&
        name !== lastName
      );

      const isNowUnknown = (
        (!name || name === 'Unknown' || name === 'No face') &&
        lastName !== 'Unknown'
      );

      if (isNewKnownPerson) {
        const greet = `Hi ${name}, I'm your college assistant. How can I help you?`;
        setRobotMessage(greet);
        setLastName(name);
        speak(greet);
        logChat('robot', name, greet);
      } else if (isNowUnknown) {
        const unknownMsg = '🔍 Face not recognized...';
        setRobotMessage(unknownMsg);
        setLastName('Unknown');
        logChat('robot', 'unknown', 'Unknown person detected');
      }

    } catch (err) {
      console.error('Recognition failed:', err);
      setRobotMessage('⚠️ Error fetching recognition data');
    }
  };

  useEffect(() => {
    const interval = setInterval(fetchRecognition, 3000);
    return () => clearInterval(interval);
  }, [lastName]);

  const logChat = async (sender, receiver, message) => {
    setChatLog((prev) => [...prev, { sender, receiver, message }]);
    await logChatUtil({ sender, receiver, message });
  };

  const handleAsk = async (inputText) => {
    const question = inputText || userInput;
    if (!question.trim()) return;

    setChatLog((prev) => [
      ...prev,
      { sender: 'user', receiver: lastName || 'robot', message: question }
    ]);

    try {
      const res = await API.get(`/info?question=${encodeURIComponent(question)}`);
      const answer = res.data.response;
      setRobotMessage(answer);
      speak(answer);
      logChat('robot', lastName || 'user', answer);
    } catch (err) {
      const errMsg = '❌ Error: Unable to fetch response.';
      setRobotMessage(errMsg);
      speak(errMsg);
    }

    setUserInput('');
  };

  return (
    <div className="space-y-6 fade-in max-w-7xl mx-auto px-4">
      {/* Header */}
      <div className="glass-card p-6">
        <h1 className="text-3xl font-bold gradient-text mb-2">Robot Monitor View</h1>
        <p className="text-gray-600 dark:text-gray-300">
          Displaying robot vision and real-time assistant messages
        </p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Live Video Feed */}
        <div className="glass-card p-4 relative flex flex-col h-[500px]">
          <h2 className="text-xl font-semibold mb-4 text-gray-800 dark:text-white">
            Live Feed
          </h2>
          <div className="flex-1 relative rounded-xl overflow-hidden bg-black">
            <img
              src="http://localhost:5000/video_feed"
              alt="Live Feed"
              className="w-full h-full object-cover"
              onError={(e) => {
                e.target.style.display = 'none';
                const fallback = document.getElementById('video-fallback');
                if (fallback) fallback.style.display = 'block';
              }}
            />
            <span
              id="video-fallback"
              style={{ display: 'none' }}
              className="absolute text-gray-400 text-lg left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2"
            >
              Video feed unavailable
            </span>
          </div>
        </div>

        {/* Robot Assistant */}
        <div className="glass-card p-4 flex flex-col justify-between h-[500px]">
          <h2 className="text-xl font-semibold mb-4 text-gray-800 dark:text-white">
            Assistant Conversation
          </h2>

          {/* Avatar + Typing Message */}
          <RobotSpeechDisplay
            message={robotMessage}
            isSpeaking={isSpeaking}
            volume={volume}
          />

          {/* Chat Log */}
          <div className="mt-6 flex flex-col space-y-4">
            <div className="h-48 overflow-y-auto bg-white/10 rounded-xl p-4 space-y-2 text-sm text-gray-800 dark:text-white font-mono">
              {chatLog.length === 0 ? (
                <div className="text-gray-400">No conversation yet.</div>
              ) : (
                chatLog.map((entry, i) => (
                  <div key={i} className={`p-2 rounded-lg ${
                    entry.sender === 'robot'
                      ? 'bg-blue-100 dark:bg-blue-800/30'
                      : 'bg-gray-200 dark:bg-gray-700/50'
                  }`}>
                    <strong>{entry.sender === 'robot' ? '🤖 Robot:' : '👤 User:'}</strong> {entry.message}
                    {entry.receiver && entry.receiver !== 'robot' && (
                      <span className="ml-2 text-xs text-gray-500">to {entry.receiver}</span>
                    )}
                  </div>
                ))
              )}
            </div>

            {/* Input Field */}
            <div className="flex">
              <input
                className="flex-1 rounded-l-xl px-4 py-2 border border-gray-300 dark:border-gray-700 focus:outline-none"
                placeholder="Ask the robot..."
                value={userInput}
                onChange={(e) => setUserInput(e.target.value)}
                onKeyDown={(e) => e.key === 'Enter' && handleAsk()}
              />
              <button
                className="btn-primary rounded-r-xl px-4 flex items-center"
                onClick={() => handleAsk()}
              >
                <FiMessageCircle className="mr-1" /> Ask
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
