import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  FiUsers, FiSearch, FiFilter, FiRefreshCw, FiEye, FiDownload, FiChevronDown, FiPlus, FiEdit, FiTrash2, FiCamera, FiX
} from 'react-icons/fi';
import LoadingSpinner from '../components/LoadingSpinner';
import Notification from '../components/Notification';

const UpdatePhotoModal = ({ person, personType, onClose, onPhotoUpdated }) => {
  const [newImage, setNewImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewImage(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(file);
      setError('');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!newImage) {
      setError('Please select an image file.');
      return;
    }
    setIsUploading(true);
    setError('');

    const formData = new FormData();
    formData.append('image', newImage);
    formData.append('personId', person.id);
    formData.append('personType', personType);

    try {
      const response = await fetch('http://localhost:5000/api/update_photo', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errData = await response.json();
        throw new Error(errData.error || 'Failed to update photo.');
      }

      const result = await response.json();
      onPhotoUpdated(result.new_image_path);
      onClose();
    } catch (err) {
      setError(err.message);
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-60 z-50 flex justify-center items-center fade-in">
      <div className="glass-card p-8 rounded-2xl shadow-xl w-full max-w-md m-4 relative slide-in-up">
        <button onClick={onClose} className="absolute top-4 right-4 text-gray-400 hover:text-white transition-colors">
          <FiX size={24} />
        </button>
        <h2 className="text-2xl font-bold gradient-text mb-4">Update Photo for {person.name}</h2>
        <form onSubmit={handleSubmit}>
          <div className="space-y-4">
            <div className="w-full h-48 border-2 border-dashed border-gray-600 rounded-lg flex items-center justify-center bg-white/5">
              {preview ? (
                <img src={preview} alt="New photo preview" className="w-full h-full object-contain rounded-lg" />
              ) : (
                <label htmlFor="photo-upload" className="text-center cursor-pointer">
                  <FiCamera className="mx-auto text-4xl text-gray-400 mb-2" />
                  <span className="text-gray-400">Click to select an image</span>
                </label>
              )}
              <input type="file" id="photo-upload" accept="image/*" onChange={handleFileChange} className="sr-only" />
            </div>
            {error && <p className="text-red-500 text-sm">{error}</p>}
            <button
              type="submit"
              className="btn-primary w-full"
              disabled={isUploading}
            >
              {isUploading ? <LoadingSpinner size="sm" /> : 'Upload and Update'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const StudentCard = ({ student, index, onUpdatePhoto, onEdit, onDelete }) => {
  return (
    <div 
      className="glass-card overflow-hidden group slide-in-up"
      style={{ animationDelay: `${index * 0.1}s` }}
    >
      <div className="relative">
        <img 
          src={`http://localhost:5000/${student.image_path}?t=${new Date().getTime()}`} 
          alt={student.name}
          className="w-full h-56 object-cover transition-transform duration-500 group-hover:scale-110"
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-all duration-300"></div>
        <div className="absolute top-4 right-4 flex space-x-2 opacity-0 group-hover:opacity-100 transition-opacity">
          <button onClick={() => onUpdatePhoto(student)} title="Update Photo" className="p-2 rounded-full bg-blue-500/80 hover:bg-blue-600 text-white backdrop-blur-sm">
            <FiCamera size={16} />
          </button>
          <button onClick={() => onEdit(student)} title="Edit Details" className="p-2 rounded-full bg-yellow-500/80 hover:bg-yellow-600 text-white backdrop-blur-sm">
            <FiEdit size={16} />
          </button>
          <button onClick={() => onDelete(student.id)} title="Delete Student" className="p-2 rounded-full bg-red-500/80 hover:bg-red-600 text-white backdrop-blur-sm">
            <FiTrash2 size={16} />
          </button>
        </div>
      </div>
      <div className="p-4">
        <h3 className="font-semibold text-lg text-gray-800 dark:text-white truncate">{student.name}</h3>
        <p className="text-sm text-gray-600 dark:text-gray-300">{student.department}</p>
        <p className="text-sm text-gray-500 dark:text-gray-400">Age: {student.age}</p>
      </div>
    </div>
  );
};

export default function StudentDB() {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [notification, setNotification] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [filterDepartment, setFilterDepartment] = useState('');
  const [isAdding, setIsAdding] = useState(false);
  const [newStudentData, setNewStudentData] = useState({ name: '', age: '', department: '' });

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/api/students');
      setStudents(response.data);
    } catch (err) {
      showNotification('error', 'Error', 'Failed to fetch student data.');
      console.error('Error fetching students:', err);
    } finally {
      setLoading(false);
    }
  };
  
  const showNotification = (type, title, message) => {
    setNotification({ type, title, message });
    setTimeout(() => setNotification(null), 5000);
  };

  const handlePhotoUpdated = (newImagePath) => {
    fetchStudents();
    showNotification('success', 'Success', 'Photo has been updated and faces are reloading.');
  };

  const handleAddStudent = async () => {
    setIsAdding(true);
    try {
      const response = await axios.post('http://localhost:5000/api/students', newStudentData);
      setStudents([...students, response.data]);
      setNewStudentData({ name: '', age: '', department: '' });
      showNotification('success', 'Success', 'Student added successfully.');
    } catch (err) {
      showNotification('error', 'Error', 'Failed to add student.');
      console.error('Error adding student:', err);
    } finally {
      setIsAdding(false);
    }
  };

  const handleEditStudent = async (updatedStudent) => {
    setSelectedStudent(updatedStudent);
  };

  const handleUpdateStudent = async (updatedData) => {
    setLoading(true);
    try {
      const response = await axios.put(`http://localhost:5000/api/students/${selectedStudent.id}`, updatedData);
      const updatedStudents = students.map(student => student.id === selectedStudent.id ? response.data : student);
      setStudents(updatedStudents);
      setSelectedStudent(null);
      showNotification('success', 'Success', 'Student details updated successfully.');
    } catch (err) {
      showNotification('error', 'Error', 'Failed to update student details.');
      console.error('Error updating student:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteStudent = async (studentId) => {
    if (!window.confirm('Are you sure you want to delete this student?')) return;
    setLoading(true);
    try {
      await axios.delete(`http://localhost:5000/api/students/${studentId}`);
      setStudents(students.filter(student => student.id !== studentId));
      showNotification('success', 'Success', 'Student deleted successfully.');
    } catch (err) {
      showNotification('error', 'Error', 'Failed to delete student.');
      console.error('Error deleting student:', err);
    } finally {
      setLoading(false);
    }
  };

  const filteredStudents = students.filter(student => {
    const matchesSearch = student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         student.id.toString().includes(searchTerm);
    const matchesDepartment = !filterDepartment || student.department === filterDepartment;
    return matchesSearch && matchesDepartment;
  });

  const departments = [...new Set(students.map(student => student.department))];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-white to-purple-100 py-10 px-2 fade-in relative overflow-x-hidden">
      {/* Animated background highlights */}
      <div className="pointer-events-none select-none">
        <div className="absolute top-10 left-1/4 w-1/2 h-32 bg-gradient-to-r from-blue-400/30 to-purple-400/20 rounded-full blur-2xl animate-pulse"></div>
        <div className="absolute bottom-10 right-1/4 w-1/3 h-24 bg-gradient-to-l from-purple-400/30 to-pink-400/20 rounded-full blur-2xl animate-pulse"></div>
      </div>
      <div className="relative z-10 max-w-7xl mx-auto flex flex-col gap-8">
        {/* Filters (modern, wide) with fixed + button */}
        <div className="relative">
          <div className="glass-card p-6 rounded-2xl shadow-xl border-2 border-blue-100 max-w-5xl w-full mx-auto mb-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="relative">
                <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                <input
                  type="text"
                  placeholder="Search by name or ID..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="enhanced-input pl-10 text-lg"
                />
              </div>
              <div className="relative">
                <FiFilter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                <select
                  value={filterDepartment}
                  onChange={(e) => setFilterDepartment(e.target.value)}
                  className="enhanced-input pl-10 text-lg"
                >
                  <option value="">All Departments</option>
                  {departments.map(dept => (
                    <option key={dept} value={dept}>{dept}</option>
                  ))}
                </select>
              </div>
            </div>
            {/* Fixed + button at top right of filters */}
            <button
              onClick={() => setIsAdding(true)}
              className="fixed top-8 right-16 z-30 p-5 rounded-full bg-gradient-to-br from-blue-500 to-purple-500 text-white shadow-2xl hover:scale-110 transition flex items-center justify-center text-3xl"
              title="Add Student"
              style={{marginTop: '-2.5rem'}}
            >
              <FiPlus />
            </button>
          </div>
        </div>
        {/* Student Cards Grid */}
        <div className="w-full">
          {loading ? (
            <div className="flex justify-center items-center py-32">
              <LoadingSpinner size="lg" text="Loading student database..." />
            </div>
          ) : filteredStudents.length === 0 ? (
            <div className="glass-card p-16 text-center">
              <FiUsers className="mx-auto text-6xl text-gray-400 mb-4" />
              <h3 className="text-xl font-semibold text-gray-800 dark:text-white mb-2">
                {students.length === 0 ? 'No Students Found' : 'No Results Found'}
              </h3>
              <p className="text-gray-600 dark:text-gray-300">
                {students.length === 0 
                  ? 'The student database is empty. Try enrolling new students.'
                  : 'Try adjusting your search or filter criteria.'
                }
              </p>
            </div>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-8">
              {filteredStudents.map((student, index) => (
                <StudentCard 
                  key={student.id} 
                  student={student} 
                  index={index} 
                  onUpdatePhoto={setSelectedStudent}
                  onEdit={handleEditStudent}
                  onDelete={handleDeleteStudent}
                />
              ))}
            </div>
          )}
        </div>
        {/* Add Student Modal (modern, stepper style) */}
        {isAdding && (
          <div className="fixed inset-0 bg-black bg-opacity-60 z-50 flex justify-center items-center fade-in">
            <div className="glass-card p-8 rounded-2xl shadow-xl w-full max-w-md m-4 relative slide-in-up">
              <button onClick={() => setIsAdding(false)} className="absolute top-4 right-4 text-gray-400 hover:text-white transition-colors">
                <FiX size={24} />
              </button>
              <h2 className="text-2xl font-bold gradient-text mb-4">Add New Student</h2>
              <form onSubmit={(e) => { e.preventDefault(); handleAddStudent(); }}>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Name</label>
                    <input
                      type="text"
                      value={newStudentData.name}
                      onChange={(e) => setNewStudentData({ ...newStudentData, name: e.target.value })}
                      className="enhanced-input"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Age</label>
                    <input
                      type="number"
                      value={newStudentData.age}
                      onChange={(e) => setNewStudentData({ ...newStudentData, age: e.target.value })}
                      className="enhanced-input"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Department</label>
                    <input
                      type="text"
                      value={newStudentData.department}
                      onChange={(e) => setNewStudentData({ ...newStudentData, department: e.target.value })}
                      className="enhanced-input"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Photo</label>
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => setNewStudentData({ ...newStudentData, photo: e.target.files[0] })}
                      className="enhanced-input"
                      required
                    />
                  </div>
                  <button
                    type="submit"
                    className="btn-primary w-full"
                    disabled={isAdding}
                  >
                    {isAdding ? <LoadingSpinner size="sm" /> : 'Add Student'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
        {/* Update Photo Modal, Edit Modal, Notification (unchanged) */}
        {notification && (
          <Notification 
            type={notification.type}
            title={notification.title}
            message={notification.message}
            onClose={() => setNotification(null)}
          />
        )}
        {selectedStudent && (
          <UpdatePhotoModal 
            person={selectedStudent}
            personType="student"
            onClose={() => setSelectedStudent(null)}
            onPhotoUpdated={handlePhotoUpdated}
          />
        )}
      </div>
    </div>
  );
}
