import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import { FiCpu, FiMic } from 'react-icons/fi';
import RobotSpeechDisplay from './RobotSpeechDisplay';
import { speakWithAudioFeedback } from '../utils/voice';

const API_URL = 'http://localhost:5000';

export default function RobotController() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [recognizedName, setRecognizedName] = useState('');
  const [collegeName, setCollegeName] = useState('');
  const [robotMessage, setRobotMessage] = useState('🎓 Initializing...');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [wasUnknownGreeted, setWasUnknownGreeted] = useState(false);
  const [volume, setVolume] = useState(0);
  const [lastTimestamp, setLastTimestamp] = useState(0);

  const fetchCollegeName = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/college_name`);
      setCollegeName(res.data.name);
    } catch {
      setCollegeName('Your College');
    }
  };

  const fetchRecognition = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/recognized_face`);
      const { name, timestamp } = res.data;
      if (timestamp === lastTimestamp) return;
      setLastTimestamp(timestamp);

      if (name && name !== 'Unknown' && name !== recognizedName) {
        const greet = `Hi ${name}, I’m the robot assistant of ${collegeName}. How can I help?`;
        handleVoiceResponse(greet);
        setRecognizedName(name);
        setRobotMessage(greet);
        setWasUnknownGreeted(false);
        setTimeout(handleMicListen, 3000);
      } else if ((!name || name === 'Unknown') && !wasUnknownGreeted) {
        const greet = `Hello! I’m CollegeBot at ${collegeName}. How can I assist you?`;
        handleVoiceResponse(greet);
        setRecognizedName('');
        setRobotMessage(greet);
        setWasUnknownGreeted(true);
      }
    } catch {
      // ignore
    }
  };

  const handleVoiceResponse = (text) => {
    setIsSpeaking(true);
    speakWithAudioFeedback(text, setVolume, () => {
      setIsSpeaking(false);
      setVolume(0);
    });
  };

  const handleMicListen = async () => {
    if (isSpeaking) return;
    setRobotMessage('🎤 Listening...');
    try {
      const res = await axios.post(`${API_URL}/api/robot_ask`, { query: 'listen_voice' });
      const answer = res.data.response || "Sorry, I didn't catch that.";
      setRobotMessage(answer);
      handleVoiceResponse(answer);
    } catch {
      setRobotMessage("❌ Can't reach AI assistant.");
    }
  };

  const drawOverlay = () => {
    const img = videoRef.current;
    const canvas = canvasRef.current;
    if (!img || !canvas) return;
    const width = img.clientWidth;
    const height = img.clientHeight;
    canvas.width = width;
    canvas.height = height;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, width, height);
    ctx.fillStyle = recognizedName ? '#28a745' : '#d63384';
    ctx.font = 'bold 1.5rem sans-serif';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'top';
    ctx.fillText(recognizedName ? `🎉 Hello, ${recognizedName}` : '🤖 Face Not Recognized', width / 2, 20);
  };

  useEffect(() => {
    fetchCollegeName();
    const interval = setInterval(fetchRecognition, 3000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    drawOverlay();
    const obs = new ResizeObserver(drawOverlay);
    if (videoRef.current) obs.observe(videoRef.current);
    return () => obs.disconnect();
  }, [recognizedName]);

  return (
    <div className="max-w-5xl mx-auto p-4 space-y-6">
      <div className="glass-card p-6">
        <div className="flex items-center space-x-3">
          <FiCpu size={24} className="text-white p-3 rounded-xl bg-gradient-to-r from-purple-600 to-indigo-600" />
          <div>
            <h1 className="text-3xl font-bold gradient-text">Robot Controller</h1>
            <p className="text-gray-600">Stable camera + voice interface</p>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Camera Feed */}
        <div className="glass-card overflow-hidden relative h-[450px]">
          <img
            ref={videoRef}
            src={`${API_URL}/video_feed`}
            alt="Robot Camera"
            className="absolute inset-0 w-full h-full object-cover"
          />
          <canvas ref={canvasRef} className="absolute inset-0 w-full h-full pointer-events-none" />
        </div>

        {/* Voice & Avatar */}
        <div className="glass-card flex flex-col justify-between p-6 h-[450px]">
          <h2 className="text-xl font-bold flex items-center">
            <FiMic className="mr-2" /> Assistant
          </h2>
          <RobotSpeechDisplay message={robotMessage} isSpeaking={isSpeaking} volume={volume} />
          <div className="text-sm flex justify-between">
            <span>College: <strong>{collegeName}</strong></span>
            <span>Mic: {isSpeaking ? 'Speaking...' : 'Idle'}</span>
          </div>
        </div>
      </div>
    </div>
  );
}
