import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FiUsers, FiUserCheck, FiAlertCircle, FiBookOpen, FiMessageCircle } from 'react-icons/fi';
import { useAuth } from '../App';

const adminLinks = [
  {
    to: '/students',
    icon: <FiUsers className="text-3xl text-blue-600 mb-2 " />,
    title: 'Student Database',
    desc: 'View, edit, and manage student records',
    color: 'from-blue-100 to-blue-50',
  },
  {
    to: '/staff',
    icon: <FiUserCheck className="text-3xl text-purple-600 mb-2" />,
    title: 'Staff Database',
    desc: 'View, edit, and manage staff records',
    color: 'from-purple-100 to-purple-50',
  },
  {
    to: '/unknowns',
    icon: <FiAlertCircle className="text-3xl text-red-500 mb-2" />,
    title: 'Unknown Faces',
    desc: 'Review and enroll unknown face detections',
    color: 'from-red-100 to-red-50',
  },
  {
    to: '/collegeinfo',
    icon: <FiBookOpen className="text-3xl text-green-600 mb-2" />,
    title: 'College Info',
    desc: 'Edit and manage college, event, and staff info',
    color: 'from-green-100 to-green-50',
  },
  {
    to: '/chatlogs',
    icon: <FiMessageCircle className="text-3xl text-indigo-600 mb-2" />,
    title: 'Chat Logs',
    desc: 'View and manage all chat interactions',
    color: 'from-indigo-100 to-indigo-50',
  },
];

// Placeholder data for future dynamic content
const recentUsers = [
  { username: 'vishal', role: 'admin', lastLogin: '2025-07-07 10:00' },
  { username: 'sabari', role: 'admin', lastLogin: '2025-07-06 15:30' },

  // ...more rows
];

const changeLog = [
  { user: 'sabari', action: 'edited student sabari', time: '2025-07-07 10:05' },
  { user: 'vishal', action: 'added college info', time: '2025-07-06 15:35' },
  // ...more log entries
];

export default function AdminPanel() {
  const { isAuthenticated, user } = useAuth();
  const [newUsername, setNewUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [newRole, setNewRole] = useState('user');
  const [msg, setMsg] = useState('');
  const [err, setErr] = useState('');
  const [users, setUsers] = useState([]);
  const [logs, setLogs] = useState([]);
  const [loadingUsers, setLoadingUsers] = useState(false);
  const [loadingLogs, setLoadingLogs] = useState(false);
  const [changeRequests, setChangeRequests] = useState([]);
  const [loadingRequests, setLoadingRequests] = useState(false);
  const handleCreateUser = async (e) => {
    e.preventDefault();
    setMsg(''); setErr('');
    const res = await fetch('/api/users', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        admin_username: user?.username,
        admin_password: prompt('Enter your admin password:'),
        username: newUsername,
        password: newPassword,
        role: newRole
      })
    });
    const data = await res.json();
    if (data.success) {
      setMsg('User created successfully!');
      setNewUsername(''); setNewPassword(''); setNewRole('user');
    } else {
      setErr(data.error || 'Failed to create user');
    }
  };
  // Fetch users, logs, and change requests
  useEffect(() => {
    if (user?.role === 'admin') {
      setLoadingUsers(true);
      fetch(`/api/users?admin_username=${user.username}&admin_password=${prompt('Enter your admin password to view users:')}`)
        .then(res => res.json())
        .then(data => {
          setUsers(data.users || []);
          setLoadingUsers(false);
        });
      setLoadingLogs(true);
      fetch('/api/change_log')
        .then(res => res.json())
        .then(data => {
          setLogs(data.logs || []);
          setLoadingLogs(false);
        });
      setLoadingRequests(true);
      fetch(`/api/change_requests?admin_username=${user.username}&admin_password=${prompt('Enter your admin password to view requests:')}`)
        .then(res => res.json())
        .then(data => {
          setChangeRequests(data.requests || []);
          setLoadingRequests(false);
        });
    }
  }, [user]);

  // Grant/revoke can_edit
  const handlePermission = async (username, can_edit) => {
    const res = await fetch('/api/users/permission', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        admin_username: user.username,
        admin_password: prompt('Enter your admin password to change permission:'),
        username,
        can_edit
      })
    });
    const data = await res.json();
    if (data.success) {
      setUsers(users => users.map(u => u.username === username ? { ...u, can_edit } : u));
    } else {
      alert(data.error || 'Failed to update permission');
    }
  };

  // Approve/reject change request
  const handleApproveRequest = async (request_id, approve) => {
    const res = await fetch('/api/change_requests/approve', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        admin_username: user.username,
        admin_password: prompt('Enter your admin password to approve/reject:'),
        request_id,
        approve
      })
    });
    const data = await res.json();
    if (data.success) {
      setChangeRequests(reqs => reqs.filter(r => r.id !== request_id));
    } else {
      alert(data.error || 'Failed to update request');
    }
  };
  // Placeholder for future: user info and change log
  const currentUser = isAuthenticated ? 'admin' : 'guest'; // Replace with real user info in future

  return (
    <div className="min-h-screen py-10 px-4 bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100 dark:from-gray-900 dark:via-gray-800 dark:to-slate-900 fade-in">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-4xl font-extrabold text-blue-800 dark:text-white mb-8 text-center drop-shadow">Admin Control Panel</h1>
        {/* User Creation Form (admin only) */}
        {user?.role === 'admin' && (
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-6 mb-8">
            <h2 className="text-xl font-bold mb-4 text-blue-700">Create New User</h2>
            <form className="flex flex-col gap-4 sm:flex-row sm:items-end" onSubmit={handleCreateUser}>
              <input type="text" placeholder="Username" value={newUsername} onChange={e => setNewUsername(e.target.value)} required className="border rounded px-3 py-2" />
              <input type="password" placeholder="Password" value={newPassword} onChange={e => setNewPassword(e.target.value)} required className="border rounded px-3 py-2" />
              <select value={newRole} onChange={e => setNewRole(e.target.value)} className="border rounded px-3 py-2">
                <option value="user">User</option>
                <option value="admin">Admin</option>
              </select>
              <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Create</button>
            </form>
            {msg && <div className="text-green-600 mt-2">{msg}</div>}
            {err && <div className="text-red-600 mt-2">{err}</div>}
          </div>
        )}
        {/* User List (admin only) */}
        {user?.role === 'admin' && (
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-6 mb-8">
            <h2 className="text-xl font-bold mb-4 text-blue-700">All Users</h2>
            {loadingUsers ? <div>Loading users...</div> : (
              <table className="min-w-full text-sm">
                <thead>
                  <tr>
                    <th className="p-2 text-left">ID</th>
                    <th className="p-2 text-left">Username</th>
                    <th className="p-2 text-left">Can Edit?</th>
                    <th className="p-2 text-left">Created At</th>
                    <th className="p-2 text-left">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map(u => (
                    <tr key={u.id}>
                      <td className="p-2">{u.id}</td>
                      <td className="p-2">{u.username}</td>
                      <td className="p-2">{u.can_edit ? 'Yes' : 'No'}</td>
                      <td className="p-2">{u.created_at}</td>
                      <td className="p-2">
                        <button
                          className={`px-2 py-1 rounded ${u.can_edit ? 'bg-red-500' : 'bg-green-500'} text-white`}
                          onClick={() => handlePermission(u.username, !u.can_edit)}
                        >
                          {u.can_edit ? 'Revoke Edit' : 'Grant Edit'}
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        )}
        {/* Change Requests (admin only) */}
        {user?.role === 'admin' && (
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-6 mb-8">
            <h2 className="text-xl font-bold mb-4 text-blue-700">Change Requests</h2>
            {loadingRequests ? <div>Loading requests...</div> : (
              <table className="min-w-full text-sm">
                <thead>
                  <tr>
                    <th className="p-2 text-left">ID</th>
                    <th className="p-2 text-left">User ID</th>
                    <th className="p-2 text-left">Type</th>
                    <th className="p-2 text-left">Target</th>
                    <th className="p-2 text-left">Data</th>
                    <th className="p-2 text-left">Created</th>
                    <th className="p-2 text-left">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {changeRequests.map(r => (
                    <tr key={r.id}>
                      <td className="p-2">{r.id}</td>
                      <td className="p-2">{r.user_id}</td>
                      <td className="p-2">{r.change_type}</td>
                      <td className="p-2">{r.target_id}</td>
                      <td className="p-2">{r.data}</td>
                      <td className="p-2">{r.created_at}</td>
                      <td className="p-2 flex gap-2">
                        <button className="bg-green-600 text-white px-2 py-1 rounded" onClick={() => handleApproveRequest(r.id, true)}>Approve</button>
                        <button className="bg-red-600 text-white px-2 py-1 rounded" onClick={() => handleApproveRequest(r.id, false)}>Reject</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        )}
        {/* Change Log (admin only) */}
        {user?.role === 'admin' && (
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-6 mb-8">
            <h2 className="text-xl font-bold mb-4 text-blue-700">Change Log</h2>
            {loadingLogs ? <div>Loading logs...</div> : (
              <table className="min-w-full text-sm">
                <thead>
                  <tr>
                    <th className="p-2 text-left">User</th>
                    <th className="p-2 text-left">Action</th>
                    <th className="p-2 text-left">Details</th>
                    <th className="p-2 text-left">Time</th>
                  </tr>
                </thead>
                <tbody>
                  {logs.map(log => (
                    <tr key={log.id}>
                      <td className="p-2">{log.username}</td>
                      <td className="p-2">{log.action}</td>
                      <td className="p-2">{log.details}</td>
                      <td className="p-2">{log.timestamp}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        )}
        {/* Main admin links grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 mb-10">
          {adminLinks.map(link => (
            <Link
              key={link.to}
              to={link.to}
              className={`group bg-gradient-to-br ${link.color} rounded-2xl shadow-lg p-8 flex flex-col items-center text-center hover:scale-105 hover:shadow-2xl transition-all border border-gray-200 dark:border-gray-700`}
            >
              {link.icon}
              <div className="text-xl font-bold mb-1 text-gray-800 dark:text-white group-hover:text-blue-700 transition-colors">{link.title}</div>
              <div className="text-gray-500 dark:text-gray-300 text-sm">{link.desc}</div>
            </Link>
          ))}
        </div>

        {/* Recent User Logins */}
        <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-6 mb-8">
          <h2 className="text-xl font-bold mb-4 text-blue-700">Recent User Logins</h2>
          <table className="min-w-full text-sm">
            <thead>
              <tr>
                <th className="p-2 text-left">Username</th>
                <th className="p-2 text-left">Role</th>
                <th className="p-2 text-left">Last Login</th>
              </tr>
            </thead>
            <tbody>
              {recentUsers.map((user, idx) => (
                <tr key={idx}>
                  <td className="p-2">{user.username}</td>
                  <td className="p-2">{user.role}</td>
                  <td className="p-2">{user.lastLogin}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Recent Changes */}
        <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-6 mb-8">
          <h2 className="text-xl font-bold mb-4 text-blue-700">Recent Changes</h2>
          <ul className="text-gray-700 dark:text-gray-300 text-sm list-disc ml-5">
            {changeLog.map((log, idx) => (
              <li key={idx}>
                {log.user} {log.action} <span className="text-xs text-gray-400">({log.time})</span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
