// RobotView.jsx (Full Updated View with Dashboard Features)
import React, { useState, useRef, useEffect } from 'react';
import { FiSend, FiUsers, FiUser, FiMessageSquare, FiActivity, FiMic } from 'react-icons/fi';
import { robotAsk } from '../utils/api';
import API from '../utils/api';

// Simulate robot actions and analysis
function getSimulatedActionAnalysis(action) {
  // Simulate type, sentiment, prediction
  const types = ['Greet', 'Answer', 'Clarify', 'Goodbye', 'Info'];
  const sentiments = ['Positive', 'Neutral', 'Helpful', 'Friendly'];
  const predictions = ['High', 'Medium', 'Low'];
  return {
    type: types[Math.floor(Math.random() * types.length)],
    sentiment: sentiments[Math.floor(Math.random() * sentiments.length)],
    prediction: predictions[Math.floor(Math.random() * predictions.length)]
  };
}

function getSimulatedVoiceAnalysis() {
  // Simulate volume, clarity, prediction
  const volume = Math.floor(Math.random() * 100);
  const clarity = ['Clear', 'Normal', 'Noisy'][Math.floor(Math.random() * 3)];
  const prediction = ['High', 'Medium', 'Low'][Math.floor(Math.random() * 3)];
  return { volume, clarity, prediction };
}

export default function RobotView() {
  const [chatInput, setChatInput] = useState('');
  const [chatLoading, setChatLoading] = useState(false);
  const [aiChat, setAiChat] = useState([]); // [{sender, message, isUser, voiceAnalysis}]
  const [robotActions, setRobotActions] = useState([]); // [{action, analysis}]
  const [stats, setStats] = useState(null);
  const [robotStatus, setRobotStatus] = useState('Active');
  const chatEndRef = useRef(null);

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const res = await API.get('/statistics');
      setStats(res.data);
    } catch (e) {
      setStats(null);
    }
  };

  useEffect(() => {
    setTimeout(() => chatEndRef.current?.scrollIntoView({ behavior: 'smooth' }), 100);
  }, [aiChat]);

  const handleChatSend = async (e) => {
    e.preventDefault();
    if (!chatInput.trim()) return;
    // Simulate voice analysis for user
    const userVoice = getSimulatedVoiceAnalysis();
    setAiChat(prev => [...prev, { sender: 'You', message: chatInput, isUser: true, voiceAnalysis: userVoice }]);
    setRobotActions(prev => [
      { action: `User asked: "${chatInput}"`, analysis: getSimulatedActionAnalysis('User') },
      ...prev.slice(0, 9)
    ]);
    setChatLoading(true);
    setRobotStatus('Processing');
    try {
      const res = await robotAsk(chatInput);
      // Simulate voice analysis for AI
      const aiVoice = getSimulatedVoiceAnalysis();
      setAiChat(prev => [...prev, { sender: 'AI', message: res.data.response, isUser: false, voiceAnalysis: aiVoice }]);
      setRobotActions(prev => [
        { action: `AI responded: "${res.data.response.slice(0, 40)}..."`, analysis: getSimulatedActionAnalysis('AI') },
        ...prev.slice(0, 9)
      ]);
      setRobotStatus('Active');
    } catch (e) {
      const aiVoice = getSimulatedVoiceAnalysis();
      setAiChat(prev => [...prev, { sender: 'AI', message: 'Sorry, I could not process your request.', isUser: false, voiceAnalysis: aiVoice }]);
      setRobotActions(prev => [
        { action: `AI error response`, analysis: getSimulatedActionAnalysis('AI') },
        ...prev.slice(0, 9)
      ]);
      setRobotStatus('Active');
    } finally {
      setChatInput('');
      setChatLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-indigo-100 via-white to-blue-200 relative overflow-hidden fade-in">
      {/* Futuristic background blobs */}
      <div className="pointer-events-none select-none absolute inset-0 z-0">
        <div className="absolute top-10 left-1/4 w-1/2 h-40 bg-gradient-to-r from-indigo-400/30 to-blue-400/20 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-10 right-1/4 w-1/3 h-32 bg-gradient-to-l from-blue-400/30 to-pink-400/20 rounded-full blur-2xl animate-pulse"></div>
      </div>
      <div className="relative z-10 w-full max-w-5xl mx-auto flex flex-col md:flex-row gap-8 py-12 px-2">
        {/* Left: Chat Input only */}
        <div className="w-full md:w-1/3 flex flex-col justify-end">
          <div className="glass-card rounded-3xl shadow-2xl border-2 border-blue-100 p-6 flex flex-col gap-4 bg-white/80 sticky bottom-0">
            <h2 className="text-xl font-bold text-indigo-700 mb-2 flex items-center gap-2"><FiMessageSquare /> Ask the Robot</h2>
            <form onSubmit={handleChatSend} className="flex w-full gap-2">
              <input
                type="text"
                className="flex-1 enhanced-input text-lg px-4 py-3 rounded-2xl border-2 border-blue-200 focus:border-indigo-400 focus:ring-2 focus:ring-indigo-100 transition-all shadow"
                placeholder="Type your question..."
                value={chatInput}
                onChange={e => setChatInput(e.target.value)}
                disabled={chatLoading}
                required
                autoFocus
              />
              <button
                type="submit"
                className="btn-primary flex items-center gap-1 px-6 py-3 rounded-2xl text-lg shadow-lg"
                disabled={chatLoading || !chatInput.trim()}
              >
                <FiSend size={22} />
                {chatLoading ? 'Sending...' : 'Send'}
              </button>
            </form>
            {/* Robot Status */}
            <div className="flex items-center gap-2 mt-2">
              <FiActivity className={robotStatus === 'Active' ? 'text-green-500 animate-pulse' : 'text-yellow-500'} />
              <span className={robotStatus === 'Active' ? 'text-green-700 font-semibold' : 'text-yellow-700 font-semibold'}>
                Robot Status: {robotStatus}
              </span>
            </div>
          </div>
        </div>
        {/* Right: Conversation + Actions + Stats */}
        <div className="flex-1 flex flex-col gap-6">
          {/* Robot Actions & Analysis */}
          <div className="glass-card w-full rounded-3xl shadow-2xl border-2 border-blue-100 p-6 mb-2">
            <h2 className="text-lg font-bold text-indigo-700 mb-2 flex items-center gap-2"><FiActivity /> Robot Actions & Analysis</h2>
            {robotActions.length === 0 ? (
              <div className="text-gray-400 text-center">No actions yet.</div>
            ) : (
              <ul className="space-y-2">
                {robotActions.slice(0, 6).map((act, idx) => (
                  <li key={idx} className="flex flex-col md:flex-row md:items-center gap-2 border-b border-blue-50 pb-2">
                    <span className="font-mono text-sm text-gray-700">{act.action}</span>
                    <span className="flex gap-2 text-xs">
                      <span className="bg-indigo-100 text-indigo-700 px-2 py-1 rounded-full">Type: {act.analysis.type}</span>
                      <span className="bg-blue-100 text-blue-700 px-2 py-1 rounded-full">Sentiment: {act.analysis.sentiment}</span>
                      <span className="bg-green-100 text-green-700 px-2 py-1 rounded-full">Prediction: {act.analysis.prediction}</span>
                    </span>
                  </li>
                ))}
              </ul>
            )}
          </div>
          {/* Conversation */}
          <div className="glass-card w-full rounded-3xl shadow-2xl border-2 border-blue-100 p-6 flex flex-col h-[500px] max-h-[70vh]">
            <h2 className="text-lg font-bold text-indigo-700 mb-2 flex items-center gap-2"><FiMic /> Conversation</h2>
            <div className="flex-1 w-full overflow-y-auto pr-2">
              {aiChat.length === 0 ? (
                <div className="text-gray-400 text-center my-12">No conversation yet.</div>
              ) : (
                <ul className="space-y-4">
                  {aiChat.map((msg, idx) => (
                    <li key={idx} className={msg.isUser ? 'flex justify-start' : 'flex justify-end'}>
                      <div className={
                        'max-w-[80%] px-4 py-2 rounded-2xl shadow relative ' +
                        (msg.isUser
                          ? 'bg-indigo-500 text-white rounded-bl-none'
                          : 'bg-gray-100 text-gray-800 rounded-br-none border border-blue-100')
                      }>
                        <span className="block text-xs font-semibold mb-1 opacity-70">{msg.sender}</span>
                        <span className="break-words whitespace-pre-line text-base">{msg.message}</span>
                        {/* Voice Analysis Badge */}
                        <div className="flex gap-2 mt-2 text-xs">
                          <span className="bg-blue-100 text-blue-700 px-2 py-1 rounded-full">Volume: {msg.voiceAnalysis?.volume ?? '--'}</span>
                          <span className="bg-green-100 text-green-700 px-2 py-1 rounded-full">Clarity: {msg.voiceAnalysis?.clarity ?? '--'}</span>
                          <span className="bg-indigo-100 text-indigo-700 px-2 py-1 rounded-full">Prediction: {msg.voiceAnalysis?.prediction ?? '--'}</span>
                        </div>
                      </div>
                    </li>
                  ))}
                  <div ref={chatEndRef} />
                </ul>
              )}
            </div>
          </div>
          {/* Stats Card */}
          <div className="glass-card w-full rounded-3xl shadow-2xl border-2 border-blue-100 p-6 flex flex-col items-center gap-6">
            <h2 className="text-xl font-bold text-indigo-700 mb-2 flex items-center gap-2"><FiUsers /> Database Stats</h2>
            <div className="flex flex-col gap-4 w-full">
              <div className="flex items-center gap-3">
                <span className="inline-flex items-center justify-center w-10 h-10 rounded-full bg-indigo-100 text-indigo-600"><FiUser size={20} /></span>
                <span className="text-lg font-semibold">Students:</span>
                <span className="ml-auto text-xl font-bold text-indigo-700">{stats ? stats.total_students : '--'}</span>
              </div>
              <div className="flex items-center gap-3">
                <span className="inline-flex items-center justify-center w-10 h-10 rounded-full bg-blue-100 text-blue-600"><FiUser size={20} /></span>
                <span className="text-lg font-semibold">Staff:</span>
                <span className="ml-auto text-xl font-bold text-blue-700">{stats ? stats.total_staff : '--'}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
