import React, { useEffect, useState } from 'react';
import { FiEdit, FiPlus, FiTrash2, FiX } from 'react-icons/fi';
import Notification from '../components/Notification';
import API from '../utils/api';
import LoadingSpinner from '../components/LoadingSpinner';

const API_URL = API.defaults.baseURL.replace('/api', '');

function Modal({ open, onClose, children }) {
  if (!open) return null;
  return (
    <div className="fixed inset-0 z-40 flex items-center justify-center bg-black bg-opacity-40">
      <div className="bg-white dark:bg-gray-900 rounded-lg shadow-lg p-6 w-full max-w-md relative animate-fadeIn">
        <button
          className="absolute top-3 right-3 text-gray-400 hover:text-gray-700 dark:hover:text-gray-200"
          onClick={onClose}
        >
          <FiX size={22} />
        </button>
        {children}
      </div>
    </div>
  );
}

export default function CollegeInfoEditor() {
  const [info, setInfo] = useState([]);
  const [loading, setLoading] = useState(true);
  const [notification, setNotification] = useState(null);
  const [editingInfo, setEditingInfo] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [modalType, setModalType] = useState('add'); // 'add' or 'edit'
  const [formInfo, setFormInfo] = useState({ category: '', content: '' });

  useEffect(() => {
    fetchInfo();
  }, []);

  const fetchInfo = async () => {
    try {
      setLoading(true);
      const res = await API.get('/college_info');
      setInfo(res.data || []);
    } catch (err) {
      showNotification('error', 'Failed to fetch college information.');
    } finally {
      setLoading(false);
    }
  };

  const showNotification = (type, message) => {
    setNotification({ type, message });
    setTimeout(() => setNotification(null), 4000);
  };

  const openAddModal = () => {
    setFormInfo({ category: '', content: '' });
    setModalType('add');
    setModalOpen(true);
  };

  const openEditModal = (row) => {
    setFormInfo({ ...row });
    setEditingInfo(row);
    setModalType('edit');
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
    setEditingInfo(null);
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormInfo((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddInfo = async (e) => {
    e.preventDefault();
    try {
      await API.post('/college_info', formInfo);
      showNotification('success', 'Info added successfully.');
      fetchInfo();
      closeModal();
    } catch (err) {
      showNotification('error', 'Failed to add info.');
    }
  };

  const handleEditInfo = async (e) => {
    e.preventDefault();
    try {
      await API.put(`/college_info/${editingInfo.id}`, formInfo);
      showNotification('success', 'Info updated successfully.');
      fetchInfo();
      closeModal();
    } catch (err) {
      showNotification('error', 'Failed to update info.');
    }
  };

  const handleDeleteInfo = async (id) => {
    if (window.confirm('Are you sure you want to delete this info?')) {
      try {
        await API.delete(`/college_info/${id}`);
        showNotification('success', 'Info deleted successfully.');
        fetchInfo();
      } catch (err) {
        showNotification('error', 'Failed to delete info.');
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-950 py-10 px-4">
      <div className="max-w-3xl mx-auto">
        <div className="flex items-center justify-between mb-8">
          <h1 className="text-3xl font-bold text-gray-800 dark:text-white">College Info</h1>
          <button
            className="flex items-center gap-2 px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg shadow transition"
            onClick={openAddModal}
          >
            <FiPlus /> Add Info
          </button>
        </div>
        {loading ? (
          <div className="flex justify-center py-20">
            <LoadingSpinner />
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
            {info.length === 0 ? (
              <div className="col-span-full text-center text-gray-500 dark:text-gray-400">No info available.</div>
            ) : (
              info.map((row) => (
                <div
                  key={row.id}
                  className="bg-white dark:bg-gray-900 rounded-xl shadow-md p-6 flex flex-col gap-2 border border-gray-100 dark:border-gray-800 hover:shadow-lg transition"
                >
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-sm font-semibold text-blue-600 dark:text-blue-400 uppercase tracking-wide">{row.category}</span>
                    <div className="flex gap-2">
                      <button
                        onClick={() => openEditModal(row)}
                        className="p-2 rounded-full bg-yellow-400/80 hover:bg-yellow-500 text-white"
                        title="Edit"
                      >
                        <FiEdit size={16} />
                      </button>
                      <button
                        onClick={() => handleDeleteInfo(row.id)}
                        className="p-2 rounded-full bg-red-500/80 hover:bg-red-600 text-white"
                        title="Delete"
                      >
                        <FiTrash2 size={16} />
                      </button>
                    </div>
                  </div>
                  <div className="text-gray-700 dark:text-gray-200 whitespace-pre-line min-h-[48px]">{row.content}</div>
                </div>
              ))
            )}
          </div>
        )}
      </div>

      {/* Modal for Add/Edit */}
      <Modal open={modalOpen} onClose={closeModal}>
        <form onSubmit={modalType === 'add' ? handleAddInfo : handleEditInfo} className="space-y-4">
          <h2 className="text-xl font-bold mb-2 text-gray-800 dark:text-white">
            {modalType === 'add' ? 'Add New Info' : 'Edit Info'}
          </h2>
          <div>
            <label className="block text-sm font-medium text-gray-600 dark:text-gray-300 mb-1">Category</label>
            <input
              type="text"
              name="category"
              value={formInfo.category}
              onChange={handleFormChange}
              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-700 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 dark:bg-gray-800 dark:text-white"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-600 dark:text-gray-300 mb-1">Content</label>
            <textarea
              name="content"
              value={formInfo.content}
              onChange={handleFormChange}
              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-700 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 min-h-[100px] dark:bg-gray-800 dark:text-white"
              required
            />
          </div>
          <div className="flex justify-end gap-2 mt-4">
            <button
              type="button"
              onClick={closeModal}
              className="px-4 py-2 rounded-lg bg-gray-200 hover:bg-gray-300 text-gray-700 dark:bg-gray-700 dark:text-gray-200 dark:hover:bg-gray-600"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 rounded-lg bg-blue-600 hover:bg-blue-700 text-white font-semibold shadow"
            >
              {modalType === 'add' ? 'Add Info' : 'Save Changes'}
            </button>
          </div>
        </form>
      </Modal>

      {/* Notification */}
      {notification && (
        <div className="fixed bottom-6 right-6 z-50">
          <Notification
            type={notification.type}
            message={notification.message}
            onClose={() => setNotification(null)}
          />
        </div>
      )}
    </div>
  );
}
