// RobotView.jsx (Full Updated View with Dashboard Features)
import React, { useEffect, useState, useRef } from 'react';
import { FiAlertTriangle, FiSend, FiMessageSquare } from 'react-icons/fi';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import API, { fetchLiveChatLogs, robotAsk } from '../utils/api';

const API_URL = API.defaults.baseURL.replace('/api', '');

export default function RobotView() {
  const [stats, setStats] = useState(null);
  const [lastUpdated, setLastUpdated] = useState('');
  const [fps, setFps] = useState(30);
  const [chatLogs, setChatLogs] = useState([]);
  const [chatInput, setChatInput] = useState('');
  const [chatLoading, setChatLoading] = useState(false);
  const [aiChat, setAiChat] = useState([]); // [{sender, message, isUser}]
  const chatEndRef = useRef(null);

  useEffect(() => {
    fetchStats();
    fetchRobotChats();
    const interval = setInterval(() => {
      fetchStats();
      fetchRobotChats();
      setLastUpdated(new Date().toLocaleTimeString());
    }, 10000);
    return () => clearInterval(interval);
  }, []);

  const fetchStats = async () => {
    try {
      const res = await API.get('/statistics');
      setStats(res.data);
    } catch (e) { console.error('❌ Failed to fetch stats'); }
  };

  // Fetch all chat logs, filter for robot-user conversations
  const fetchRobotChats = async () => {
    try {
      const res = await fetchLiveChatLogs();
      // Filter: sender or receiver is 'robot', 'ai', 'assistant', case-insensitive
      const robotNames = ['robot', 'ai', 'assistant'];
      const logs = (res.data || []).filter(
        log => robotNames.includes((log.sender || '').toLowerCase()) || robotNames.includes((log.receiver || '').toLowerCase())
      );
      setChatLogs(logs);
    } catch (e) {
      setChatLogs([]);
    }
  };

  // AI Chat/Assistant logic
  const handleChatSend = async (e) => {
    e.preventDefault();
    if (!chatInput.trim()) return;
    setAiChat(prev => [...prev, { sender: 'You', message: chatInput, isUser: true }]);
    setChatLoading(true);
    try {
      const res = await robotAsk(chatInput);
      setAiChat(prev => [...prev, { sender: 'AI', message: res.data.response, isUser: false }]);
    } catch (e) {
      setAiChat(prev => [...prev, { sender: 'AI', message: 'Sorry, I could not process your request.', isUser: false }]);
    } finally {
      setChatInput('');
      setChatLoading(false);
      setTimeout(() => chatEndRef.current?.scrollIntoView({ behavior: 'smooth' }), 100);
    }
  };

  useEffect(() => {
    setTimeout(() => chatEndRef.current?.scrollIntoView({ behavior: 'smooth' }), 100);
  }, [aiChat]);

  return (
    <div className="max-w-7xl mx-auto p-4 fade-in">
      <div className="flex flex-col md:flex-row md:justify-between md:items-center gap-4 mb-2">
        <h1 className="text-3xl font-extrabold gradient-text tracking-tight">🧠 Robot Dashboard</h1>
        <span className="text-xs text-gray-400">Last update: {lastUpdated || 'Just now'}</span>
      </div>

      {/* Main grid: stats + robot actions + chat logs */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Left: System Stats + Robot Actions */}
        <div className="space-y-6 md:col-span-2">
          {/* Stats Overview */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="glass-card p-6 flex flex-col items-center">
              <span className="text-2xl font-bold text-indigo-500">{stats ? stats.total_students : '--'}</span>
              <span className="text-gray-500">Students</span>
            </div>
            <div className="glass-card p-6 flex flex-col items-center">
              <span className="text-2xl font-bold text-indigo-500">{stats ? stats.total_staff : '--'}</span>
              <span className="text-gray-500">Staff</span>
            </div>
            <div className="glass-card p-6 flex flex-col items-center">
              <span className="text-2xl font-bold text-indigo-500">{stats ? stats.total_recognitions : '--'}</span>
              <span className="text-gray-500">Recognitions</span>
            </div>
            <div className="glass-card p-6 flex flex-col items-center">
              <span className="text-2xl font-bold text-indigo-500">{stats ? stats.accuracy_rate + '%' : '--'}</span>
              <span className="text-gray-500">Accuracy</span>
            </div>
          </div>

          {/* Stats Graph */}
          <div className="glass-card p-6">
            <h2 className="text-lg font-bold mb-3">📊 System Stats</h2>
            <ResponsiveContainer width="100%" height={220}>
              <BarChart data={stats ? [stats] : []} margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="label" tick={false} />
                <YAxis />
                <Tooltip />
                <Bar dataKey="total_students" fill="#6366f1" name="Students" />
                <Bar dataKey="total_staff" fill="#a5b4fc" name="Staff" />
                <Bar dataKey="total_recognitions" fill="#818cf8" name="Recognitions" />
              </BarChart>
            </ResponsiveContainer>
          </div>

          {/* Robot Actions (show last 5 robot-user chat logs) */}
          <div className="glass-card p-6">
            <h2 className="text-lg font-bold mb-3 flex items-center gap-2"><FiMessageSquare /> Robot Actions</h2>
            {chatLogs.length === 0 ? (
              <p className="text-gray-400">No recent robot actions.</p>
            ) : (
              <ul className="space-y-2">
                {chatLogs.slice(0, 5).map((log, idx) => (
                  <li key={log.id || idx} className="text-sm text-gray-700 flex flex-col">
                    <span className="font-semibold text-indigo-600">{log.sender} → {log.receiver}</span>
                    <span className="text-gray-500">{log.message}</span>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>

        {/* Right: Robot-User Chat Logs */}
        <div className="glass-card p-4 flex flex-col h-[600px] max-h-[80vh]">
          <h2 className="text-lg font-bold mb-3 flex items-center gap-2"><FiMessageSquare /> Robot Chat Logs</h2>
          <div className="flex-1 overflow-y-auto pr-2">
            {chatLogs.length === 0 ? (
              <div className="text-gray-400 text-center mt-8">No robot-user conversations yet.</div>
            ) : (
              <ul className="space-y-3">
                {chatLogs.map((log, idx) => (
                  <li key={log.id || idx} className="border-b pb-2">
                    <div className="flex items-center gap-2 text-xs text-gray-400 mb-1">
                      <span>{new Date(log.timestamp).toLocaleString()}</span>
                      <span>•</span>
                      <span className="font-semibold text-indigo-600">{log.sender}</span>
                      <span>→</span>
                      <span className="font-semibold text-green-600">{log.receiver}</span>
                    </div>
                    <div className="text-gray-800 text-sm">{log.message}</div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </div>

      {/* AI Chat/Assistant */}
      <div className="glass-card p-4 mt-8 max-w-3xl mx-auto">
        <h2 className="text-lg font-bold mb-2 flex items-center gap-2"><FiMessageSquare /> AI Chat Assistant</h2>
        <div className="h-64 overflow-y-auto bg-white/60 rounded-lg p-3 mb-3 border border-gray-100 flex flex-col">
          {aiChat.length === 0 ? (
            <div className="text-gray-400 text-center my-auto">Start a conversation with the AI assistant about the website, college, students, staff, or anything!</div>
          ) : (
            aiChat.map((msg, idx) => (
              <div key={idx} className={msg.isUser ? 'text-right' : 'text-left'}>
                <div className={
                  'inline-block px-3 py-2 rounded-lg mb-1 ' +
                  (msg.isUser
                    ? 'bg-indigo-500 text-white ml-auto'
                    : 'bg-gray-200 text-gray-800 mr-auto')
                }>
                  <span className="font-semibold mr-2">{msg.sender}:</span> {msg.message}
                </div>
              </div>
            ))
          )}
          <div ref={chatEndRef} />
        </div>
        <form onSubmit={handleChatSend} className="flex gap-2">
          <input
            type="text"
            className="flex-1 enhanced-input"
            placeholder="Type your message..."
            value={chatInput}
            onChange={e => setChatInput(e.target.value)}
            disabled={chatLoading}
            required
          />
          <button
            type="submit"
            className="btn-primary flex items-center gap-1 px-4"
            disabled={chatLoading || !chatInput.trim()}
          >
            <FiSend />
            {chatLoading ? 'Sending...' : 'Send'}
          </button>
        </form>
      </div>

      {/* Debug Console */}
      <div className="glass-card p-6 text-xs bg-black/80 text-green-300 font-mono mt-8">
        <h2 className="font-bold mb-1">🐞 Debug Console</h2>
        <p>🎥 FPS: {fps}</p>
        <p>📦 DB Sync: ✅ OK</p>
        <p>🧠 Last Update: {lastUpdated}</p>
      </div>
    </div>
  );
}
