import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { 
  FiActivity, 
  FiSearch, 
  FiFilter, 
  FiRefreshCw, 
  FiEye, 
  FiDownload,
  FiCalendar,
  FiUser,
  FiUsers,
  FiEye as FiUnknown,
  FiClock,
  FiPercent,
  FiImage
} from 'react-icons/fi';
import LoadingSpinner from '../components/LoadingSpinner';

export default function RecognitionLog() {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('');
  const [filterDate, setFilterDate] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(20);

  useEffect(() => {
    fetchLogs();
  }, []);

    const fetchLogs = async () => {
      try {
      setLoading(true);
        const res = await axios.get('http://localhost:5000/api/logs');
        setLogs(res.data);
      } catch (error) {
        console.error('Error fetching logs:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredLogs = logs.filter(log => {
    const matchesSearch = log.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = !filterType || log.type === filterType;
    const matchesDate = !filterDate || log.timestamp.startsWith(filterDate);
    return matchesSearch && matchesType && matchesDate;
  });

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentLogs = filteredLogs.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredLogs.length / itemsPerPage);

  const getTypeIcon = (type) => {
    switch (type) {
      case 'student': return <FiUser className="text-blue-500" />;
      case 'staff': return <FiUsers className="text-green-500" />;
      case 'unknown': return <FiUnknown className="text-red-500" />;
      default: return <FiUser className="text-gray-500" />;
    }
  };

  const getTypeColor = (type) => {
    switch (type) {
      case 'student': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
      case 'staff': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      case 'unknown': return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200';
    }
  };

  const getConfidenceColor = (confidence) => {
    const conf = parseFloat(confidence);
    if (conf >= 0.8) return 'text-green-600 dark:text-green-400';
    if (conf >= 0.6) return 'text-yellow-600 dark:text-yellow-400';
    return 'text-red-600 dark:text-red-400';
  };

  const exportLogs = () => {
    const csvContent = [
      ['ID', 'Name', 'Type', 'Confidence', 'Timestamp', 'Image Path'].join(','),
      ...filteredLogs.map(log => [
        log.id,
        log.name,
        log.type,
        log.confidence,
        log.timestamp,
        log.image_path || ''
      ].join(','))
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `recognition_logs_${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const clearAllLogs = async () => {
    if (!window.confirm('Are you sure you want to clear all recognition logs? This action cannot be undone.')) return;
    try {
      setLoading(true);
      await axios.post('http://localhost:5000/api/recognition_logs/clear');
      await fetchLogs();
    } catch (error) {
      alert('Failed to clear recognition logs.');
      console.error('Error clearing recognition logs:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-100 via-white to-blue-100 py-12 px-2 fade-in relative overflow-x-hidden">
      {/* Animated background highlights */}
      <div className="pointer-events-none select-none">
        <div className="absolute top-10 left-1/4 w-1/2 h-32 bg-gradient-to-r from-purple-400/30 to-blue-400/20 rounded-full blur-2xl animate-pulse"></div>
        <div className="absolute bottom-10 right-1/4 w-1/3 h-24 bg-gradient-to-l from-blue-400/30 to-pink-400/20 rounded-full blur-2xl animate-pulse"></div>
      </div>
      <div className="relative z-10 max-w-6xl mx-auto flex flex-col gap-10">
        {/* Floating Action Bar */}
        <div className="flex flex-wrap gap-4 justify-end mb-4 sticky top-0 z-20 bg-white/80 rounded-2xl shadow-lg p-4 border border-blue-100">
          <button onClick={exportLogs} className="p-3 rounded-xl bg-gradient-to-r from-blue-500 to-indigo-500 text-white font-bold shadow hover:scale-105 transition flex items-center gap-2">
            <FiDownload /> Export CSV
          </button>
          <button onClick={fetchLogs} className="p-3 rounded-xl bg-gradient-to-r from-purple-500 to-pink-500 text-white font-bold shadow hover:scale-105 transition flex items-center gap-2" disabled={loading}>
            <FiRefreshCw className={loading ? 'animate-spin' : ''} /> Refresh
          </button>
          <button onClick={clearAllLogs} className="p-3 rounded-xl bg-gradient-to-r from-red-500 to-pink-500 text-white font-bold shadow hover:scale-105 transition flex items-center gap-2" disabled={loading}>
            Clear All
          </button>
        </div>
        {/* Filters */}
        <div className="glass-card p-8 rounded-2xl shadow-xl border-2 border-blue-100 mb-4">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="relative">
              <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
              <input
                type="text"
                placeholder="Search by name..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="enhanced-input pl-10 text-lg"
              />
            </div>
            <div className="relative">
              <FiFilter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
              <select
                value={filterType}
                onChange={(e) => setFilterType(e.target.value)}
                className="enhanced-input pl-10 text-lg"
              >
                <option value="">All Types</option>
                <option value="student">Students</option>
                <option value="staff">Staff</option>
                <option value="unknown">Unknown</option>
              </select>
            </div>
            <div className="relative">
              <FiCalendar className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
              <input
                type="date"
                value={filterDate}
                onChange={(e) => setFilterDate(e.target.value)}
                className="enhanced-input pl-10 text-lg"
              />
            </div>
          </div>
        </div>
        {/* Stats */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6 mb-4">
          <div className="gradient-card p-6 rounded-2xl text-center shadow-lg">
            <div className="text-3xl font-extrabold text-blue-800 dark:text-white">{logs.length}</div>
            <div className="text-gray-600 dark:text-gray-300 text-lg">Total Logs</div>
          </div>
          <div className="gradient-card p-6 rounded-2xl text-center shadow-lg">
            <div className="text-3xl font-extrabold text-blue-800 dark:text-white">{logs.filter(log => log.type === 'student').length}</div>
            <div className="text-gray-600 dark:text-gray-300 text-lg">Students</div>
          </div>
          <div className="gradient-card p-6 rounded-2xl text-center shadow-lg">
            <div className="text-3xl font-extrabold text-blue-800 dark:text-white">{logs.filter(log => log.type === 'staff').length}</div>
            <div className="text-gray-600 dark:text-gray-300 text-lg">Staff</div>
          </div>
          <div className="gradient-card p-6 rounded-2xl text-center shadow-lg">
            <div className="text-3xl font-extrabold text-blue-800 dark:text-white">{logs.filter(log => log.type === 'unknown').length}</div>
            <div className="text-gray-600 dark:text-gray-300 text-lg">Unknown</div>
          </div>
        </div>
        {/* Log Table */}
        <div className="glass-card overflow-hidden rounded-2xl shadow-2xl border-2 border-blue-100">
          {loading ? (
            <div className="p-16">
              <LoadingSpinner size="lg" text="Loading recognition logs..." />
            </div>
          ) : (
            <>
              <div className="overflow-x-auto">
                <table className="enhanced-table text-lg">
                  <thead className="bg-gradient-to-r from-blue-100 to-indigo-100">
                    <tr>
                      <th>#</th>
                      <th>Name</th>
                      <th>Type</th>
                      <th>Confidence</th>
                      <th>Timestamp</th>
                      <th>Image</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {currentLogs.map((log, index) => (
                      <tr key={log.id || index} className="slide-in-up border-b border-blue-50 hover:bg-blue-50/40 transition" style={{ animationDelay: `${index * 0.05}s` }}>
                        <td className="font-mono font-semibold">#{log.id || index + 1}</td>
                        <td className="font-semibold">{log.name}</td>
                        <td>
                          <div className="flex items-center space-x-2">
                            {getTypeIcon(log.type)}
                            <span className={`px-2 py-1 rounded-full text-xs font-semibold ${getTypeColor(log.type)}`}>
                              {log.type}
                            </span>
                          </div>
                        </td>
                        <td>
                          <div className="flex items-center space-x-2">
                            <FiPercent className="text-gray-400" />
                            <span className={`font-semibold ${getConfidenceColor(log.confidence)}`}>
                              {(parseFloat(log.confidence) * 100).toFixed(1)}%
                            </span>
                          </div>
                        </td>
                        <td>
                          <div className="flex items-center space-x-2">
                            <FiClock className="text-gray-400" />
                            <span>{new Date(log.timestamp).toLocaleString()}</span>
                          </div>
                        </td>
                        <td>
                          {log.image_path ? (
                            <div className="flex justify-center">
                              <img
                                src={`http://localhost:5000/${log.image_path}`}
                                alt="snapshot"
                                className="w-14 h-14 object-cover rounded-xl shadow-md border-2 border-white dark:border-gray-700 hover:scale-110 transition-transform duration-200"
                              />
                            </div>
                          ) : (
                            <div className="flex justify-center">
                              <FiImage className="text-gray-400" size={24} />
                            </div>
                          )}
                        </td>
                        <td>
                          <div className="flex space-x-2">
                            <button className="p-2 rounded-lg bg-blue-100 text-blue-600 hover:bg-blue-200 transition-colors duration-200">
                              <FiEye size={18} />
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              {totalPages > 1 && (
                <div className="p-6 border-t border-blue-100">
                  <div className="flex items-center justify-between">
                    <div className="text-lg text-gray-600 dark:text-gray-300">
                      Showing {indexOfFirstItem + 1} to {Math.min(indexOfLastItem, filteredLogs.length)} of {filteredLogs.length} results
                    </div>
                    <div className="flex space-x-2">
                      <button
                        onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                        disabled={currentPage === 1}
                        className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        Previous
                      </button>
                      <span className="px-3 py-2 text-lg text-gray-600 dark:text-gray-300">
                        Page {currentPage} of {totalPages}
                      </span>
                      <button
                        onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                        disabled={currentPage === totalPages}
                        className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        Next
                      </button>
                    </div>
                  </div>
                </div>
              )}
            </>
          )}
          {!loading && filteredLogs.length === 0 && (
            <div className="p-16 text-center">
              <FiActivity className="mx-auto text-7xl text-gray-400 mb-4" />
              <p className="text-gray-600 dark:text-gray-300 text-xl">
                {logs.length === 0 ? 'No recognition logs found' : 'No results match your filters'}
              </p>
              <p className="text-gray-500 dark:text-gray-400">
                {logs.length === 0 
                  ? 'Start the live feed to generate recognition logs.'
                  : 'Try adjusting your search or filter criteria.'
                }
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
