import React, { useState, useEffect, useRef } from 'react';
import {
  FiVideo, FiVideoOff, FiCamera, FiRefreshCw, FiUsers, FiEye, FiUser, FiUserCheck, FiAlertCircle, FiClock
} from 'react-icons/fi';

const API_URL = 'http://localhost:5000';

const badgeColors = {
  student: 'bg-blue-500',
  staff: 'bg-green-500',
  unknown: 'bg-red-500',
};

const TABS = [
  { key: 'all', label: 'All', icon: <FiUsers /> },
  { key: 'student', label: 'Students', icon: <FiUser /> },
  { key: 'staff', label: 'Faculty', icon: <FiUserCheck /> },
  { key: 'unknown', label: 'Unknowns', icon: <FiEye /> },
];

const LiveFeed = () => {
  const [isPlaying, setIsPlaying] = useState(true);
  const [faces, setFaces] = useState([]);
  const [tab, setTab] = useState('all');
  const [timeline, setTimeline] = useState([]);
  const intervalRef = useRef();

  useEffect(() => {
    intervalRef.current = setInterval(async () => {
      try {
        const res = await fetch(`${API_URL}/api/recognized_faces`);
        if (res.ok) {
          const data = await res.json();
          setFaces(data);
          setTimeline((prev) => {
            const now = Date.now();
            const newEntries = data.map(f => ({
              name: f.name,
              type: f.type,
              db_image_path: f.db_image_path,
              timestamp: now
            }));
            // Only keep the last 20 unique entries
            const combined = [...newEntries, ...prev].filter((v, i, arr) =>
              arr.findIndex(x => x.name === v.name && x.type === v.type) === i
            );
            return combined.slice(0, 20);
          });
        }
      } catch (e) {
        setFaces([]);
      }
    }, 1000);
    return () => clearInterval(intervalRef.current);
  }, []);

  const togglePlayPause = () => setIsPlaying(!isPlaying);
  const refreshFeed = () => {
    const videoElement = document.getElementById('video-feed');
    if (videoElement) {
      videoElement.src = `${API_URL}/video_feed?t=${Date.now()}`;
    }
  };

  // Filtering logic
  const filteredFaces = tab === 'all' ? faces : faces.filter(f => f.type === tab);

  return (
    <div className="relative min-h-screen bg-gradient-to-br from-blue-100 via-white to-indigo-100 py-10 px-2 fade-in">
      {/* Animated Backgrounds */}
      <div className="absolute inset-0 pointer-events-none z-0">
        <div className="absolute top-20 left-1/4 w-1/2 h-32 bg-gradient-to-r from-purple-400/30 to-blue-400/20 rounded-full blur-2xl animate-pulse"></div>
        <div className="absolute bottom-10 right-1/4 w-1/3 h-24 bg-gradient-to-l from-indigo-400/30 to-pink-400/20 rounded-full blur-2xl animate-pulse"></div>
      </div>
      <div className="relative z-10 max-w-7xl mx-auto flex flex-col lg:flex-row gap-10 items-start">
        {/* Sticky Video Feed */}
        <div className="lg:sticky lg:top-10 flex-shrink-0 w-full lg:w-[420px]">
          <div className="rounded-3xl overflow-hidden shadow-2xl glass-card p-0 bg-white/60">
            <div className="flex items-center justify-between px-6 py-4 border-b border-gray-200 dark:border-gray-700 bg-white/70">
              <div className="flex items-center gap-3">
                <FiCamera className="text-gray-600" />
                <span className="font-bold text-lg text-gray-800">Live Camera</span>
                <span className="ml-3 flex items-center gap-1 text-red-500 font-semibold animate-pulse">
                  <span className="w-2 h-2 rounded-full bg-red-500 animate-ping"></span> LIVE
                </span>
              </div>
              <div className="flex items-center gap-2">
                <button onClick={refreshFeed} className="p-2 rounded-lg bg-blue-100 hover:bg-blue-200 text-blue-700 transition">
                  <FiRefreshCw />
                </button>
                <button onClick={togglePlayPause} className="p-2 rounded-lg bg-blue-100 hover:bg-blue-200 text-blue-700 transition">
                  {isPlaying ? <FiVideoOff /> : <FiVideo />}
                </button>
              </div>
            </div>
            <div className="relative bg-black flex items-center justify-center min-h-[400px]">
              {isPlaying ? (
                <img
                  id="video-feed"
                  src={`${API_URL}/video_feed`}
                  alt="Live camera feed"
                  className="w-full h-[400px] object-contain rounded-b-3xl"
                />
              ) : (
                <div className="w-full h-[400px] flex items-center justify-center bg-gray-900 rounded-b-3xl">
                  <div className="text-center text-gray-400">
                    <FiVideoOff className="mx-auto text-6xl mb-4" />
                    <p className="text-lg">Feed Paused</p>
                    <p className="text-sm">Click play to resume</p>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
        {/* Recognized Faces Section */}
        <div className="flex-1 flex flex-col gap-6">
          {/* Tabs/Filters */}
          <div className="flex items-center gap-2 mb-4">
            {TABS.map(t => (
              <button
                key={t.key}
                onClick={() => setTab(t.key)}
                className={`flex items-center gap-1 px-4 py-2 rounded-full font-semibold transition-all duration-200 border-2 ${tab === t.key ? 'bg-gradient-to-r from-blue-600 to-indigo-600 text-white border-blue-600 shadow-lg' : 'bg-white/80 text-blue-700 border-blue-200 hover:bg-blue-100'}`}
              >
                {t.icon} {t.label}
              </button>
            ))}
          </div>
          {/* Masonry/Grid for Faces */}
          <div className="grid grid-cols-1 sm:grid-cols-2 xl:grid-cols-3 gap-6">
            {filteredFaces.length === 0 ? (
              <div className="col-span-full flex flex-col items-center justify-center h-40 text-gray-400">
                <FiAlertCircle className="text-4xl mb-2" />
                <span>No faces detected.</span>
              </div>
            ) : (
              filteredFaces.map((face, idx) => (
                <div key={idx} className="group relative p-4 rounded-2xl bg-white/90 dark:bg-gray-800 shadow-xl border border-blue-100 dark:border-gray-700 flex flex-col items-center transition-transform duration-300 hover:scale-105">
                  <div className="flex gap-3 items-center w-full mb-2">
                    {/* Database photo */}
                    <img
                      src={face.db_image_path || '/default-avatar.png'}
                      alt={face.name}
                      className="w-16 h-16 object-cover rounded-xl border-2 border-blue-200 shadow"
                    />
                    {/* Live snapshot */}
                    <img
                      src={face.image_path || '/default-avatar.png'}
                      alt={face.name + ' live'}
                      className="w-16 h-16 object-cover rounded-xl border-2 border-indigo-200 shadow"
                    />
                  </div>
                  <div className="flex items-center gap-2 mb-1">
                    <span className="font-bold text-lg text-gray-800 dark:text-white">{face.name}</span>
                    <span className={`ml-2 px-3 py-1 rounded-full text-xs font-bold text-white ${badgeColors[face.type] || 'bg-gray-400'}`}> 
                      {face.type === 'student' && (<><FiUser className="inline mr-1" />Student</>)}
                      {face.type === 'staff' && (<><FiUserCheck className="inline mr-1" />Faculty</>)}
                      {face.type === 'unknown' && (<><FiEye className="inline mr-1" />Unknown</>)}
                    </span>
                  </div>
                  <div className="text-xs text-gray-600 dark:text-gray-300 mb-2">
                    {face.type === 'student' && <>Department: <span className="font-semibold text-blue-700 dark:text-blue-300">{face.department}</span></>}
                    {face.type === 'staff' && <>Role: <span className="font-semibold text-green-700 dark:text-green-300">{face.role}</span></>}
                    {face.type === 'unknown' && <span className="italic text-red-500">Unknown Person</span>}
                  </div>
                  {/* Overlay on hover */}
                  <div className="absolute inset-0 bg-gradient-to-br from-blue-200/80 to-indigo-300/80 rounded-2xl flex flex-col items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-300 z-10 p-4">
                    <div className="text-white text-center">
                      <div className="font-bold text-lg mb-1">{face.name}</div>
                      <div className="mb-1">{face.type === 'student' && 'Student'}{face.type === 'staff' && 'Faculty'}{face.type === 'unknown' && 'Unknown'}</div>
                      <div className="mb-1">{face.department && <>Dept: {face.department}</>}{face.role && <>Role: {face.role}</>}</div>
                      <div className="flex items-center justify-center gap-1 text-xs"><FiClock className="mr-1" />Just now</div>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
          {/* Timeline at the bottom */}
          <div className="mt-8">
            <h3 className="text-lg font-bold mb-2 text-blue-700">Recent Recognitions</h3>
            <div className="flex gap-3 overflow-x-auto pb-2 custom-scroll">
              {timeline.length === 0 ? (
                <span className="text-gray-400">No recent recognitions.</span>
              ) : (
                timeline.map((entry, i) => (
                  <div key={i} className="flex flex-col items-center min-w-[64px]">
                    <img
                      src={entry.db_image_path || '/default-avatar.png'}
                      alt={entry.name}
                      className="w-12 h-12 object-cover rounded-full border-2 border-blue-300 shadow"
                    />
                    <span className="text-xs font-semibold text-gray-700 mt-1 truncate w-14 text-center">{entry.name}</span>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LiveFeed;
