import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import { FiCpu } from 'react-icons/fi';
import RobotSpeechDisplay from './RobotSpeechDisplay';
import {
  speakWithAudioFeedback,
  stopSpeaking,
  startListening,
  stopListening
} from '../utils/voice';

const API_URL = 'http://localhost:5000';

export default function RobotController() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [recognizedName, setRecognizedName] = useState('');
  const [hasGreeted, setHasGreeted] = useState(false);
  const [collegeName, setCollegeName] = useState('Your College');
  const [robotMessage, setRobotMessage] = useState('üéì Initializing...');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [volume, setVolume] = useState(0);

  // 1. Load college name once
  useEffect(() => {
    axios.get(`${API_URL}/api/college_name`)
      .then(res => setCollegeName(res.data.name || 'Your College'))
      .catch(() => setCollegeName('Your College'));
  }, []);

  // 2. Start checking face periodically
  useEffect(() => {
    const id = setInterval(checkFace, 2000);
    return () => clearInterval(id);
  }, [recognizedName, hasGreeted]);

  // 3. Draw simple overlay
  useEffect(() => {
    const draw = () => {
      const canvas = canvasRef.current;
      const img = videoRef.current;
      if (!canvas || !img) return;
      canvas.width = img.clientWidth;
      canvas.height = img.clientHeight;
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      if (recognizedName) {
        ctx.fillStyle = '#28a745';
        ctx.font = 'bold 2rem sans-serif';
        ctx.textAlign = 'center';
        ctx.fillText(`üéâ Hello, ${recognizedName}`, canvas.width/2, 20);
      }
    };
    draw();
    window.addEventListener('resize', draw);
    return () => window.removeEventListener('resize', draw);
  }, [recognizedName]);

  // 4. Face detection logic
  function checkFace() {
    axios.get(`${API_URL}/api/recognized_face`)
      .then(res => {
        const name = res.data.name;
        if (name && name !== 'Unknown') {
          if (name !== recognizedName) {
            setRecognizedName(name);
            if (!hasGreeted) greet(name);
          }
        } else {
          setRecognizedName('');
          setHasGreeted(false);
        }
      })
      .catch(console.error);
  }

  // 5. Greeting+voice
  function greet(name) {
    const msg = `Hi ${name}, I'm your college assistant from ${collegeName}. How can I help?`;
    stopSpeaking();
    stopListening();
    speakWithAudioFeedback(msg, setVolume, () => {
      setIsSpeaking(false);
      startListening(onUserSpeak);
    });
    setIsSpeaking(true);
    setRobotMessage(msg);
    setHasGreeted(true);
  }

  // 6. Handle user voice input
  function onUserSpeak(text, final) {
    if (!final) return;
    stopSpeaking();
    stopListening();
    setRobotMessage(`You said: ${text}`);
    axios.post(`${API_URL}/api/robot_ask`, { query: text })
      .then(res => {
        const reply = res.data.response || "Sorry, I didn't catch that.";
        speakWithAudioFeedback(reply, setVolume, () => {
          setIsSpeaking(false);
          startListening(onUserSpeak);
        });
        setIsSpeaking(true);
        setRobotMessage(reply);
      })
      .catch(err => {
        setRobotMessage("‚ùå Error reaching assistant");
      });
  }

  return (
    <div className="max-w-4xl mx-auto p-4" style={{ minHeight: '90vh' }}>
      <div className="p-6 bg-white rounded-lg shadow-lg flex items-center space-x-4 mb-6">
        <FiCpu size={32} className="text-indigo-600" />
        <h1 className="text-2xl font-bold">Robot Assistant (Controller)</h1>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6" style={{ minHeight: '60vh' }}>
        {/* Camera Feed */}
        <div className="relative bg-black rounded-lg overflow-hidden">
          <img
            ref={videoRef}
            src={`${API_URL}/video_feed`}
            alt="Robot Camera"
            className="w-full h-full object-cover"
          />
          <canvas
            ref={canvasRef}
            className="absolute top-0 left-0 w-full h-full pointer-events-none"
          />
        </div>

        {/* Voice Output */}
        <div className="bg-white rounded-lg shadow-lg p-6 flex flex-col justify-between">
          <RobotSpeechDisplay
            message={robotMessage}
            isSpeaking={isSpeaking}
            volume={volume}
          />
          <p className="mt-4 text-sm text-gray-500">üéôÔ∏è Listening for your voice ‚Äî just talk.</p>
        </div>
      </div>
    </div>
  );
}
