import React, { useState } from 'react';
import { 
  FiVideo, 
  FiVideoOff, 
  FiCamera, 
  FiMaximize, 
  FiMinimize,
  FiWifi,
  FiWifiOff,
  FiAlertCircle,
  FiGrid,
  FiList,
  FiPlus,
  FiEdit,
  FiTrash2
} from 'react-icons/fi';

const API_URL = 'http://localhost:5000';

const CameraCard = ({ camera, onEdit, onDelete }) => {
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [isPlaying, setIsPlaying] = useState(camera.status === 'Online');
  const [showControls, setShowControls] = useState(false);

  const getStatusColor = (status) => {
    switch (status) {
      case 'Online': return 'text-green-500';
      case 'Offline': return 'text-red-500';
      case 'Maintenance': return 'text-yellow-500';
      default: return 'text-gray-500';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'Online': return <FiWifi className="text-green-500" />;
      case 'Offline': return <FiWifiOff className="text-red-500" />;
      case 'Maintenance': return <FiAlertCircle className="text-yellow-500" />;
      default: return <FiWifiOff className="text-gray-500" />;
    }
  };

  return (
    <div 
      className="glass-card overflow-hidden group"
      onMouseEnter={() => setShowControls(true)}
      onMouseLeave={() => setShowControls(false)}
    >
      <div className="flex items-center justify-between p-4 border-b border-gray-200 dark:border-gray-700">
        <div className="flex items-center space-x-3">
          <FiCamera className="text-gray-600 dark:text-gray-400" />
          <div>
            <h3 className="font-semibold text-gray-800 dark:text-white">{camera.title}</h3>
            <p className="text-sm text-gray-500 dark:text-gray-400">Camera #{camera.id}</p>
          </div>
        </div>
        
        <div className="flex items-center space-x-2">
          <div className={`flex items-center space-x-2 px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(camera.status)}`}>
            {getStatusIcon(camera.status)}
            <span>{camera.status}</span>
          </div>
          
          {showControls && (
            <div className="flex items-center space-x-1">
              <button
                onClick={() => setIsPlaying(!isPlaying)}
                className="p-2 rounded-lg bg-gray-100 dark:bg-gray-700 text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-600 transition-colors duration-200"
              >
                {isPlaying ? <FiVideoOff size={16} /> : <FiVideo size={16} />}
              </button>
              <button
                onClick={() => onEdit(camera)}
                className="p-2 rounded-lg bg-blue-100 text-blue-600 hover:bg-blue-200 transition-colors duration-200"
              >
                <FiEdit size={16} />
              </button>
              <button
                onClick={() => onDelete(camera.id)}
                className="p-2 rounded-lg bg-red-100 text-red-600 hover:bg-red-200 transition-colors duration-200"
              >
                <FiTrash2 size={16} />
              </button>
            </div>
          )}
        </div>
      </div>
      
      <div className="relative bg-black">
        {isPlaying && camera.status === 'Online' ? (
          <img 
            src={camera.feedUrl} 
            alt={`${camera.title} feed`} 
            className="w-full h-auto max-h-[50vh] object-contain"
          />
        ) : (
          <div className="w-full h-64 bg-gray-900 flex items-center justify-center">
            <div className="text-center text-gray-400">
              {camera.status === 'Online' ? (
                <>
                  <FiVideoOff className="mx-auto text-6xl mb-4" />
                  <p className="text-lg">Feed Paused</p>
                </>
              ) : (
                <>
                  <FiWifiOff className="mx-auto text-6xl mb-4" />
                  <p className="text-lg">Camera Offline</p>
                </>
              )}
            </div>
          </div>
        )}
      </div>

      <div className="p-4">
        <div className="grid grid-cols-2 gap-4 text-sm text-gray-600 dark:text-gray-300">
          <div><span className="font-medium">Location:</span> {camera.location}</div>
          <div><span className="font-medium">Resolution:</span> {camera.resolution}</div>
        </div>
      </div>
    </div>
  );
};

const CameraFeeds = () => {
  const [cameras, setCameras] = useState([
    { 
      id: 1, 
      title: 'Primary Feed (Entrance)', 
      status: 'Online', 
      feedUrl: `${API_URL}/video_feed`,
      location: 'Main Entrance',
      resolution: '1920x1080'
    },
    { 
      id: 2, 
      title: 'Camera 2 (Hallway)', 
      status: 'Offline', 
      feedUrl: null,
      location: 'Central Hallway',
      resolution: '1280x720'
    },
    { 
      id: 3, 
      title: 'Camera 3 (Library)', 
      status: 'Maintenance', 
      feedUrl: null,
      location: 'Library',
      resolution: '1920x1080'
    },
    { 
      id: 4, 
      title: 'Camera 4 (Cafeteria)', 
      status: 'Offline', 
      feedUrl: null,
      location: 'Cafeteria',
      resolution: '1280x720'
    },
  ]);
  const [viewMode, setViewMode] = useState('grid');

  const handleEdit = (camera) => {
    console.log('Edit camera:', camera);
  };

  const handleDelete = (cameraId) => {
    if (window.confirm('Are you sure you want to delete this camera?')) {
      setCameras(cameras.filter(cam => cam.id !== cameraId));
    }
  };

  const onlineCameras = cameras.filter(cam => cam.status === 'Online').length;
  const totalCameras = cameras.length;

  return (
    <div className="space-y-6 fade-in">
      <div className="glass-card p-6">
        <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
          <div className="flex items-center space-x-3">
            <div className="p-3 rounded-xl bg-gradient-to-r from-blue-500 to-cyan-500 text-white">
              <FiVideo size={24} />
            </div>
            <div>
              <h1 className="text-3xl font-bold gradient-text">Camera Feeds</h1>
              <p className="text-gray-600 dark:text-gray-300">Monitor all connected cameras</p>
