          </div>
          <div className="flex items-center space-x-3">
            <div className="flex items-center space-x-2 bg-gray-100 dark:bg-gray-700 rounded-lg p-1">
              <button
                onClick={() => setViewMode('grid')}
                className={`p-2 rounded-md transition-colors duration-200 ${
                  viewMode === 'grid' 
                    ? 'bg-white dark:bg-gray-600 text-blue-600 dark:text-blue-400' 
                    : 'text-gray-600 dark:text-gray-300'
                }`}
              >
                <FiGrid size={16} />
              </button>
              <button
                onClick={() => setViewMode('list')}
                className={`p-2 rounded-md transition-colors duration-200 ${
                  viewMode === 'list' 
                    ? 'bg-white dark:bg-gray-600 text-blue-600 dark:text-blue-400' 
                    : 'text-gray-600 dark:text-gray-300'
                }`}
              >
                <FiList size={16} />
              </button>
            </div>
            <button className="btn-primary">
              <FiPlus className="mr-2" />
              Add Camera
            </button>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <div className="gradient-card p-4 text-center">
          <div className="text-2xl font-bold text-gray-800 dark:text-white">{totalCameras}</div>
          <div className="text-gray-600 dark:text-gray-300">Total Cameras</div>
        </div>
        <div className="gradient-card p-4 text-center">
          <div className="text-2xl font-bold text-gray-800 dark:text-white">{onlineCameras}</div>
          <div className="text-gray-600 dark:text-gray-300">Online</div>
        </div>
        <div className="gradient-card p-4 text-center">
          <div className="text-2xl font-bold text-gray-800 dark:text-white">
            {cameras.filter(cam => cam.status === 'Offline').length}
          </div>
          <div className="text-gray-600 dark:text-gray-300">Offline</div>
        </div>
        <div className="gradient-card p-4 text-center">
          <div className="text-2xl font-bold text-gray-800 dark:text-white">
            {totalCameras > 0 ? Math.round((onlineCameras / totalCameras) * 100) : 0}%
          </div>
          <div className="text-gray-600 dark:text-gray-300">Uptime</div>
        </div>
      </div>

      <div className={`grid gap-6 ${
        viewMode === 'grid' 
          ? 'grid-cols-1 md:grid-cols-2 lg:grid-cols-2 xl:grid-cols-2' 
          : 'grid-cols-1'
      }`}>
        {cameras.map((camera, index) => (
          <div 
            key={camera.id} 
            className="slide-in-up"
            style={{ animationDelay: `${index * 0.1}s` }}
          >
            <CameraCard 
              camera={camera} 
              onEdit={handleEdit}
              onDelete={handleDelete}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default CameraFeeds;
