import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

export default function RobotSpeechDisplay({ message, isSpeaking }) {
  const [displayed, setDisplayed] = useState('');

  useEffect(() => {
    setDisplayed('');
    if (!message) return;
    let i = 0;
    const interval = setInterval(() => {
      setDisplayed((prev) => prev + message[i]);
      i++;
      if (i >= message.length) clearInterval(interval);
    }, 30);
    return () => clearInterval(interval);
  }, [message]);

  const [blink, setBlink] = useState(false);
  useEffect(() => {
    const blinkInterval = setInterval(() => setBlink((b) => !b), 3000);
    return () => clearInterval(blinkInterval);
  }, []);

  return (
    <div className="flex flex-col items-center space-y-4">
      <div className="relative">
        <motion.div
          animate={{
            scale: isSpeaking ? 1.05 : 1,
            boxShadow: isSpeaking ? '0 0 24px #38bdf8' : 'none'
          }}
          className="w-24 h-24 rounded-full bg-gradient-to-br from-cyan-400 to-blue-500 flex items-center justify-center shadow-lg"
        >
          {/* Eyes */}
          <div
            className="absolute left-7 top-10 w-3 h-3 bg-white rounded-full"
            style={{ opacity: blink ? 0.2 : 1 }}
          />
          <div
            className="absolute right-7 top-10 w-3 h-3 bg-white rounded-full"
            style={{ opacity: blink ? 0.2 : 1 }}
          />
          {/* Mouth */}
          <div
            className="absolute left-1/2 bottom-7 w-8 h-2 bg-white rounded-full"
            style={{
              transform: 'translateX(-50%)',
              opacity: isSpeaking ? 1 : 0.5
            }}
          />
        </motion.div>
      </div>

      {/* Typing text */}
      <AnimatePresence>
        <motion.div
          key={displayed}
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: 10 }}
          className="bg-white/80 dark:bg-gray-800/80 rounded-xl px-6 py-4 shadow-lg text-lg font-mono text-gray-800 dark:text-white min-h-[48px]"
        >
          {displayed}
        </motion.div>
      </AnimatePresence>
    </div>
  );
}
